INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (213800, 0, 0, 270, 0, 210, 10.0, "エンゼルダーツ", "【物理】近接攻撃\n熱帯魚のカタチをした魚の魔物。\nその優雅な姿とは裏腹に、鋭いアゴ\nを獲物めがけて突き立てる。", 213800, 1, 0, 0);
INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (213900, 0, 1, 270, 0, 650, 3.5, "ギャングオッター", "【物理】中距離攻撃\n貝殻をぶつけて不意をつく、\n意地悪な北海のチンピラっこ。", 213900, 1, 0, 0);
INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (214000, 0, 0, 270, 0, 800, 3.5, "マジックレイ", "【魔法】範囲攻撃\n頭部の石から発する超音波で\n巧みに狩りをするエイの魔物。", 214000, 2, 0, 0);
INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (214100, 0, 15, 270, 0, 200, 2.5, "スタンプ", "【物理】前衛・近接攻撃\nしなやかな枝を、ムチのように\n使い攻撃する木の魔物。", 214100, 1, 0, 0);
INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (214200, 0, 15, 270, 0, 400, 2.5, "ポイズンポッド", "【物理】近接攻撃\n大きく開いた口に溜めた\n溶解液で獲物を食らう。", 214200, 1, 0, 0);
INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (214300, 0, 15, 270, 0, 800, 2.5, "ニンフ", "【魔法】中距離攻撃\n遊び相手を見つけては、見境なしに\nイタズラをする、森の精霊。", 214300, 2, 0, 0);
