INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (214901, 0, 2, 450, 0, 240, 1.8, "ムイミのシャドウ", "ムイミの姿をした特殊な魔物\n\n・目の前の敵１キャラに物理特大ダメージを与え\n　る。さらにＴＰが無くなるまでの間天楼覇断剣\n　を装備する。\n・前方範囲内の敵すべてに物理小ダメージを与え、\n　さらにスタンさせて小さく吹き飛ばす。\n・前方範囲内の敵すべての物理防御力を小ダウン\n　させる。", 214901, 1, 0, 0);
INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (215001, 0, 1, 450, 0, 320, 2.17, "カヤのシャドウ", "カヤの姿をした特殊な魔物\n\n・目の前の敵１キャラに物理特大ダメージを与え\n　る。バトル開始後、初弾のユニオンバーストは\n　ダメージ量が大幅アップ。\n・目の前の敵１キャラに物理中ダメージを与える。\n・自分の物理攻撃力と物理クリティカルを大アッ\n　プさせる。", 215001, 1, 0, 0);
