INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (250700, 0, 16, 270, 0, 20, 5.0, "ゴーレム", "【物理】【物理防御】\n頑丈な身体で、攻守を備えた岩の魔物。\n\n・近接攻撃\n・与えるダメージによるＴＰ回復量80％ダウン", 250700, 1, 0, 0);
