INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (303700, 0, 14, 270, 0, 1000, 0.7, "ヘルハウンド", "【物理】火山に身を落としてなお\n地獄の炎を纏い蘇った双頭の魔犬。\n愛犬ボニーと共に縄張りを駆け、\n侵入者には灼熱の洗礼を浴びせる。\n\n・敵全体に物理中ダメージを与え、\n　火傷状態にする。さらに、召喚\n　モンスターのＨＰを回復する。\n\n・ボニーを召喚する。", 303700, 1, 0, 0);
