INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (303903, 0, 13, 270, 0, 1000, 2.0, "ヒトツメ巨人", "【物理】砂塵の中、山のように佇\nむ一つ目の巨人。単眼から超強力\nな光線を発し冒険者を薙ぎ払う。\n\n・ユニオンバーストは、敵全体に\n　魔法大ダメージを与え、さらに\n　行動速度を中ダウンさせる。\n　残りＨＰが100％のキャラ\n　には、ダメージがアップする。\n\n・最も物理攻撃力が高い１キャラ\n　に、最大ＨＰに応じた物理割合\n　ダメージを与え、さらにＴＰを\n　大ダウンさせる。", 303903, 1, 0, 0);
