INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (305902, 0, 16, 270, 0, 1000, 2.2, "マダムプリズム", "【魔法】圧倒的な魅力を放つ麗しき極彩色の艶鳥。\nその色香に囚われれば、味方を襲う下僕となる。\n\n・マダムプリズムから受けるすべてのダメージで\n　発生する、パーティキャラのＴＰ回復量を中ダ\n　ウンさせる。\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた魔法割合ダメージを与える。敵にバ\n　リアが展開されていない場合、誘惑状態を付与\n　する。さらに、敵全体の物理攻撃力と魔法攻撃\n　力を大アップさせる。このスキルによるダメー\n　ジでは、敵のＴＰが回復しない。\n・前方範囲内の敵すべてに魔法大ダメージを与え、\n　誘惑状態を付与する。さらに、物理攻撃のダ\n　メージ、魔法攻撃のダメージを中ダウンさせる。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。", 305902, 2, 0, 0);
INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (306000, 0, 16, 270, 0, 2160, 1.6, "邪餅神ガミィ", "【物理】邪念の残滓が群集し邪神と化した。\n顕現した煩悩を全て浄化し、新年を迎えよう！\n\n・ユニオンバーストは敵全体に\n　物理大ダメージを与える。\n・前方3キャラに魔法中ダメージを与え、\n　物理攻撃力を下げる。\n・時限式の爆弾モチを前方中距離に召喚する。\n・爆弾モチは、召喚されてから\n　8秒後に大爆発し、範囲内の敵全体に\n　物理中ダメージを与える。\n　ただし、爆発するまでに爆弾モチ自身の\n　ＨＰが50％を切ると、\n　敵全体のＨＰを小回復し、消滅する。\n・邪餅神ガミィ自身のＨＰが\n　50％を切ると、さらに攻撃が激しく\n　凶暴になる。", 306000, 1, 0, 0);
