INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (309100, 0, 16, 270, 0, 1000, 1.8, "ドロゲーター", "【物理】大河に棲む大ワニの魔物。\nマヌケな顔で、不思議な槍で大地を\n融解させ冒険者の動きを封じる。\n\n・敵全体に物理大ダメージを与え、\n　物理防御力と行動速度をダウン\n　させる。\n\n・前から３番目の敵を中心にした\n　小範囲に物理小ダメージを与え、\n　束縛状態にする。", 309100, 1, 0, 0);
INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (309101, 0, 16, 270, 0, 3000, 1.8, "ドロゲーター", "【物理】大河に棲む大ワニの魔物。マヌケな顔で、\n不思議な槍で大地を融解させ敵の動きを封じる。\n\n・飛行状態の敵を攻撃対象にできる。\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・ドロゲーターから受けるすべてのダメージで発\n　生する、パーティキャラのＴＰ回復量を中ダウ\n　ンさせる。\n・ユニオンバーストは、敵の挑発を無視して前か\n　ら３番目の敵を中心にした小範囲内の敵すべて\n　に物理特大ダメージを与える。さらに敵全体の\n　行動速度を中ダウンさせ、このスキルでダメー\n　ジを与えた敵の数に応じてＴＰをダウンさせる。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・敵の挑発を無視して、前から３番目の敵を中心\n　にした小範囲内の敵すべてに物理小ダメージを\n　与え、束縛状態を付与する。このスキルの状態\n　異常はダメージを無効化しても付与される。", 309101, 1, 0, 0);
