INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (310505, 0, 16, 270, 0, 3000, 1.6, "ラウドラパーン", "【ＭＯＤＥ２】森の人気者の歌声に魔物たちが\n大挙する。血沸き肉躍る野生の宴会が今、\n開幕する。\n\n・ブルーゴーレム、スリーピィオウル、\n　ツキツキを倒すと次のＭＯＤＥに移行する。\n\n・ラウドラパーンは、一番前のキャラに\n　物理ダメージを与える。", 310500, 1, 0, 0);
INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (310506, 0, 16, 270, 0, 3000, 1.6, "ラウドラパーン", "【ＭＯＤＥ３】宴も終焉、大兎が最後の力を振り\n絞る。咆哮にも似た壊震の歌声が、３人の夢道を\n阻む。\n\n・ユニオンバーストは、敵全体にキャラの最大\n　ＨＰに応じた物理割合ダメージを与え、\n　ノックバックさせる。その後、目を\n　回している間、自身の物理・魔法防御力が\n　大幅にダウンする。\n・一番前のキャラを中心として、一定範囲に\n　物理中ダメージを与え、さらに行動速度を\n　ダウンさせる。\n・前から３キャラに物理小ダメージを与え、\n　さらに物理防御力を大ダウンさせる。\n・バトル開始直後に、さらに攻撃が激しく\n　凶暴になる。", 310500, 1, 0, 0);
INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (310507, 0, 16, 270, 0, 3000, 1.6, "バニフォルテ", "【物理】世にも奇妙な歌う大ウサギ\nの魔獣。その歌声はとても褒められ\nたものではなく、甚だ迷惑である。\n\n・ユニオンバーストは、敵全体に\n　キャラの最大ＨＰに応じた物理割\n　合ダメージを与え、ノックバック\n　させる。その後、目を回している\n　間、自身の物理防御力と魔法防御\n　力を特大ダウン。\n\n・一番前のキャラを中心として、\n　一定範囲に物理中ダメージを与\n　え、さらに行動速度を特大ダウン\n　とスタンさせる。\n\n・前から３キャラに物理小ダメージ\n　を与え、さらに物理防御力を大ダ\n　ウンさせる。", 310507, 1, 0, 0);
