INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (310607, 310600, 16, 270, 310600, 3000, 1.6, "葛藤のうえきちゃん", "【MODE３】その変わらぬ表情は、終にも\n変わらず冒険者は、そのいきものの狂気と畏怖に\n慄く。果たしていきものの謎は解かれるのか…。\n\n・ユニオンバーストは、敵全体に魔法大ダメージ\n　を与え、物理防御力と魔法防御力をダウンさせ\n　る。\n・前方範囲内の敵に物理ダメージを与え、ノック\n　バックさせる。\n・きゅうこんちゃんを召喚。\n　きゅうこんちゃんは攻撃の対象にならず、一定\n　時間が経つと自爆して前方範囲内のキャラに魔\n　法ダメージを与える。\n・左右の葉っぱは、ブレイクすると一定時間消滅\n　する。\n・向かって左側の葉っぱのブレイク時、物理防御\n　力と魔法防御力が小ダウン。\n・本体のブレイク時、物理防御力と魔法防御力が\n　大ダウン。\n・向かって右側の葉っぱのブレイク時、物理防御\n　力と魔法防御力が小ダウン。", 310604, 1, 0, 0);
INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (310700, 0, 16, 270, 0, 3000, 1.4, "なかよしＸ", "", 310700, 1, 0, 0);
INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (310701, 0, 16, 270, 0, 3000, 1.4, "なかよしＸ", "【物理】規格外のアイデアと歪んだ情熱から爆誕\nした鋼鉄の巨人。礎となった者の特性を、異常な\n解釈で再構築し敵の言語中枢と精神を破壊する。\n\n・自身が毒、猛毒、呪い、呪詛、火傷の効果を\n　受けると、自身の物理防御力と魔法防御力と\n　行動速度が大ダウンする。\n・ユニオンバーストは、敵全体にキャラの\n　最大ＨＰに応じた魔法割合ダメージを与える。\n・自身の物理攻撃力をアップさせたあと、\n　敵全体に物理ダメージを与える。\n・前方３キャラに最大ＨＰに応じた魔法割合\n　ダメージを与え、行動速度をダウンさせる。", 310700, 1, 0, 0);
