INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (310704, 0, 16, 270, 0, 3000, 4.2, "なかよしＸ", "【ＭＯＤＥ１】【メルクリウス財団】の\nシャドウを召喚。自分たちの過ちを\n嘲笑うかのように、影の姿で皆を翻弄する。\n\n・自身が毒、猛毒、呪い、火傷の効果を受けると、\n　自身の物理防御力と魔法防御力と行動速度\n　が大ダウンする。\n・目の前の敵１キャラに物理ダメージを与える。", 310700, 1, 0, 0);
INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (310705, 0, 16, 270, 0, 3000, 1.4, "なかよしＸ", "【ＭＯＤＥ２】鋼鉄の球体が、完璧なちぇる語で\n囁きかける。「ちぇる～ん」。心を掴み離さない\n洗脳の言の葉は、確実に世界をちぇるへと誘う。\n\n・ランダムな１キャラに魔法中ダメージを与え、\n　沈黙と誘惑状態にする。", 310700, 1, 0, 0);
INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (310706, 0, 16, 270, 0, 3000, 1.4, "なかよしＸ", "【ＭＯＤＥ３】鋼鉄の絡繰が再び大地に立つ。\n諸刃の剣・ファイナルモードを解放し、\nなかよし部の３人を追い詰める。\n\n・自身が毒、猛毒、呪い、火傷の効果を受けると、\n　自身の物理防御力と魔法防御力と行動速度\n　が大ダウンする。\n・ユニオンバーストは、敵全体にキャラの\n　最大ＨＰに応じた魔法割合ダメージを与える。\n・自身の物理攻撃力をアップさせたあと、\n　敵全体に物理ダメージを与える。\n・前方３キャラに最大ＨＰに応じた魔法割合\n　ダメージを与え、行動速度をダウンさせる。\n・バトル開始直後に、さらに攻撃が激しく\n　凶暴になる。", 310700, 1, 0, 0);
INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (310800, 0, 16, 270, 0, 3000, 2.8, "忍びの頭領ハンゾウ", "【物理】悪徳監査官に雇われた忍びの頭領。\n義を失った忍びは、その身を金に落とす。だが\nしかし、円熟の業は健在で皆の行く手を阻む。\n\n・バトル開始後、監査官が登場する。監査官がダ\n　メージを受けると、その直後に忍びの頭領ハン\n　ゾウの物理防御力と魔法防御力を特大ダウンさ\n　せる。\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた物理割合ダメージを与える。\n・大ガマに変化して毒を吐き、敵全体を毒状態に\n　する。この攻撃は必ず命中する。\n・敵全体に物理中ダメージを与える。", 310800, 1, 0, 0);
