INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (314403, 0, 16, 270, 0, 3000, 1.6, "アンチビースト・トリニティ", "【魔法】魔法少女たちのパワーを吸い取って進化\nしたアンチビースト。ランドソルはこのまま魔の\n手に堕ちてしまうのか！？負けるなノワールズ！\n\n・ユニオンバーストは、自身に近い敵３キャラの\n　うち、ＨＰが80％未満のキャラを誘惑状態に\n　する。その後、自身に近い敵３キャラにキャラ\n　の最大ＨＰに応じた魔法割合ダメージを与える。\n　誘惑状態の敵にはダメージがアップする。\n・アンチビーストの下僕を召喚し敵全体をノック\n　バックさせる。アンチビーストの下僕は、一定\n　時間経過すると最も近い敵１キャラを誘惑状態\n　にしたあと、消滅する。ただしアンチビースト\n　の下僕のＨＰが一定値以下になった場合は、\n　パーティ全体の物理攻撃力と魔法攻撃力を特大\n　アップさせたあと、消滅する。\n・自身に近い敵２キャラに魔法小ダメージを与え\n　る。さらに、自身に物理無効バリアを展開する。\n・自身に近い敵２キャラにキャラの最大ＨＰに応\n　じた魔法割合ダメージを与える。\n・アンチビースト・トリニティ自身のＨＰが\n　30％を切ると、さらに攻撃が激しく凶暴にな\n　る。", 314400, 2, 0, 0);
INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (314404, 0, 16, 270, 0, 3000, 1.6, "アンチビースト・トリニティ", "【ＭＯＤＥ１】闇の渦に囚われ希望を失いかけた\n魔法少女たち―そのとき聞こえてきたのは大事\nな人からのエールの声。立ち上がれ魔法少女よ！\n\n・ユニオンバーストは、前方３キャラのうち、\n　ＨＰが75%未満のキャラを誘惑状態にする。\n　その後、前方３キャラにキャラの最大ＨＰに\n　応じた魔法割合ダメージを与える。\n　誘惑状態の敵にはダメージがアップする。\n・バトル開始時、敵全体を呪い状態にする。\n　このスキルにより付与された呪いのダメージ\n　では、ＴＰが回復しない。\n　さらに、何らかの効果でＨＰを回復しようと\n　した場合、本来の回復量の60%をダウンさせ\n　る。\n・前方２キャラに魔法小ダメージを与える。", 314405, 2, 0, 0);
INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (314405, 0, 16, 270, 0, 3000, 1.6, "アンチビースト・トリニティ", "【ＭＯＤＥ２】再び闇のパワーを蓄え出した仇敵。\nしかし、暴走し始めていたのは敵だけではなかっ\nた―！？　もう誰も、この３人を止められない！\n\n・バリアを張り、敵の攻撃を受け付けなくなる。\n　バリアはダメージ量に関係なく、攻撃を命中\n　させるたびに徐々に耐久性を失っていき、\n　490回攻撃を命中させるとバリアは破壊され\n　る。\n・前方２キャラに魔法小ダメージを与える。", 314405, 2, 0, 0);
INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (314406, 0, 16, 270, 0, 3000, 1.6, "アンチビースト・トリニティ", "【ＭＯＤＥ３】戦いはついにクライマックスへ。\nいとしき生徒の、弟くんの、ドＳさんのために\n愛と希望と闇を抱いて、突き進めノワールズ！\n\n・ユニオンバーストは、前方３キャラのうち、\n　ＨＰが80％未満のキャラを誘惑状態にする。\n　その後、前方３キャラにキャラの最大ＨＰに\n　応じた魔法割合ダメージを与える。\n　誘惑状態の敵にはダメージがアップする。\n・アンチビーストの下僕を召喚し敵全体をノック\n　バックさせる。\n　アンチビーストの下僕は、一定時間経過すると\n　目の前の敵１キャラを誘惑状態にしたあと、消\n　滅する。\n　ただしアンチビーストの下僕のＨＰが一定値\n　以下になった場合は、パーティ全体の物理攻撃\n　力と魔法攻撃力を特大アップさせたあと、消滅\n　する。\n・前方２キャラに魔法小ダメージを与える。\n　さらに、自分に物理無効バリアを展開する。\n・前方２キャラにキャラの最大ＨＰに応じた\n　魔法割合ダメージを与える。\n・アンチビースト・トリニティ自身のＨＰが\n　30％を切ると、さらに攻撃が激しく凶暴にな\n　る。", 314400, 2, 0, 0);
INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (314500, 0, 16, 270, 0, 3000, 1.6, "ひとりぼっちの聖夜の妖精", "", 314500, 1, 0, 0);
INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (314501, 0, 16, 270, 0, 3000, 1.6, "ひとりぼっちの聖夜の妖精", "【物理】遠い昔に人々から忘れられた悲しき妖精。\n信仰を失い、寂しさを紛らわすかのように悪戯を\n働き人目を集めようとする。コンテスト会場の混\n乱を治め、再び信仰を取り戻せるのか！？\n\n・ユニオンバーストは、敵全体にひとりぼっちの\n　聖夜の妖精の物理攻撃力とキャラの最大ＨＰに\n　応じた物理割合ダメージを与える。\n・前から３キャラにひとりぼっちの聖夜の妖精の\n　魔法攻撃力とキャラの最大ＨＰに応じた魔法割\n　合ダメージを与え、大きく吹き飛ばす。このス\n　キルの状態異常はダメージを無効化しても付与\n　される。\n・ひとりぼっちの聖夜の妖精自身の物理/魔法防\n　御力を極大アップ。", 314500, 1, 0, 0);
INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (314502, 0, 16, 270, 0, 3000, 1.6, "ひとりぼっちの聖夜の妖精", "【物理】遠い昔に人々から忘れられた悲しき妖精。\n信仰を失い、寂しさを紛らわすかのように悪戯を\n働き人目を集めようとする。コンテスト会場の混\n乱を治め、再び信仰を取り戻せるのか！？\n\n・ユニオンバーストは、敵全体にひとりぼっちの\n　聖夜の妖精の物理攻撃力とキャラの最大ＨＰに\n　応じた物理割合ダメージを与える。\n・前から３キャラにひとりぼっちの聖夜の妖精の\n　魔法攻撃力とキャラの最大ＨＰに応じた魔法割\n　合ダメージを与え、大きく吹き飛ばす。このス\n　キルの状態異常はダメージを無効化しても付与\n　される。\n・ひとりぼっちの聖夜の妖精自身の物理/魔法防\n　御力を極大アップ。", 314500, 1, 0, 0);
INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (314503, 0, 16, 270, 0, 3000, 1.6, "ひとりぼっちの聖夜の妖精", "【物理】遠い昔に人々から忘れられた悲しき妖精。\n信仰を失い、寂しさを紛らわすかのように悪戯を\n働き人目を集めようとする。コンテスト会場の混\n乱を治め、再び信仰を取り戻せるのか！？\n\n・ユニオンバーストは、敵全体にひとりぼっちの\n　聖夜の妖精の物理攻撃力とキャラの最大ＨＰに\n　応じた物理割合ダメージを与える。\n・前から３キャラにひとりぼっちの聖夜の妖精の\n　魔法攻撃力とキャラの最大ＨＰに応じた魔法割\n　合ダメージを与え、大きく吹き飛ばす。このス\n　キルの状態異常はダメージを無効化しても付与\n　される。\n・ひとりぼっちの聖夜の妖精自身の物理/魔法防\n　御力を極大アップ。\n・ひとりぼっちの聖夜の妖精自身のＨＰが40％\n　を切ると、自身の物理/魔法防御力を大ダウン\n　させ、さらに攻撃が激しく凶暴になる。", 314500, 1, 0, 0);
INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (314504, 0, 16, 270, 0, 3000, 1.6, "ひとりぼっちの聖夜の妖精", "【ＭＯＤＥ１】妖精がお菓子で形作ったミヤコた\nちの甘い猛攻と妖精の悪戯が押し寄せる。聖夜に\n育んだ輝く絆でニセモノたちを打ち砕け！\n\n・ミヤコ、マヒル、リノのシャドウを召喚。\n・前から３キャラに魔法中ダメージを与え、物理\n　防御力を特大ダウン。このスキルの効果はダ\n　メージを無効化しても付与される。", 314500, 1, 0, 0);
INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (314505, 0, 16, 270, 0, 3000, 0.0, "ひとりぼっちの聖夜の妖精", "【ＭＯＤＥ２】妖精が袋から取り出したるは魅惑\nのお菓子。一瞬でも気を許したが最期、甘味な罠\nに堕ちていく。プリンパワーで甘い幻想を打ち砕\nけ！\n\n・ユニオンバーストは、敵全体にひとりぼっちの\n　聖夜の妖精の物理攻撃力とキャラの最大ＨＰに\n　応じた物理割合ダメージを与える。\n・前から３キャラにひとりぼっちの聖夜の妖精の\n　魔法攻撃力とキャラの最大ＨＰに応じた魔法割\n　合ダメージを与え、大きく吹き飛ばす。このス\n　キルの状態異常はダメージを無効化しても付与\n　される。\n・敵全体にキャラの最大ＨＰに応じた物理割合ダ\n　メージを与え、キャラのＨＰが60%を切って\n　いる場合、誘惑状態にする。このスキルの状態\n　異常はダメージを無効化しても付与される。", 314500, 1, 0, 0);
INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (314506, 0, 16, 270, 0, 3000, 1.6, "ひとりぼっちの聖夜の妖精", "【ＭＯＤＥ３】聖夜に独り妖精の哀叫が鳴り響く。\n傍若無人に暴れる妖精をなだめ、プリンの絆で皆\nの幸せのクリスマスと妖精の笑顔を取り戻せ！\n\n・ユニオンバーストは、敵全体にひとりぼっちの\n　聖夜の妖精の物理攻撃力とキャラの最大ＨＰに\n　応じた物理割合ダメージを与える。\n・前から３キャラにひとりぼっちの聖夜の妖精の\n　魔法攻撃力とキャラの最大ＨＰに応じた魔法割\n　合ダメージを与え、大きく吹き飛ばす。このス\n　キルの状態異常はダメージを無効化しても付与\n　される。\n・ひとりぼっちの聖夜の妖精自身の物理/魔法防\n　御力を極大アップ。\n・ひとりぼっちの聖夜の妖精自身のＨＰが30％\n　を切ると、さらに攻撃が激しく凶暴になる。", 314500, 1, 0, 0);
INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (314600, 0, 16, 270, 0, 3000, 1.6, "邪牛神モーモン", "", 314600, 1, 0, 0);
INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (314601, 0, 16, 270, 0, 3000, 1.6, "邪牛神モーモン", "【物理】縁起物を取り込み、願いの力を吸収して\n猛り荒ぶる牛の魔物。怒りに身を落とした猛牛が\n周囲の願いを打ち砕く。\n\n・ユニオンバーストは、敵全体に邪牛神モーモン\n　の物理攻撃力とキャラの最大ＨＰに応じた\n　物理割合ダメージを与え、火傷状態にする。\n・最も魔法攻撃力の高い敵１キャラにキャラの最\n　大ＨＰに応じた物理割合ダメージを与え、ノッ\n　クバックさせると同時にスタンさせる。\n・前から１番目と２番目のキャラに物理大ダメー\n　ジを与える。スキル発動後、自身の物理攻撃力\n　を大アップさせ、物理/魔法防御力が大ダウン。", 314600, 1, 0, 0);
INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (314602, 0, 16, 270, 0, 3000, 1.6, "邪牛神モーモン", "【物理】縁起物を取り込み、願いの力を吸収して\n猛り荒ぶる牛の魔物。怒りに身を落とした猛牛が\n周囲の願いを打ち砕く。\n\n・ユニオンバーストは、敵全体に邪牛神モーモン\n　の物理攻撃力とキャラの最大ＨＰに応じた\n　物理割合ダメージを与え、火傷状態にする。\n・最も魔法攻撃力の高い敵１キャラにキャラの最\n　大ＨＰに応じた物理割合ダメージを与え、ノッ\n　クバックさせると同時にスタンさせる。\n・前から１番目と２番目のキャラに物理大ダメー\n　ジを与える。スキル発動後、自身の物理攻撃力\n　を大アップさせ、物理/魔法防御力が大ダウン。", 314600, 1, 0, 0);
INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (314603, 0, 16, 270, 0, 3000, 1.6, "邪牛神モーモン", "【物理】縁起物を取り込み、願いの力を吸収して\n猛り荒ぶる牛の魔物。怒りに身を落とした猛牛が\n周囲の願いを打ち砕く。\n\n・ユニオンバーストは、敵全体に邪牛神モーモン\n　の物理攻撃力とキャラの最大ＨＰに応じた\n　物理割合ダメージを与え、火傷状態にする。\n・通常攻撃は、目の前の敵１キャラに邪牛神モー\n　モンの物理攻撃力とキャラの最大ＨＰに応じた\n　物理割合ダメージを与える。\n・最も魔法攻撃力の高い敵１キャラにキャラの最\n　大ＨＰに応じた物理割合ダメージを与え、ノッ\n　クバックさせると同時にスタンさせる。\n・前から１番目と２番目のキャラに物理大ダメー\n　ジを与える。スキル発動後、自身の物理攻撃力\n　を大アップさせ、物理/魔法防御力が大ダウン。\n・残り時間が30秒を切ると、さらに攻撃が激し\n　く凶暴になる。", 314600, 1, 0, 0);
INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (314604, 0, 16, 270, 0, 3000, 1.6, "邪牛神モーモン", "【ＭＯＤＥ１】力への渇望から魔物は子分を率\nい、再び略奪へ踏みだす。迫り来るならず者た\nちを打倒し、おせちの具材を守り抜け。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。\n　この防御力アップ効果で上昇した防御力は、バ\n　トル中に受ける防御力ダウンスキルの影響を受\n　けない。\n・邪牛神モーモンおよびハイオークから受けるす\n　べてのダメージで発生する、パーティキャラの\n　ＴＰ回復量を中ダウンさせる。\n・バトル開始時、ハイオークを３体召喚する。\n・ハイオークを１体倒すたびに、残りハイオーク\n　数が１減少する。その後、ハイオークが再度\n　召喚される。\n・目の前の敵１キャラに魔法小ダメージを与え、　物理防御力を大ダウンさせる。このスキルの状\n　態異常はダメージを無効化しても付与される。", 314600, 1, 0, 0);
INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (314605, 0, 16, 270, 0, 3000, 1.6, "邪牛神モーモン", "【ＭＯＤＥ２】猛牛の強靭な肉体が爆ぜる。\n怪腕から繰り出される殴打をまともに受けると\n肉体と、そこに宿る全ての煩悩をも吹き飛ばす。\n\n・敵からの攻撃による麻痺、スタン、束縛、凍結、\n　時間停止、行動速度低下効果への耐性を持たな\n　い。\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。\n　この防御力アップ効果で上昇した防御力は、バ\n　トル中に受ける防御力ダウンスキルの影響を受\n　けない。\n・邪牛神モーモンから受けるすべてのダメージで\n　発生する、パーティキャラのＴＰ回復量を中ダ\n　ウンさせる。\n・ユニオンバーストは、敵全体に邪牛神モーモン\n　の物理攻撃力とキャラの最大ＨＰに応じた\n　物理割合ダメージを与え、火傷状態にする。\n・通常攻撃は、目の前の敵１キャラに邪牛神モー\n　モンの物理攻撃力とキャラの最大ＨＰに応じた\n　物理割合ダメージを与える。\n・前から１番目と２番目のキャラに物理大ダメー\n　ジを与える。スキル発動後、自身の物理攻撃力\n　を大アップさせ、物理/魔法防御力が大ダウン。\n・敵全体のステータスアップ効果を解除し、一定\n　時間バトルフィールドを展開する。\n【バトルフィールド効果】\n・敵全体の物理攻撃力と魔法攻撃力を100％\n　ダウンした後、物理攻撃力と魔法攻撃力を極大\n　アップ。\n・邪牛神モーモンの物理攻撃力を100％ダウン\n　した後、物理攻撃力を極大アップ。", 314605, 1, 0, 0);
INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (314606, 0, 16, 270, 0, 3000, 1.6, "邪牛神モーモン", "【ＭＯＤＥ３】猛牛は痛手を負おうとも、鼻息\nを荒げ立ちはだかる。妄執に取りつかれた迷惑\nな魔物を打ち倒し、謹賀新年を取り戻せ！\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。\n　この防御力アップ効果で上昇した防御力は、バ\n　トル中に受ける防御力ダウンスキルの影響を受\n　けない。\n・邪牛神モーモンから受けるすべてのダメージで\n　発生する、パーティキャラのＴＰ回復量を中ダ\n　ウンさせる。\n・ユニオンバーストは、敵全体に邪牛神モーモン\n　の物理攻撃力とキャラの最大ＨＰに応じた\n　物理割合ダメージを与え、火傷状態にする。\n・最も魔法攻撃力の高い敵１キャラに物理割合\n　ダメージを与え、ノックバックさせると同時に\n　スタンさせる。\n・前から１番目と２番目のキャラに物理大ダメー\n　ジを与える。スキル発動後、自身の物理攻撃力\n　を大アップさせ、物理/魔法防御力が大ダウン。\n・残り時間が30秒を切ると、さらに攻撃が激し\n　く凶暴になる。", 314605, 1, 0, 0);
INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (314700, 0, 16, 270, 0, 3000, 1.6, "グレーターゴーレム", "", 314700, 1, 0, 0);
INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (314701, 0, 16, 270, 0, 3000, 1.6, "グレーターゴーレム", "【物理】同族のゴーレムを次々に呼び寄せる\n岩石の魔物。大地を揺さぶる巨腕が冒険者たちに\n襲い掛かる。\n\n・主人公が目覚めていないため、バトル開始時\n　に全キャラクターのユニオンバーストが発動\n　不能状態になる。\n・ぺコリーヌが目覚めていないため、本バトルで\n　はペコリーヌ（同名キャラ含む）が編成不可。\n・ユニオンバーストは、前から4キャラにグレー\n　ターゴーレムの物理攻撃力とキャラの最大ＨＰ\n　に応じた物理割合ダメージを与える。\n・敵全体をノックバックさせ、ゴーレムを召喚す\n　る。さらに自分の物理攻撃力を中アップさせ\n　る。\n・前から１番目のキャラに物理大ダメージ。", 314700, 1, 0, 0);
