INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (314705, 0, 16, 270, 0, 3000, 1.6, "グレーターゴーレム", "【ＭＯＤＥ２】森の奥から突如、現れた数多の\n虚像たち。自分たちの似姿の前に本当の絆が\n試される。\n\n・主人公が目覚めていないため、バトル開始時\n　に全キャラクターのユニオンバーストが発動\n　不能状態になる。\n・ぺコリーヌが目覚めていないため、本バトルで\n　はペコリーヌ（同名キャラ含む）が編成不可。\n・バトル開始時、自分の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・敵全体をノックバックさせ複数のシャドウを召\n　喚する。\n・前から１番目のキャラに物理小ダメージ。", 314704, 1, 0, 0);
INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (314706, 0, 16, 270, 0, 3000, 1.6, "グレーターゴーレム", "【ＭＯＤＥ３】ひび割れた巨岩の咆哮に岩々が\n再び立ちはだかる。岩の躯体の奥から覗く紅き\n瞳が敵を見据え、巨大な剛腕で全てを薙ぎ払う。\n\n・主人公が目覚めていないため、バトル開始時\n　に全キャラクターのユニオンバーストが発動\n　不能状態になる。\n・ぺコリーヌが目覚めていないため、本バトルで\n　はペコリーヌ（同名キャラ含む）が編成不可。\n・バトル開始時、自分の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・ユニオンバーストは、前から4キャラにグレー\n　ターゴーレムの物理攻撃力とキャラの最大ＨＰ\n　に応じた物理割合ダメージを与える。\n・敵全体をノックバックさせ、ゴーレムを召喚す\n　る。さらに自分の物理攻撃力を中アップさせ\n　る。このスキルの効果は発動するたびにアッ\n　プする。\n・前から１番目のキャラに物理大ダメージ。", 314706, 1, 0, 0);
INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (314800, 0, 16, 270, 0, 3000, 1.6, "オーバーロードフォース", "", 314800, 1, 0, 0);
INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (314801, 0, 16, 270, 0, 3000, 1.6, "オーバーロードフォース", "【物理】『強さを求める願い』から生まれた力。\n本当の強さとは何か？　自分自身に問い続け答え\nを見つけられるのか。絆を取り戻すべく立ち上が\nれ。\n\n・ユニオンバーストは、敵全体に物理大ダメージ\n　を与える。オーバーロードスフィアのターゲッ\n　トがブレイクしていない場合、ダメージがアッ\n　プする。\n・敵全体の物理防御力を中ダウンさせ、物理中ダ\n　メージを与える。オーバーロードスフィアの\n　ターゲットがブレイクしていない場合、ダメー\n　ジとスキルの効果がアップする。このスキルの\n　効果はダメージを無効化しても付与される。\n・前から1番目のキャラに物理大ダメージ。\n・オーバーロードスフィアのターゲットのブレイ\n　ク時、自身の物理防御力と魔法防御力が中ダウ\n　ン。", 314800, 1, 0, 0);
