INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (320400, 0, 16, 270, 0, 3000, 1.6, "赤い霧の精霊", "【魔法】『レジェンドオブアストルム』を守護す\nる精霊。世界の調和を保つため、外部より紛れ込\nんだ異端を排除すべく始動する。\n\n・ユニオンバーストは、敵全体に魔法大ダメージ\n　を与える。\n・敵全体の物理攻撃力と魔法攻撃力を大ダウンさ\n　せる。さらに自身にＴＰ継続回復状態を付与す\n　る。\n・最も近い敵１キャラに魔法大ダメージを与える。\n・残り時間が15秒を切ると、さらに攻撃が激し\n　く凶暴になる。その後、自身のＴＰを特大ダウ\n　ンする。", 320400, 2, 0, 0);
