INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (321000, 0, 16, 200, 0, 3000, 1.6, "強獣ゲドン", "【物理】ジオ・ゲヘナに聳える巨大\n怪獣型魔物。口から熱光線を放出し\n豪腕と尾で矮小な獲物を蹂躙する。\n\n・ユニオンバーストは、敵全体のバ\n　リアを解除し、敵全体に魔法割合\n　ダメージを与え、さらに物理防御\n　力を60％ダウンし、ＨＰ吸収を\n　特大ダウンさせる。このスキルの\n　状態異常はダメージを無効化して\n　も付与される。\n・敵全体に物理大ダメージを与え\n　る。さらに、キャラのＨＰが\n　90％未満の場合スタンさせる。\n　このスキルの状態異常はダメージ\n　を無効化しても付与される。\n・敵全体に物理特大ダメージ。\n・強獣ゲドンのＨＰが一定値以下に\n　なると、ユニオンバーストが強化\n　され、スキルが物理と魔法ダメー\n　ジになる。", 321000, 1, 0, 0);
INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (321100, 0, 16, 270, 0, 3000, 1.6, "魔物植物・モベルベ", "【魔法】品種改良を施され、卑劣漢に服従するよ\nう仕立てられた魔物植物。絢爛と咲き誇る巨躯は\n悪意に操られるがまま花祭りの開催を脅かす。\n\n・ユニオンバーストは、敵全体に最大ＨＰに応じ\n　た魔法割合ダメージを与え、ＴＰを中ダウンさ\n　せる。さらに物理防御力と魔法防御力を大ダウ\n　ンさせる。自身の物理防御力と魔法防御力を\n　特大アップさせる。このスキルの状態異常は\n　ダメージを無効化しても付与される。このスキ\n　ルはダメージを無効化してもＴＰダウンさせる。\n・敵全体に魔法大ダメージを与え、行動速度を中\n　ダウンさせる。このスキルの状態異常は\n　ダメージを無効化しても付与される。\n・自身の魔法攻撃力を大アップさせる。\n・魔法攻撃力が最も低い敵１キャラに魔法特大ダ\n　メージを与え、スタン状態を付与する。このス\n　キルの状態異常はダメージを無効化しても付与\n　される。", 321100, 2, 0, 0);
