INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (626501, 0, 7, 450, 0, 740, 1.965, "ライラエルのシャドウ", "ライラエルの姿をした特殊な魔物\n\n・特殊スキルは、環境効果【界雷】を発動する。\n　このスキルの効果は敵、味方すべてに適用され\n　る。魔法攻撃のダメージが小アップし、物理攻\n　撃を行うキャラが行動する度にそのキャラの最\n　大ＨＰに応じて最大99999の割合ダメージ\n　を与える。", 126501, 2, 0, 1);
