INSERT INTO `v1_841bbf01821973782adb678df4b88f379bb31208a3fdf5789d340ed60ff11b50` VALUES ("アヤネ", "2030/12/17 14:59:59", 0.0, 2, 0.0, 10046, 24, 0.0, 102301, "やったね、ぷうきち！\nこれならお兄ちゃんたちの\nお手本になれたよね！", 24001, "2020/07/31 12:00:00", 401, 2);
INSERT INTO `v1_841bbf01821973782adb678df4b88f379bb31208a3fdf5789d340ed60ff11b50` VALUES ("アヤネ", "2030/12/17 14:59:59", 0.0, 2, 2.21, 10046, 24, 0.0, 102301, "私たちの勝ちだね！\nぷうきちと一緒なら\nどんなボールでも\nホームランを打ってみせるよ！", 24002, "2020/07/31 12:00:00", 402, 1);
INSERT INTO `v1_841bbf01821973782adb678df4b88f379bb31208a3fdf5789d340ed60ff11b50` VALUES ("アヤネ", "2030/12/17 14:59:59", 0.0, 2, 0.0, 10046, 24, 0.0, 102301, "やったやったぁ！\nお兄ちゃん、見ててくれた？\n私、すっごくがんばったよ！", 24003, "2020/07/31 12:00:00", 403, 2);
INSERT INTO `v1_841bbf01821973782adb678df4b88f379bb31208a3fdf5789d340ed60ff11b50` VALUES ("アサヒ", "2030/12/17 14:59:59", 0.0, 1, 1.99, 10046, 24, 0.0, 192201, "特訓の成果が出たみたいだ！\nもっと練習を重ねて\nこの調子で上達していこう！", 24004, "2020/07/31 12:00:00", 701, 2);
INSERT INTO `v1_841bbf01821973782adb678df4b88f379bb31208a3fdf5789d340ed60ff11b50` VALUES ("アサヒ", "2030/12/17 14:59:59", 0.0, 2, 2.21, 10046, 24, 0.0, 192201, "よし、いい感じだ！\nキャプテンとしての仕事は\nきっちり果たせそうだよ", 24005, "2020/07/31 12:00:00", 702, 1);
INSERT INTO `v1_841bbf01821973782adb678df4b88f379bb31208a3fdf5789d340ed60ff11b50` VALUES ("アサヒ", "2030/12/17 14:59:59", 0.0, 1, 3.33, 10046, 24, 0.0, 192201, "試合に向けて\nいい感覚を掴めた気がするよ！\nこのまま一気に全国大会だ！", 24006, "2020/07/31 12:00:00", 703, 2);
INSERT INTO `v1_841bbf01821973782adb678df4b88f379bb31208a3fdf5789d340ed60ff11b50` VALUES ("部員３", "2030/12/17 14:59:59", 0.0, 2, 0.0, 10046, 24, 0.0, 192301, "このくらい余裕だ\nオレたちは全国大会を\n目指してるんだからな", 24007, "2020/07/31 12:00:00", 801, 2);
INSERT INTO `v1_841bbf01821973782adb678df4b88f379bb31208a3fdf5789d340ed60ff11b50` VALUES ("部員３", "2030/12/17 14:59:59", 0.0, 2, 2.68, 10046, 24, 0.0, 192301, "オレだって\nビーチベースボール選手だ\nどんな球だろうが\n全部スタンドまで飛ばしてやる", 24008, "2020/07/31 12:00:00", 802, 1);
INSERT INTO `v1_841bbf01821973782adb678df4b88f379bb31208a3fdf5789d340ed60ff11b50` VALUES ("部員３", "2030/12/17 14:59:59", 0.0, 2, 0.0, 10046, 24, 0.0, 192301, "いい感じだ\nこれなら試合でも活躍できそうだ\n大暴れしてやる！", 24009, "2020/07/31 12:00:00", 803, 2);
INSERT INTO `v1_841bbf01821973782adb678df4b88f379bb31208a3fdf5789d340ed60ff11b50` VALUES ("", "2030/12/17 14:59:59", 0.0, 1, 0.0, 10046, 24, 0.0, 192501, "やった！", 24010, "2020/07/31 12:00:00", 501, 1);
INSERT INTO `v1_841bbf01821973782adb678df4b88f379bb31208a3fdf5789d340ed60ff11b50` VALUES ("", "2030/12/17 14:59:59", 0.0, 1, 0.0, 10046, 24, 0.0, 192501, "勝ったよ！", 24011, "2020/07/31 12:00:00", 502, 1);
INSERT INTO `v1_841bbf01821973782adb678df4b88f379bb31208a3fdf5789d340ed60ff11b50` VALUES ("", "2030/12/17 14:59:59", 0.0, 1, 0.0, 10046, 24, 0.0, 192501, "いい調子！", 24012, "2020/07/31 12:00:00", 503, 1);
INSERT INTO `v1_841bbf01821973782adb678df4b88f379bb31208a3fdf5789d340ed60ff11b50` VALUES ("ミサト", "2030/12/17 14:59:59", 0.0, 2, 5.33, 10046, 24, 0.0, 113501, "あら～…もしかして\n勝っちゃったのかしら？\nみんなの練習を見ていたから\nかもしれないわね", 24013, "2020/07/31 12:00:00", 111, 6);
INSERT INTO `v1_841bbf01821973782adb678df4b88f379bb31208a3fdf5789d340ed60ff11b50` VALUES ("ミサト", "2030/12/17 14:59:59", 0.0, 2, 6.85, 10046, 24, 0.0, 113501, "みんなの力になりたくて\n先生も頑張ってみました～！\nうまく投げられたみたいで\nよかったわ～！", 24014, "2020/07/31 12:00:00", 112, 1);
INSERT INTO `v1_841bbf01821973782adb678df4b88f379bb31208a3fdf5789d340ed60ff11b50` VALUES ("ミサト", "2030/12/17 14:59:59", 0.0, 2, 3.0, 10046, 24, 0.0, 113501, "練習お疲れさま～！\n先生も協力できて\nとってもうれしいわ～♪\n次も頑張りましょうね～！", 24015, "2020/07/31 12:00:00", 113, 1);
INSERT INTO `v1_841bbf01821973782adb678df4b88f379bb31208a3fdf5789d340ed60ff11b50` VALUES ("ダイチ", "2030/12/17 14:59:59", 0.0, 2, 6.11, 10046, 24, 0.0, 192401, "まだ練習が足りねえみてえだな\nこれっぽっちも本気で\n投げてねえっつーの\nもう一回やんぞ！", 24016, "2020/07/31 12:00:00", 611, 1);
INSERT INTO `v1_841bbf01821973782adb678df4b88f379bb31208a3fdf5789d340ed60ff11b50` VALUES ("ダイチ", "2030/12/17 14:59:59", 0.0, 2, 6.75, 10046, 24, 0.0, 192401, "ふん、当然の結果だな\n俺の球は、誰にも打てねえよ！\nまたやりてえっつーなら\nとことん付き合ってやるぜ！", 24017, "2020/07/31 12:00:00", 612, 1);
INSERT INTO `v1_841bbf01821973782adb678df4b88f379bb31208a3fdf5789d340ed60ff11b50` VALUES ("ダイチ", "2030/12/17 14:59:59", 0.0, 2, 0.0, 10046, 24, 0.0, 192401, "おっしゃあ！\nこの勢いで全国に行ってやるぜ！\n全員三振で仕留めてやる！", 24018, "2020/07/31 12:00:00", 613, 2);
INSERT INTO `v1_841bbf01821973782adb678df4b88f379bb31208a3fdf5789d340ed60ff11b50` VALUES ("ハツネ", "2030/12/17 14:59:59", 0.0, 1, 4.52, 10046, 24, 0.0, 113401, "きらーん☆\nハツネちゃんの大勝利なのだ～！\nみんながもっと上達できるように\n私もまだまだ頑張るからね！", 24019, "2020/07/31 12:00:00", 211, 2);
INSERT INTO `v1_841bbf01821973782adb678df4b88f379bb31208a3fdf5789d340ed60ff11b50` VALUES ("ハツネ", "2030/12/17 14:59:59", 0.0, 1, 3.89, 10046, 24, 0.0, 113401, "やったあ！　私の勝ちだね！\nチアリーダーもピッチャーも\nハツネちゃんに任せてよ！", 24020, "2020/07/31 12:00:00", 212, 2);
INSERT INTO `v1_841bbf01821973782adb678df4b88f379bb31208a3fdf5789d340ed60ff11b50` VALUES ("ハツネ", "2030/12/17 14:59:59", 0.0, 2, 13.49, 10046, 24, 0.0, 113401, "よお～し、この調子でもっと\n頑張ろう…と思ったけど\n超能力を使ったから…\n眠くなってきちゃった…すやぁ…", 24021, "2020/07/31 12:00:00", 213, 1);
INSERT INTO `v1_841bbf01821973782adb678df4b88f379bb31208a3fdf5789d340ed60ff11b50` VALUES ("カヤ", "2030/12/17 14:59:59", 0.0, 2, 3.17, 10046, 24, 0.0, 106501, "いい勝負だったぜ！\nたぎっちまって\nつい本気で投げちまったよ", 24022, "2020/07/31 12:00:00", 311, 1);
INSERT INTO `v1_841bbf01821973782adb678df4b88f379bb31208a3fdf5789d340ed60ff11b50` VALUES ("カヤ", "2030/12/17 14:59:59", 0.0, 2, 4.42, 10046, 24, 0.0, 106501, "どうだ？\nオレ様のボールは熱かっただろ！\nま、でもおまえらの根性も\nなかなかのもんだったぜ", 24023, "2020/07/31 12:00:00", 312, 1);
INSERT INTO `v1_841bbf01821973782adb678df4b88f379bb31208a3fdf5789d340ed60ff11b50` VALUES ("カヤ", "2030/12/17 14:59:59", 0.0, 1, 2.89, 10046, 24, 0.0, 106501, "なんだよ、そんなもんか？\nこっちはまだまだ投げたりねえ\nもっと勝負しようぜ！", 24024, "2020/07/31 12:00:00", 313, 6);
INSERT INTO `v1_841bbf01821973782adb678df4b88f379bb31208a3fdf5789d340ed60ff11b50` VALUES ("アヤネ", "2030/12/17 14:59:59", 0.0, 3, 3.71, 10046, 24, 0.0, 102301, "あとちょっとだったのに～！\n次こそぷうきちと一緒に\n勝ってみせるからね！", 24025, "2020/07/31 12:00:00", 421, 4);
INSERT INTO `v1_841bbf01821973782adb678df4b88f379bb31208a3fdf5789d340ed60ff11b50` VALUES ("アサヒ", "2030/12/17 14:59:59", 0.0, 1, 5.1, 10046, 24, 0.0, 192201, "いい勝負だったね！\nでも、やっぱり悔しいな…\nもっと練習して\n今度は絶対に俺が勝つから！", 24026, "2020/07/31 12:00:00", 721, 2);
INSERT INTO `v1_841bbf01821973782adb678df4b88f379bb31208a3fdf5789d340ed60ff11b50` VALUES ("部員３", "2030/12/17 14:59:59", 0.0, 3, 4.34, 10046, 24, 0.0, 192301, "ちっ…あと一歩足りなかったか\n次こそは絶対に勝つから\nそのつもりでいろ", 24027, "2020/07/31 12:00:00", 821, 1);
INSERT INTO `v1_841bbf01821973782adb678df4b88f379bb31208a3fdf5789d340ed60ff11b50` VALUES ("", "2030/12/17 14:59:59", 0.0, 1, 0.0, 10046, 24, 0.0, 192501, "いい勝負だったよ\nまたやろうね", 24028, "2020/07/31 12:00:00", 521, 1);
INSERT INTO `v1_841bbf01821973782adb678df4b88f379bb31208a3fdf5789d340ed60ff11b50` VALUES ("ミサト", "2030/12/17 14:59:59", -360.0, 2, 5.62, 10046, 24, 0.0, 113501, "とってもよくできました～♪\n頑張ってくれて先生うれしいわ\nこれからも試合に向けて\n練習していきましょうね～", 24029, "2020/07/31 12:00:00", 101, 1);
INSERT INTO `v1_841bbf01821973782adb678df4b88f379bb31208a3fdf5789d340ed60ff11b50` VALUES ("ミサト", "2030/12/17 14:59:59", -360.0, 2, 9.58, 10046, 24, 0.0, 113501, "頑張った成果ね～！\nこのまま練習を続ければ\nもっとすごい成績を残せそうよ\n期待してますからね～！", 24030, "2020/07/31 12:00:00", 102, 1);
INSERT INTO `v1_841bbf01821973782adb678df4b88f379bb31208a3fdf5789d340ed60ff11b50` VALUES ("ミサト", "2030/12/17 14:59:59", -360.0, 2, 0.0, 10046, 24, 0.0, 113501, "いい子、いい子～♪\n頑張り屋さんのあなたを\nたっくさん褒めてあげますからね", 24031, "2020/07/31 12:00:00", 103, 2);
INSERT INTO `v1_841bbf01821973782adb678df4b88f379bb31208a3fdf5789d340ed60ff11b50` VALUES ("ミサト", "2030/12/17 14:59:59", -360.0, 1, 9.13, 10046, 24, 0.0, 113501, "夢に向けて、一歩前進できたわね\nこの調子で、夢を叶えましょう\n目指せ、全国大会～！", 24032, "2020/07/31 12:00:00", 104, 2);
INSERT INTO `v1_841bbf01821973782adb678df4b88f379bb31208a3fdf5789d340ed60ff11b50` VALUES ("ミサト", "2030/12/17 14:59:59", -360.0, 2, 8.27, 10046, 24, 0.0, 113501, "絶好調ね～♪\nきっと試合でも大活躍\n間違いなしだわ\n楽しみにしていますからね～！", 24033, "2020/07/31 12:00:00", 105, 1);
