INSERT INTO `v1_841bbf01821973782adb678df4b88f379bb31208a3fdf5789d340ed60ff11b50` VALUES (192201, 1, "2020/07/31 12:00:00", 24, 2.21, 10046, 24005, 2, "2030/12/17 14:59:59", "よし、いい感じだ！\nキャプテンとしての仕事は\nきっちり果たせそうだよ", 0.0, 702, "アサヒ", 0.0);
