INSERT INTO `v1_841bbf01821973782adb678df4b88f379bb31208a3fdf5789d340ed60ff11b50` VALUES (113501, 6, "2020/07/31 12:00:00", 24, 5.33, 10046, 24013, 2, "2030/12/17 14:59:59", "あら～…もしかして\n勝っちゃったのかしら？\nみんなの練習を見ていたから\nかもしれないわね", 0.0, 111, "ミサト", 0.0);
