INSERT INTO `v1_841bbf01821973782adb678df4b88f379bb31208a3fdf5789d340ed60ff11b50` VALUES (113501, 1, "2020/07/31 12:00:00", 24, 3.0, 10046, 24015, 2, "2030/12/17 14:59:59", "練習お疲れさま～！\n先生も協力できて\nとってもうれしいわ～♪\n次も頑張りましょうね～！", 0.0, 113, "ミサト", 0.0);
INSERT INTO `v1_841bbf01821973782adb678df4b88f379bb31208a3fdf5789d340ed60ff11b50` VALUES (192401, 1, "2020/07/31 12:00:00", 24, 6.11, 10046, 24016, 2, "2030/12/17 14:59:59", "まだ練習が足りねえみてえだな\nこれっぽっちも本気で\n投げてねえっつーの\nもう一回やんぞ！", 0.0, 611, "ダイチ", 0.0);
