INSERT INTO `v1_841bbf01821973782adb678df4b88f379bb31208a3fdf5789d340ed60ff11b50` VALUES (192401, 2, "2020/07/31 12:00:00", 24, 0.0, 10046, 24018, 2, "2030/12/17 14:59:59", "おっしゃあ！\nこの勢いで全国に行ってやるぜ！\n全員三振で仕留めてやる！", 0.0, 613, "ダイチ", 0.0);
INSERT INTO `v1_841bbf01821973782adb678df4b88f379bb31208a3fdf5789d340ed60ff11b50` VALUES (113401, 2, "2020/07/31 12:00:00", 24, 4.52, 10046, 24019, 1, "2030/12/17 14:59:59", "きらーん☆\nハツネちゃんの大勝利なのだ～！\nみんながもっと上達できるように\n私もまだまだ頑張るからね！", 0.0, 211, "ハツネ", 0.0);
