INSERT INTO `v1_841bbf01821973782adb678df4b88f379bb31208a3fdf5789d340ed60ff11b50` VALUES (113401, 1, "2020/07/31 12:00:00", 24, 13.49, 10046, 24021, 2, "2030/12/17 14:59:59", "よお～し、この調子でもっと\n頑張ろう…と思ったけど\n超能力を使ったから…\n眠くなってきちゃった…すやぁ…", 0.0, 213, "ハツネ", 0.0);
