INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (10142, 100, 24, 100, "2030/12/17 14:59:59", "訓練成功、おめでとうございます\n褒めるべき時にはしっかり褒めるのも\n教官の務めですものね。それとも、\n罵られる方が好みですか？　クスクス", 10.85, 7, 0.0, "エリコ", "2024/03/31 12:00:00", 1, 0.0, 129901);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (10142, 101, 24, 101, "2030/12/17 14:59:59", "厳しい訓練も、終わってみれば\n短く感じるものですわね。\n正直、私は暴れ足りないくらい…\nいえ、何でもありませんわ。クスクス", 11.15, 2, 0.0, "エリコ", "2024/03/31 12:00:00", 1, 0.0, 129901);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (10142, 102, 24, 102, "2030/12/17 14:59:59", "行く手を阻む忌々しい邪魔者を\n蹴散らして、見事ゴールイン…！\nまるで私とあのおかたの人生を\n象徴するような道のりでした…", 6.4, 5, 0.0, "エリコ", "2024/03/31 12:00:00", 7, 0.0, 129901);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (10142, 103, 24, 103, "2030/12/17 14:59:59", "まったくもって不甲斐ない結果です\nしかし、挽回の機会はまだありますわ\n次はゴミ虫の地位から脱却できるよう\n死力を尽くしなさい。いいですね？", 0.0, 3, 0.0, "エリコ", "2024/03/31 12:00:00", 3, 0.0, 129901);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (10142, 104, 24, 104, "2030/12/17 14:59:59", "クスクス…ああご心配なさらず\nなんとも情けない成績を残す輩には\nどんな罰が相応しいか、考えている\nだけですので…クスクスクス", 2.43, 8, 0.0, "エリコ", "2024/03/31 12:00:00", 2, 0.0, 129901);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (10142, 105, 24, 105, "2030/12/17 14:59:59", "私が教官として認められなければ、\nあのおかたを失望させてしまう…\nそれだけはあってはなりません\nあなた方も、わかっていますわね？", 9.08, 3, 0.0, "エリコ", "2024/03/31 12:00:00", 4, 0.0, 129901);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (10142, 106, 24, 106, "2030/12/17 14:59:59", "まずまずの結果ではありますが、\nこの程度で満足してもらっては\n困りますわ。エリコ班の一員として、\nより高みを目指していただきますので", 0.0, 6, 0.0, "エリコ", "2024/03/31 12:00:00", 6, 0.0, 129901);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (10142, 107, 24, 107, "2030/12/17 14:59:59", "私の指導を受けながら、この結果は…\nもっと真剣に考えねばなりませんね\nあなた方が怠けずにいるには、\nどんなしつけが必要かを…クスクス", 10.6, 8, 0.0, "エリコ", "2024/03/31 12:00:00", 3, 0.0, 129901);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (10142, 108, 24, 108, "2030/12/17 14:59:59", "ひとまず訓練をこなしたご褒美か、\n実力を出しきれたとは言えない罰か…\nみなさんにどちらを与えるべきか\n少し悩む結果ですわね", 7.54, 4, 0.0, "エリコ", "2024/03/31 12:00:00", 6, 0.0, 129901);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (10142, 109, 24, 109, "2030/12/17 14:59:59", "なかなかいい戦いぶりでしたわ\nこれもあなた方の頑張りと、何より\n優秀な教官である私の教育の賜物\nそうですわね？　クスクスクス…", 11.5, 7, 0.0, "エリコ", "2024/03/31 12:00:00", 2, 0.0, 129901);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (10142, 110, 24, 110, "2030/12/17 14:59:59", "これだけ進めたのならば合格点ですわ\nでも、もっと高みを目指したいと\n思っているのなら…さらなる訓練にも\n付き合って差し上げましょう", 8.83, 1, 0.0, "エリコ", "2024/03/31 12:00:00", 2, 0.0, 129901);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (10142, 111, 24, 111, "2030/12/17 14:59:59", "未熟で頼りなかったあなた方が、\n見違えるように成長しました\nなるほど…これこそが、教官冥利に\n尽きるという気持ちなのですね", 6.68, 2, 0.0, "エリコ", "2024/03/31 12:00:00", 1, 0.0, 129901);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (10142, 112, 24, 112, "2030/12/17 14:59:59", "まさかあなた方が、私の予想すら\n超える力を発揮するとは…驚きました\nもはやゴミ虫などではありません\n一人前の騎士として胸を張りなさい", 8.8, 2, 0.0, "エリコ", "2024/03/31 12:00:00", 6, 0.0, 129901);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (10142, 113, 24, 113, "2030/12/17 14:59:59", "クスクス、見事な働きです\nみなさんならできると信じていました\n他でもない私の指導を受けた、\nエリコ班の優秀な新兵なのですから", 8.9, 1, 0.0, "エリコ", "2024/03/31 12:00:00", 2, 0.0, 129901);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (10142, 114, 24, 114, "2030/12/17 14:59:59", "なんてすばらしい成績…！\n今日という日は『あのおかたを想い\nエリコが限界を超えた記念日』として\n歴史に刻まれることでしょう…！", 3.7, 5, 0.0, "エリコ", "2024/03/31 12:00:00", 2, 0.0, 129901);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (10142, 200, 24, 200, "2030/12/17 14:59:59", "よし、いい訓練になったな\nおまえたちもよく動けてたし、\nちゃんと成長できてると思うぜ\nその調子で頑張れよっ", 7.6, 2, 0.0, "マコト", "2024/03/31 12:00:00", 1, 0.0, 129801);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (10142, 201, 24, 201, "2030/12/17 14:59:59", "ひとまずゴールおめでとう！\nでも、努力は継続してこそだからな\nおまえたちにやる気があるなら、\n何度だって付き合ってやるよ", 2.25, 1, 0.0, "マコト", "2024/03/31 12:00:00", 2, 0.0, 129801);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (10142, 202, 24, 202, "2030/12/17 14:59:59", "行軍訓練はこれにて完了！\n…なーんて思ってないよな？\n学んだことが体に染みつくまで\n地道に繰り返すのが練習ってもんさ", 3.28, 1, 0.0, "マコト", "2024/03/31 12:00:00", 2, 0.0, 129801);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (10142, 203, 24, 203, "2030/12/17 14:59:59", "みんなに無理をさせちまったか…\nでもこれで諦めることはないぜ\n次はきっとうまくいく。そう信じて\n鍛え続ければ、道は開けるはずだ！", 2.98, 1, 0.0, "マコト", "2024/03/31 12:00:00", 4, 0.0, 129801);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (10142, 204, 24, 204, "2030/12/17 14:59:59", "こいつは悔しい結果だな…\nま、反省点はいろいろあったし\nひとつずつ乗り越えていこう\nあたしたちで力を合わせて、なっ！", 3.0, 1, 0.0, "マコト", "2024/03/31 12:00:00", 4, 0.0, 129801);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (10142, 205, 24, 205, "2030/12/17 14:59:59", "おまえたちはまだ未熟な訓練兵、\n失敗を必要以上に悔やむことはねえよ\nでも実戦じゃこうはいかないからな\n次は気合を入れ直していけよっ！", 10.3, 2, 0.0, "マコト", "2024/03/31 12:00:00", 1, 0.0, 129801);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (10142, 206, 24, 206, "2030/12/17 14:59:59", "これがおまえたちの全力？\nいやいや、そんなはずはないよなっ\nおまえらならもっと強くなれる！\nあたしが言うんだ、間違いないぜ！", 2.93, 1, 0.0, "マコト", "2024/03/31 12:00:00", 6, 0.0, 129801);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (10142, 207, 24, 207, "2030/12/17 14:59:59", "おまえたちの実力を考えりゃ、\nベストを尽くせたとは言えない成績だ\nなら、次やるべきことも決まってる\n気持ちを切り替えて、鍛え直すんだ！", 9.3, 1, 0.0, "マコト", "2024/03/31 12:00:00", 3, 0.0, 129801);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (10142, 208, 24, 208, "2030/12/17 14:59:59", "まだまだ課題がある内容だったな～\n無駄な動き、判断の遅れ…\nそういうのをしっかり直していけば、\n記録ももっと伸びるはずだっ", 6.9, 1, 0.0, "マコト", "2024/03/31 12:00:00", 4, 0.0, 129801);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (10142, 209, 24, 209, "2030/12/17 14:59:59", "ここまでいけりゃ、合格点！\nむしろ、最初の頼りなさを思えば\nすげえ成長してるんじゃねえか？\n胸を張れる結果だと思うぜっ！", 0.0, 2, 0.0, "マコト", "2024/03/31 12:00:00", 2, 0.0, 129801);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (10142, 210, 24, 210, "2030/12/17 14:59:59", "なかなかハードな訓練だったけど、\nみんなしっかりついてこられたな\n地道に続けた基礎トレの成果が\n形になってる、その証だぜっ", 5.95, 2, 0.0, "マコト", "2024/03/31 12:00:00", 1, 0.0, 129801);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (10142, 211, 24, 211, "2030/12/17 14:59:59", "今回の訓練もよく頑張ったな！\nしっかり休んで次に備えろよ！\nなんつーか、教え子が成長してくのを\n見るのは楽しいもんだな…", 6.73, 1, 0.0, "マコト", "2024/03/31 12:00:00", 2, 0.0, 129801);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (10142, 212, 24, 212, "2030/12/17 14:59:59", "おまえら、すげえじゃねえか！\nこれならもう、ひよっこ新兵なんて\n言えねえな。きっと騎士団でも\n大活躍間違いなしだぜ！", 0.0, 2, 0.0, "マコト", "2024/03/31 12:00:00", 2, 0.0, 129801);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (10142, 213, 24, 213, "2030/12/17 14:59:59", "敵との戦い方、状況判断の正確さ\n何より根性…どれも最高だったぞ！\nうかうかしてたら、教官のあたしまで\n追い抜かれちまうかもな…へへっ", 6.65, 2, 0.0, "マコト", "2024/03/31 12:00:00", 1, 0.0, 129801);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (10142, 214, 24, 214, "2030/12/17 14:59:59", "あたしの指導が良かった？\nいやいや、ここまで強くなれたのは、\n他でもないおまえたち自身が努力した\nおかげだよ。改めて…おめでとうっ！", 3.05, 1, 0.0, "マコト", "2024/03/31 12:00:00", 6, 0.0, 129801);
