INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (100, 10.85, "2030/12/17 14:59:59", 1, 129901, 10142, 7, "訓練成功、おめでとうございます\n褒めるべき時にはしっかり褒めるのも\n教官の務めですものね。それとも、\n罵られる方が好みですか？　クスクス", 0.0, 0.0, 100, "エリコ", "2024/03/31 12:00:00", 24);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (101, 11.15, "2030/12/17 14:59:59", 1, 129901, 10142, 2, "厳しい訓練も、終わってみれば\n短く感じるものですわね。\n正直、私は暴れ足りないくらい…\nいえ、何でもありませんわ。クスクス", 0.0, 0.0, 101, "エリコ", "2024/03/31 12:00:00", 24);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (102, 6.4, "2030/12/17 14:59:59", 7, 129901, 10142, 5, "行く手を阻む忌々しい邪魔者を\n蹴散らして、見事ゴールイン…！\nまるで私とあのおかたの人生を\n象徴するような道のりでした…", 0.0, 0.0, 102, "エリコ", "2024/03/31 12:00:00", 24);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (103, 0.0, "2030/12/17 14:59:59", 3, 129901, 10142, 3, "まったくもって不甲斐ない結果です\nしかし、挽回の機会はまだありますわ\n次はゴミ虫の地位から脱却できるよう\n死力を尽くしなさい。いいですね？", 0.0, 0.0, 103, "エリコ", "2024/03/31 12:00:00", 24);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (104, 2.43, "2030/12/17 14:59:59", 2, 129901, 10142, 8, "クスクス…ああご心配なさらず\nなんとも情けない成績を残す輩には\nどんな罰が相応しいか、考えている\nだけですので…クスクスクス", 0.0, 0.0, 104, "エリコ", "2024/03/31 12:00:00", 24);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (105, 9.08, "2030/12/17 14:59:59", 4, 129901, 10142, 3, "私が教官として認められなければ、\nあのおかたを失望させてしまう…\nそれだけはあってはなりません\nあなた方も、わかっていますわね？", 0.0, 0.0, 105, "エリコ", "2024/03/31 12:00:00", 24);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (106, 0.0, "2030/12/17 14:59:59", 6, 129901, 10142, 6, "まずまずの結果ではありますが、\nこの程度で満足してもらっては\n困りますわ。エリコ班の一員として、\nより高みを目指していただきますので", 0.0, 0.0, 106, "エリコ", "2024/03/31 12:00:00", 24);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (107, 10.6, "2030/12/17 14:59:59", 3, 129901, 10142, 8, "私の指導を受けながら、この結果は…\nもっと真剣に考えねばなりませんね\nあなた方が怠けずにいるには、\nどんなしつけが必要かを…クスクス", 0.0, 0.0, 107, "エリコ", "2024/03/31 12:00:00", 24);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (108, 7.54, "2030/12/17 14:59:59", 6, 129901, 10142, 4, "ひとまず訓練をこなしたご褒美か、\n実力を出しきれたとは言えない罰か…\nみなさんにどちらを与えるべきか\n少し悩む結果ですわね", 0.0, 0.0, 108, "エリコ", "2024/03/31 12:00:00", 24);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (109, 11.5, "2030/12/17 14:59:59", 2, 129901, 10142, 7, "なかなかいい戦いぶりでしたわ\nこれもあなた方の頑張りと、何より\n優秀な教官である私の教育の賜物\nそうですわね？　クスクスクス…", 0.0, 0.0, 109, "エリコ", "2024/03/31 12:00:00", 24);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (110, 8.83, "2030/12/17 14:59:59", 2, 129901, 10142, 1, "これだけ進めたのならば合格点ですわ\nでも、もっと高みを目指したいと\n思っているのなら…さらなる訓練にも\n付き合って差し上げましょう", 0.0, 0.0, 110, "エリコ", "2024/03/31 12:00:00", 24);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (111, 6.68, "2030/12/17 14:59:59", 1, 129901, 10142, 2, "未熟で頼りなかったあなた方が、\n見違えるように成長しました\nなるほど…これこそが、教官冥利に\n尽きるという気持ちなのですね", 0.0, 0.0, 111, "エリコ", "2024/03/31 12:00:00", 24);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (112, 8.8, "2030/12/17 14:59:59", 6, 129901, 10142, 2, "まさかあなた方が、私の予想すら\n超える力を発揮するとは…驚きました\nもはやゴミ虫などではありません\n一人前の騎士として胸を張りなさい", 0.0, 0.0, 112, "エリコ", "2024/03/31 12:00:00", 24);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (113, 8.9, "2030/12/17 14:59:59", 2, 129901, 10142, 1, "クスクス、見事な働きです\nみなさんならできると信じていました\n他でもない私の指導を受けた、\nエリコ班の優秀な新兵なのですから", 0.0, 0.0, 113, "エリコ", "2024/03/31 12:00:00", 24);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (114, 3.7, "2030/12/17 14:59:59", 2, 129901, 10142, 5, "なんてすばらしい成績…！\n今日という日は『あのおかたを想い\nエリコが限界を超えた記念日』として\n歴史に刻まれることでしょう…！", 0.0, 0.0, 114, "エリコ", "2024/03/31 12:00:00", 24);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (200, 7.6, "2030/12/17 14:59:59", 1, 129801, 10142, 2, "よし、いい訓練になったな\nおまえたちもよく動けてたし、\nちゃんと成長できてると思うぜ\nその調子で頑張れよっ", 0.0, 0.0, 200, "マコト", "2024/03/31 12:00:00", 24);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (201, 2.25, "2030/12/17 14:59:59", 2, 129801, 10142, 1, "ひとまずゴールおめでとう！\nでも、努力は継続してこそだからな\nおまえたちにやる気があるなら、\n何度だって付き合ってやるよ", 0.0, 0.0, 201, "マコト", "2024/03/31 12:00:00", 24);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (202, 3.28, "2030/12/17 14:59:59", 2, 129801, 10142, 1, "行軍訓練はこれにて完了！\n…なーんて思ってないよな？\n学んだことが体に染みつくまで\n地道に繰り返すのが練習ってもんさ", 0.0, 0.0, 202, "マコト", "2024/03/31 12:00:00", 24);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (203, 2.98, "2030/12/17 14:59:59", 4, 129801, 10142, 1, "みんなに無理をさせちまったか…\nでもこれで諦めることはないぜ\n次はきっとうまくいく。そう信じて\n鍛え続ければ、道は開けるはずだ！", 0.0, 0.0, 203, "マコト", "2024/03/31 12:00:00", 24);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (204, 3.0, "2030/12/17 14:59:59", 4, 129801, 10142, 1, "こいつは悔しい結果だな…\nま、反省点はいろいろあったし\nひとつずつ乗り越えていこう\nあたしたちで力を合わせて、なっ！", 0.0, 0.0, 204, "マコト", "2024/03/31 12:00:00", 24);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (205, 10.3, "2030/12/17 14:59:59", 1, 129801, 10142, 2, "おまえたちはまだ未熟な訓練兵、\n失敗を必要以上に悔やむことはねえよ\nでも実戦じゃこうはいかないからな\n次は気合を入れ直していけよっ！", 0.0, 0.0, 205, "マコト", "2024/03/31 12:00:00", 24);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (206, 2.93, "2030/12/17 14:59:59", 6, 129801, 10142, 1, "これがおまえたちの全力？\nいやいや、そんなはずはないよなっ\nおまえらならもっと強くなれる！\nあたしが言うんだ、間違いないぜ！", 0.0, 0.0, 206, "マコト", "2024/03/31 12:00:00", 24);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (207, 9.3, "2030/12/17 14:59:59", 3, 129801, 10142, 1, "おまえたちの実力を考えりゃ、\nベストを尽くせたとは言えない成績だ\nなら、次やるべきことも決まってる\n気持ちを切り替えて、鍛え直すんだ！", 0.0, 0.0, 207, "マコト", "2024/03/31 12:00:00", 24);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (208, 6.9, "2030/12/17 14:59:59", 4, 129801, 10142, 1, "まだまだ課題がある内容だったな～\n無駄な動き、判断の遅れ…\nそういうのをしっかり直していけば、\n記録ももっと伸びるはずだっ", 0.0, 0.0, 208, "マコト", "2024/03/31 12:00:00", 24);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (209, 0.0, "2030/12/17 14:59:59", 2, 129801, 10142, 2, "ここまでいけりゃ、合格点！\nむしろ、最初の頼りなさを思えば\nすげえ成長してるんじゃねえか？\n胸を張れる結果だと思うぜっ！", 0.0, 0.0, 209, "マコト", "2024/03/31 12:00:00", 24);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (210, 5.95, "2030/12/17 14:59:59", 1, 129801, 10142, 2, "なかなかハードな訓練だったけど、\nみんなしっかりついてこられたな\n地道に続けた基礎トレの成果が\n形になってる、その証だぜっ", 0.0, 0.0, 210, "マコト", "2024/03/31 12:00:00", 24);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (211, 6.73, "2030/12/17 14:59:59", 2, 129801, 10142, 1, "今回の訓練もよく頑張ったな！\nしっかり休んで次に備えろよ！\nなんつーか、教え子が成長してくのを\n見るのは楽しいもんだな…", 0.0, 0.0, 211, "マコト", "2024/03/31 12:00:00", 24);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (212, 0.0, "2030/12/17 14:59:59", 2, 129801, 10142, 2, "おまえら、すげえじゃねえか！\nこれならもう、ひよっこ新兵なんて\n言えねえな。きっと騎士団でも\n大活躍間違いなしだぜ！", 0.0, 0.0, 212, "マコト", "2024/03/31 12:00:00", 24);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (213, 6.65, "2030/12/17 14:59:59", 1, 129801, 10142, 2, "敵との戦い方、状況判断の正確さ\n何より根性…どれも最高だったぞ！\nうかうかしてたら、教官のあたしまで\n追い抜かれちまうかもな…へへっ", 0.0, 0.0, 213, "マコト", "2024/03/31 12:00:00", 24);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (214, 3.05, "2030/12/17 14:59:59", 6, 129801, 10142, 1, "あたしの指導が良かった？\nいやいや、ここまで強くなれたのは、\n他でもないおまえたち自身が努力した\nおかげだよ。改めて…おめでとうっ！", 0.0, 0.0, 214, "マコト", "2024/03/31 12:00:00", 24);
