INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (0.0, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 0.0, 7, 100, 24, 1, "エリコ", 100, "訓練成功、おめでとうございます\n褒めるべき時にはしっかり褒めるのも\n教官の務めですものね。それとも、\n罵られる方が好みですか？　クスクス", 129901, 10142, 10.85);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (0.0, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 0.0, 2, 101, 24, 1, "エリコ", 101, "厳しい訓練も、終わってみれば\n短く感じるものですわね。\n正直、私は暴れ足りないくらい…\nいえ、何でもありませんわ。クスクス", 129901, 10142, 11.15);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (0.0, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 0.0, 5, 102, 24, 7, "エリコ", 102, "行く手を阻む忌々しい邪魔者を\n蹴散らして、見事ゴールイン…！\nまるで私とあのおかたの人生を\n象徴するような道のりでした…", 129901, 10142, 6.4);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (0.0, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 0.0, 3, 103, 24, 3, "エリコ", 103, "まったくもって不甲斐ない結果です\nしかし、挽回の機会はまだありますわ\n次はゴミ虫の地位から脱却できるよう\n死力を尽くしなさい。いいですね？", 129901, 10142, 0.0);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (0.0, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 0.0, 8, 104, 24, 2, "エリコ", 104, "クスクス…ああご心配なさらず\nなんとも情けない成績を残す輩には\nどんな罰が相応しいか、考えている\nだけですので…クスクスクス", 129901, 10142, 2.43);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (0.0, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 0.0, 3, 105, 24, 4, "エリコ", 105, "私が教官として認められなければ、\nあのおかたを失望させてしまう…\nそれだけはあってはなりません\nあなた方も、わかっていますわね？", 129901, 10142, 9.08);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (0.0, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 0.0, 6, 106, 24, 6, "エリコ", 106, "まずまずの結果ではありますが、\nこの程度で満足してもらっては\n困りますわ。エリコ班の一員として、\nより高みを目指していただきますので", 129901, 10142, 0.0);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (0.0, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 0.0, 8, 107, 24, 3, "エリコ", 107, "私の指導を受けながら、この結果は…\nもっと真剣に考えねばなりませんね\nあなた方が怠けずにいるには、\nどんなしつけが必要かを…クスクス", 129901, 10142, 10.6);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (0.0, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 0.0, 4, 108, 24, 6, "エリコ", 108, "ひとまず訓練をこなしたご褒美か、\n実力を出しきれたとは言えない罰か…\nみなさんにどちらを与えるべきか\n少し悩む結果ですわね", 129901, 10142, 7.54);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (0.0, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 0.0, 7, 109, 24, 2, "エリコ", 109, "なかなかいい戦いぶりでしたわ\nこれもあなた方の頑張りと、何より\n優秀な教官である私の教育の賜物\nそうですわね？　クスクスクス…", 129901, 10142, 11.5);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (0.0, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 0.0, 1, 110, 24, 2, "エリコ", 110, "これだけ進めたのならば合格点ですわ\nでも、もっと高みを目指したいと\n思っているのなら…さらなる訓練にも\n付き合って差し上げましょう", 129901, 10142, 8.83);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (0.0, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 0.0, 2, 111, 24, 1, "エリコ", 111, "未熟で頼りなかったあなた方が、\n見違えるように成長しました\nなるほど…これこそが、教官冥利に\n尽きるという気持ちなのですね", 129901, 10142, 6.68);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (0.0, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 0.0, 2, 112, 24, 6, "エリコ", 112, "まさかあなた方が、私の予想すら\n超える力を発揮するとは…驚きました\nもはやゴミ虫などではありません\n一人前の騎士として胸を張りなさい", 129901, 10142, 8.8);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (0.0, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 0.0, 1, 113, 24, 2, "エリコ", 113, "クスクス、見事な働きです\nみなさんならできると信じていました\n他でもない私の指導を受けた、\nエリコ班の優秀な新兵なのですから", 129901, 10142, 8.9);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (0.0, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 0.0, 5, 114, 24, 2, "エリコ", 114, "なんてすばらしい成績…！\n今日という日は『あのおかたを想い\nエリコが限界を超えた記念日』として\n歴史に刻まれることでしょう…！", 129901, 10142, 3.7);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (0.0, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 0.0, 2, 200, 24, 1, "マコト", 200, "よし、いい訓練になったな\nおまえたちもよく動けてたし、\nちゃんと成長できてると思うぜ\nその調子で頑張れよっ", 129801, 10142, 7.6);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (0.0, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 0.0, 1, 201, 24, 2, "マコト", 201, "ひとまずゴールおめでとう！\nでも、努力は継続してこそだからな\nおまえたちにやる気があるなら、\n何度だって付き合ってやるよ", 129801, 10142, 2.25);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (0.0, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 0.0, 1, 202, 24, 2, "マコト", 202, "行軍訓練はこれにて完了！\n…なーんて思ってないよな？\n学んだことが体に染みつくまで\n地道に繰り返すのが練習ってもんさ", 129801, 10142, 3.28);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (0.0, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 0.0, 1, 203, 24, 4, "マコト", 203, "みんなに無理をさせちまったか…\nでもこれで諦めることはないぜ\n次はきっとうまくいく。そう信じて\n鍛え続ければ、道は開けるはずだ！", 129801, 10142, 2.98);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (0.0, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 0.0, 1, 204, 24, 4, "マコト", 204, "こいつは悔しい結果だな…\nま、反省点はいろいろあったし\nひとつずつ乗り越えていこう\nあたしたちで力を合わせて、なっ！", 129801, 10142, 3.0);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (0.0, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 0.0, 2, 205, 24, 1, "マコト", 205, "おまえたちはまだ未熟な訓練兵、\n失敗を必要以上に悔やむことはねえよ\nでも実戦じゃこうはいかないからな\n次は気合を入れ直していけよっ！", 129801, 10142, 10.3);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (0.0, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 0.0, 1, 206, 24, 6, "マコト", 206, "これがおまえたちの全力？\nいやいや、そんなはずはないよなっ\nおまえらならもっと強くなれる！\nあたしが言うんだ、間違いないぜ！", 129801, 10142, 2.93);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (0.0, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 0.0, 1, 207, 24, 3, "マコト", 207, "おまえたちの実力を考えりゃ、\nベストを尽くせたとは言えない成績だ\nなら、次やるべきことも決まってる\n気持ちを切り替えて、鍛え直すんだ！", 129801, 10142, 9.3);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (0.0, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 0.0, 1, 208, 24, 4, "マコト", 208, "まだまだ課題がある内容だったな～\n無駄な動き、判断の遅れ…\nそういうのをしっかり直していけば、\n記録ももっと伸びるはずだっ", 129801, 10142, 6.9);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (0.0, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 0.0, 2, 209, 24, 2, "マコト", 209, "ここまでいけりゃ、合格点！\nむしろ、最初の頼りなさを思えば\nすげえ成長してるんじゃねえか？\n胸を張れる結果だと思うぜっ！", 129801, 10142, 0.0);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (0.0, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 0.0, 2, 210, 24, 1, "マコト", 210, "なかなかハードな訓練だったけど、\nみんなしっかりついてこられたな\n地道に続けた基礎トレの成果が\n形になってる、その証だぜっ", 129801, 10142, 5.95);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (0.0, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 0.0, 1, 211, 24, 2, "マコト", 211, "今回の訓練もよく頑張ったな！\nしっかり休んで次に備えろよ！\nなんつーか、教え子が成長してくのを\n見るのは楽しいもんだな…", 129801, 10142, 6.73);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (0.0, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 0.0, 2, 212, 24, 2, "マコト", 212, "おまえら、すげえじゃねえか！\nこれならもう、ひよっこ新兵なんて\n言えねえな。きっと騎士団でも\n大活躍間違いなしだぜ！", 129801, 10142, 0.0);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (0.0, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 0.0, 2, 213, 24, 1, "マコト", 213, "敵との戦い方、状況判断の正確さ\n何より根性…どれも最高だったぞ！\nうかうかしてたら、教官のあたしまで\n追い抜かれちまうかもな…へへっ", 129801, 10142, 6.65);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (0.0, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 0.0, 1, 214, 24, 6, "マコト", 214, "あたしの指導が良かった？\nいやいや、ここまで強くなれたのは、\n他でもないおまえたち自身が努力した\nおかげだよ。改めて…おめでとうっ！", 129801, 10142, 3.05);
