INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (1, 7, 10142, 100, 24, "2030/12/17 14:59:59", 10.85, 100, "訓練成功、おめでとうございます\n褒めるべき時にはしっかり褒めるのも\n教官の務めですものね。それとも、\n罵られる方が好みですか？　クスクス", 0.0, "2024/03/31 12:00:00", "エリコ", 0.0, 129901);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (1, 2, 10142, 101, 24, "2030/12/17 14:59:59", 11.15, 101, "厳しい訓練も、終わってみれば\n短く感じるものですわね。\n正直、私は暴れ足りないくらい…\nいえ、何でもありませんわ。クスクス", 0.0, "2024/03/31 12:00:00", "エリコ", 0.0, 129901);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (7, 5, 10142, 102, 24, "2030/12/17 14:59:59", 6.4, 102, "行く手を阻む忌々しい邪魔者を\n蹴散らして、見事ゴールイン…！\nまるで私とあのおかたの人生を\n象徴するような道のりでした…", 0.0, "2024/03/31 12:00:00", "エリコ", 0.0, 129901);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (3, 3, 10142, 103, 24, "2030/12/17 14:59:59", 0.0, 103, "まったくもって不甲斐ない結果です\nしかし、挽回の機会はまだありますわ\n次はゴミ虫の地位から脱却できるよう\n死力を尽くしなさい。いいですね？", 0.0, "2024/03/31 12:00:00", "エリコ", 0.0, 129901);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (2, 8, 10142, 104, 24, "2030/12/17 14:59:59", 2.43, 104, "クスクス…ああご心配なさらず\nなんとも情けない成績を残す輩には\nどんな罰が相応しいか、考えている\nだけですので…クスクスクス", 0.0, "2024/03/31 12:00:00", "エリコ", 0.0, 129901);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (4, 3, 10142, 105, 24, "2030/12/17 14:59:59", 9.08, 105, "私が教官として認められなければ、\nあのおかたを失望させてしまう…\nそれだけはあってはなりません\nあなた方も、わかっていますわね？", 0.0, "2024/03/31 12:00:00", "エリコ", 0.0, 129901);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (6, 6, 10142, 106, 24, "2030/12/17 14:59:59", 0.0, 106, "まずまずの結果ではありますが、\nこの程度で満足してもらっては\n困りますわ。エリコ班の一員として、\nより高みを目指していただきますので", 0.0, "2024/03/31 12:00:00", "エリコ", 0.0, 129901);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (3, 8, 10142, 107, 24, "2030/12/17 14:59:59", 10.6, 107, "私の指導を受けながら、この結果は…\nもっと真剣に考えねばなりませんね\nあなた方が怠けずにいるには、\nどんなしつけが必要かを…クスクス", 0.0, "2024/03/31 12:00:00", "エリコ", 0.0, 129901);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (6, 4, 10142, 108, 24, "2030/12/17 14:59:59", 7.54, 108, "ひとまず訓練をこなしたご褒美か、\n実力を出しきれたとは言えない罰か…\nみなさんにどちらを与えるべきか\n少し悩む結果ですわね", 0.0, "2024/03/31 12:00:00", "エリコ", 0.0, 129901);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (2, 7, 10142, 109, 24, "2030/12/17 14:59:59", 11.5, 109, "なかなかいい戦いぶりでしたわ\nこれもあなた方の頑張りと、何より\n優秀な教官である私の教育の賜物\nそうですわね？　クスクスクス…", 0.0, "2024/03/31 12:00:00", "エリコ", 0.0, 129901);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (2, 1, 10142, 110, 24, "2030/12/17 14:59:59", 8.83, 110, "これだけ進めたのならば合格点ですわ\nでも、もっと高みを目指したいと\n思っているのなら…さらなる訓練にも\n付き合って差し上げましょう", 0.0, "2024/03/31 12:00:00", "エリコ", 0.0, 129901);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (1, 2, 10142, 111, 24, "2030/12/17 14:59:59", 6.68, 111, "未熟で頼りなかったあなた方が、\n見違えるように成長しました\nなるほど…これこそが、教官冥利に\n尽きるという気持ちなのですね", 0.0, "2024/03/31 12:00:00", "エリコ", 0.0, 129901);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (6, 2, 10142, 112, 24, "2030/12/17 14:59:59", 8.8, 112, "まさかあなた方が、私の予想すら\n超える力を発揮するとは…驚きました\nもはやゴミ虫などではありません\n一人前の騎士として胸を張りなさい", 0.0, "2024/03/31 12:00:00", "エリコ", 0.0, 129901);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (2, 1, 10142, 113, 24, "2030/12/17 14:59:59", 8.9, 113, "クスクス、見事な働きです\nみなさんならできると信じていました\n他でもない私の指導を受けた、\nエリコ班の優秀な新兵なのですから", 0.0, "2024/03/31 12:00:00", "エリコ", 0.0, 129901);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (2, 5, 10142, 114, 24, "2030/12/17 14:59:59", 3.7, 114, "なんてすばらしい成績…！\n今日という日は『あのおかたを想い\nエリコが限界を超えた記念日』として\n歴史に刻まれることでしょう…！", 0.0, "2024/03/31 12:00:00", "エリコ", 0.0, 129901);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (1, 2, 10142, 200, 24, "2030/12/17 14:59:59", 7.6, 200, "よし、いい訓練になったな\nおまえたちもよく動けてたし、\nちゃんと成長できてると思うぜ\nその調子で頑張れよっ", 0.0, "2024/03/31 12:00:00", "マコト", 0.0, 129801);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (2, 1, 10142, 201, 24, "2030/12/17 14:59:59", 2.25, 201, "ひとまずゴールおめでとう！\nでも、努力は継続してこそだからな\nおまえたちにやる気があるなら、\n何度だって付き合ってやるよ", 0.0, "2024/03/31 12:00:00", "マコト", 0.0, 129801);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (2, 1, 10142, 202, 24, "2030/12/17 14:59:59", 3.28, 202, "行軍訓練はこれにて完了！\n…なーんて思ってないよな？\n学んだことが体に染みつくまで\n地道に繰り返すのが練習ってもんさ", 0.0, "2024/03/31 12:00:00", "マコト", 0.0, 129801);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (4, 1, 10142, 203, 24, "2030/12/17 14:59:59", 2.98, 203, "みんなに無理をさせちまったか…\nでもこれで諦めることはないぜ\n次はきっとうまくいく。そう信じて\n鍛え続ければ、道は開けるはずだ！", 0.0, "2024/03/31 12:00:00", "マコト", 0.0, 129801);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (4, 1, 10142, 204, 24, "2030/12/17 14:59:59", 3.0, 204, "こいつは悔しい結果だな…\nま、反省点はいろいろあったし\nひとつずつ乗り越えていこう\nあたしたちで力を合わせて、なっ！", 0.0, "2024/03/31 12:00:00", "マコト", 0.0, 129801);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (1, 2, 10142, 205, 24, "2030/12/17 14:59:59", 10.3, 205, "おまえたちはまだ未熟な訓練兵、\n失敗を必要以上に悔やむことはねえよ\nでも実戦じゃこうはいかないからな\n次は気合を入れ直していけよっ！", 0.0, "2024/03/31 12:00:00", "マコト", 0.0, 129801);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (6, 1, 10142, 206, 24, "2030/12/17 14:59:59", 2.93, 206, "これがおまえたちの全力？\nいやいや、そんなはずはないよなっ\nおまえらならもっと強くなれる！\nあたしが言うんだ、間違いないぜ！", 0.0, "2024/03/31 12:00:00", "マコト", 0.0, 129801);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (3, 1, 10142, 207, 24, "2030/12/17 14:59:59", 9.3, 207, "おまえたちの実力を考えりゃ、\nベストを尽くせたとは言えない成績だ\nなら、次やるべきことも決まってる\n気持ちを切り替えて、鍛え直すんだ！", 0.0, "2024/03/31 12:00:00", "マコト", 0.0, 129801);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (4, 1, 10142, 208, 24, "2030/12/17 14:59:59", 6.9, 208, "まだまだ課題がある内容だったな～\n無駄な動き、判断の遅れ…\nそういうのをしっかり直していけば、\n記録ももっと伸びるはずだっ", 0.0, "2024/03/31 12:00:00", "マコト", 0.0, 129801);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (2, 2, 10142, 209, 24, "2030/12/17 14:59:59", 0.0, 209, "ここまでいけりゃ、合格点！\nむしろ、最初の頼りなさを思えば\nすげえ成長してるんじゃねえか？\n胸を張れる結果だと思うぜっ！", 0.0, "2024/03/31 12:00:00", "マコト", 0.0, 129801);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (1, 2, 10142, 210, 24, "2030/12/17 14:59:59", 5.95, 210, "なかなかハードな訓練だったけど、\nみんなしっかりついてこられたな\n地道に続けた基礎トレの成果が\n形になってる、その証だぜっ", 0.0, "2024/03/31 12:00:00", "マコト", 0.0, 129801);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (2, 1, 10142, 211, 24, "2030/12/17 14:59:59", 6.73, 211, "今回の訓練もよく頑張ったな！\nしっかり休んで次に備えろよ！\nなんつーか、教え子が成長してくのを\n見るのは楽しいもんだな…", 0.0, "2024/03/31 12:00:00", "マコト", 0.0, 129801);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (2, 2, 10142, 212, 24, "2030/12/17 14:59:59", 0.0, 212, "おまえら、すげえじゃねえか！\nこれならもう、ひよっこ新兵なんて\n言えねえな。きっと騎士団でも\n大活躍間違いなしだぜ！", 0.0, "2024/03/31 12:00:00", "マコト", 0.0, 129801);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (1, 2, 10142, 213, 24, "2030/12/17 14:59:59", 6.65, 213, "敵との戦い方、状況判断の正確さ\n何より根性…どれも最高だったぞ！\nうかうかしてたら、教官のあたしまで\n追い抜かれちまうかもな…へへっ", 0.0, "2024/03/31 12:00:00", "マコト", 0.0, 129801);
INSERT INTO `v1_85e060c814bafa4536f9975f2aba1b2611adc4bd19451eaed8b9ba03450e831f` VALUES (6, 1, 10142, 214, 24, "2030/12/17 14:59:59", 3.05, 214, "あたしの指導が良かった？\nいやいや、ここまで強くなれたのは、\n他でもないおまえたち自身が努力した\nおかげだよ。改めて…おめでとうっ！", 0.0, "2024/03/31 12:00:00", "マコト", 0.0, 129801);
