INSERT INTO `v1_8a124878c0167062dc99b186929b5b6a1de96180f8d24a0652a40103ce5d5a76` VALUES (14004, 106001, "2030/12/17 14:59:59", -363.0, 0, 0.0, 1, "キャル", 0, "2015/12/17 15:00:00", 14, 0.0, 1, 1, "クエストを攻略すると、\nアイテムが手に入るんだって\nもらえるものは、\nもらっておきましょ！");
