INSERT INTO `v1_8a124878c0167062dc99b186929b5b6a1de96180f8d24a0652a40103ce5d5a76` VALUES (24002, 109001, "2030/12/17 14:59:59", -385.0, 10011, 5.0, 2, "エリコ（バレンタイン）", 0, "2019/01/31 12:00:00", 24, 8.0, 2, 1, "このような失敗、あなた様が\nするはずありません\nもう一度挑戦してみましょう");
INSERT INTO `v1_8a124878c0167062dc99b186929b5b6a1de96180f8d24a0652a40103ce5d5a76` VALUES (24003, 109001, "2030/12/17 14:59:59", -385.0, 10011, 10.11, 2, "エリコ（バレンタイン）", 0, "2019/01/31 12:00:00", 24, 8.0, 3, 1, "あなた様のために\n作っているケーキを\nあなた様に手伝っていただく……\nことになるなんて……クスクス");
INSERT INTO `v1_8a124878c0167062dc99b186929b5b6a1de96180f8d24a0652a40103ce5d5a76` VALUES (24004, 109001, "2030/12/17 14:59:59", -385.0, 10011, 4.0, 2, "エリコ（バレンタイン）", 0, "2019/01/31 12:00:00", 24, 8.0, 4, 1, "あまり気に病む必要はありませんわ\nこれからは私がたっぷりと\n時間をかけて教えて差し上げますから");
INSERT INTO `v1_8a124878c0167062dc99b186929b5b6a1de96180f8d24a0652a40103ce5d5a76` VALUES (24005, 109001, "2030/12/17 14:59:59", -385.0, 10011, 6.0, 2, "エリコ（バレンタイン）", 0, "2019/01/31 12:00:00", 24, 8.0, 5, 1, "今回は調子が悪かった……のですか？\nあなた様が手伝ってくれたものなら\nわたしはどんなものでも……クスクス");
INSERT INTO `v1_8a124878c0167062dc99b186929b5b6a1de96180f8d24a0652a40103ce5d5a76` VALUES (24006, 109002, "2030/12/17 14:59:59", 0.0, 10011, 0.0, 1, "エリコ（バレンタイン）", 0, "2019/01/31 12:00:00", 24, 0.0, 6, 1, "あなた様が手がけた\nチョコレートケーキは\n私にとっての宝物……\n侮辱する者は生かしておきませんわ");
INSERT INTO `v1_8a124878c0167062dc99b186929b5b6a1de96180f8d24a0652a40103ce5d5a76` VALUES (24007, 109002, "2030/12/17 14:59:59", 0.0, 10011, 0.0, 1, "エリコ（バレンタイン）", 0, "2019/01/31 12:00:00", 24, 0.0, 7, 1, "とても素晴らしい出来栄えですわ！\nあなた様にふさわしい\nチョコレートケーキができました");
INSERT INTO `v1_8a124878c0167062dc99b186929b5b6a1de96180f8d24a0652a40103ce5d5a76` VALUES (24008, 109002, "2030/12/17 14:59:59", 0.0, 10011, 0.0, 1, "エリコ（バレンタイン）", 0, "2019/01/31 12:00:00", 24, 0.0, 8, 1, "私を手伝ってくれたあなた様は\nすでに私の虜……\n甘い世界に誘って差し上げますわ");
