INSERT INTO `v1_8b5eb77a33173a1c9345a8726ab916378c50845e72b7e60c8ec4ce06392285dc` VALUES (1.5, 1, 0.029, 1, 160, 110, 101, 0, 0, 1, 122601, 1, "仲間と夏を楽しむ、夏海の魔法士。自分を変えたいという固い決意で、障害物を無効化して走り続ける。", 105);
INSERT INTO `v1_8b5eb77a33173a1c9345a8726ab916378c50845e72b7e60c8ec4ce06392285dc` VALUES (2.5, 2, 0.014, 0, 120, 105, 102, 1, 0, 2, 122401, 3, "太陽よりも輝く、夏海の元気娘。　持ち前の身体能力で、どんなコースも柔軟に対応し、駆け抜ける。", 111);
INSERT INTO `v1_8b5eb77a33173a1c9345a8726ab916378c50845e72b7e60c8ec4ce06392285dc` VALUES (2.5, 3, 0.017, 0, 350, 110, 103, 0, 1, 3, 122501, 2, "波打ち際に煌めく、夏海の剣士。　日々の鍛錬で鍛えた脚で、長い距離も速度を落とさず走り切る。", 105);
INSERT INTO `v1_8b5eb77a33173a1c9345a8726ab916378c50845e72b7e60c8ec4ce06392285dc` VALUES (1.5, 4, 0.016, 0, 130, 110, 104, 1, 0, 4, 107501, 3, "海の幸に目がない、渚のお姫様。　『王家の装備』の力を解放し、障害物をものともせず前へと進む。", 105);
INSERT INTO `v1_8b5eb77a33173a1c9345a8726ab916378c50845e72b7e60c8ec4ce06392285dc` VALUES (2.5, 5, 0.014, 1, 160, 110, 105, 0, 0, 5, 107601, 1, "海でも主さまをお世話する、白浜の巫女。精霊の力で自身を加速させ、誰よりも速くゴールを目指す。", 105);
INSERT INTO `v1_8b5eb77a33173a1c9345a8726ab916378c50845e72b7e60c8ec4ce06392285dc` VALUES (3.5, 6, 0.029, 0, 255, 120, 106, 0, 1, 6, 107801, 2, "水着姿を恥じらう、ツンデレ猫娘。お得意の魔法で、対戦相手の走りを妨害し、レースを有利に進める。", 103);
INSERT INTO `v1_8b5eb77a33173a1c9345a8726ab916378c50845e72b7e60c8ec4ce06392285dc` VALUES (3.5, 7, 0.013, 1, 160, 120, 107, 0, 0, 7, 110601, 1, "優雅に浜を駆ける、真夏のマホ姫。しゃなりしゃなりと、雅な身のこなしで障害物を跳び越えていく。", 103);
INSERT INTO `v1_8b5eb77a33173a1c9345a8726ab916378c50845e72b7e60c8ec4ce06392285dc` VALUES (2.5, 8, 0.014, 0, 350, 110, 108, 0, 1, 8, 110401, 2, "浜の平和を守る、真夏の狼娘。　　獣人の強靭なスタミナで、レース　後半にスパートをかける。", 103);
INSERT INTO `v1_8b5eb77a33173a1c9345a8726ab916378c50845e72b7e60c8ec4ce06392285dc` VALUES (1.5, 9, 0.012, 0, 130, 105, 109, 1, 0, 9, 110501, 3, "浜に力強く舞う、真夏の南国娘。　レースの喧噪の中でも精神を乱さず後半に大きな捲りを見せる。", 100);
INSERT INTO `v1_8b5eb77a33173a1c9345a8726ab916378c50845e72b7e60c8ec4ce06392285dc` VALUES (2.5, 10, 0.014, 1, 200, 105, 110, 0, 0, 10, 115701, 1, "浜の事件を嗅ぎ付けた、真夏の　　名探偵。高い推理力と魔法で、的確に対戦相手の走りを乱す。", 103);
INSERT INTO `v1_8b5eb77a33173a1c9345a8726ab916378c50845e72b7e60c8ec4ce06392285dc` VALUES (2.5, 11, 0.071, 0, 350, 105, 111, 0, 1, 11, 113601, 2, "再び鎧を脱いだ、灼熱の騎士団長。実戦で培った判断力で、瞬時にバリアを展開し、障害物を無力化する。", 103);
INSERT INTO `v1_8b5eb77a33173a1c9345a8726ab916378c50845e72b7e60c8ec4ce06392285dc` VALUES (1.5, 12, 0.014, 0, 130, 120, 112, 1, 0, 12, 107901, 3, "水が苦手な、海辺の気まぐれ猫娘。浜に現れた美少女義賊は、対戦相手のスピードを奪いとる。", 103);
INSERT INTO `v1_8b5eb77a33173a1c9345a8726ab916378c50845e72b7e60c8ec4ce06392285dc` VALUES (1.5, 13, 0.022, 1, 200, 110, 113, 0, 0, 13, 108001, 2, "バカンスも効率第一な、海の傭兵。レースでも最高効率を求め、どんな状況でも隙のない走りを見せる。", 105);
INSERT INTO `v1_8b5eb77a33173a1c9345a8726ab916378c50845e72b7e60c8ec4ce06392285dc` VALUES (2.5, 14, 0.031, 1, 160, 110, 114, 0, 0, 14, 110301, 1, "真夏の太陽が良く似合う、海辺の　サレンママ。多才で器用、どんな　障害物もエレガントに越えていく。", 108);
INSERT INTO `v1_8b5eb77a33173a1c9345a8726ab916378c50845e72b7e60c8ec4ce06392285dc` VALUES (0.5, 15, 0.036, 0, 130, 110, 115, 1, 0, 15, 107701, 1, "海に嵐を呼ぶ夏のドジっ娘メイド。日頃から災難に慣れており、多少　のトラブルには動じたりしない。", 105);
INSERT INTO `v1_8b5eb77a33173a1c9345a8726ab916378c50845e72b7e60c8ec4ce06392285dc` VALUES (3.5, 16, 0.048, 0, 350, 110, 116, 0, 1, 16, 113101, 2, "粋な夏を愉しむ、若夏の姉御。　　流麗な身体捌きで、始・継・終と、UBを重ねるごとに、加速する。", 95);
INSERT INTO `v1_8b5eb77a33173a1c9345a8726ab916378c50845e72b7e60c8ec4ce06392285dc` VALUES (1.5, 17, 0.043, 0, 130, 105, 117, 1, 0, 17, 113301, 3, "夏のトキメキに触れた、オタク魔法少女。多彩な魔法を操り、少しずつ他の走者の速度を落とし続ける。", 105);
INSERT INTO `v1_8b5eb77a33173a1c9345a8726ab916378c50845e72b7e60c8ec4ce06392285dc` VALUES (1.5, 18, 0.018, 0, 130, 105, 118, 1, 0, 18, 113201, 2, "海獄に降臨せし、真夏の冥姫。　　レース後半に魔力を全解放し、　　蒼き疾風と成りて浜を駆け抜ける。", 95);
INSERT INTO `v1_8b5eb77a33173a1c9345a8726ab916378c50845e72b7e60c8ec4ce06392285dc` VALUES (2.5, 19, 0.021, 1, 160, 110, 119, 0, 0, 19, 117001, 2, "夏の夕を火花で彩る、渚の壊し屋。『壊し屋』の異名と過激な愛情は、対戦相手にプレッシャーを与える。", 108);
INSERT INTO `v1_8b5eb77a33173a1c9345a8726ab916378c50845e72b7e60c8ec4ce06392285dc` VALUES (2.5, 20, 0.036, 0, 255, 110, 120, 0, 1, 20, 113501, 1, "青春に生きる者を導く、夏の先生。夢へと邁進する聖母の加護が、その身をあらゆる困難から守る。", 105);
INSERT INTO `v1_8b5eb77a33173a1c9345a8726ab916378c50845e72b7e60c8ec4ce06392285dc` VALUES (2.5, 21, 0.02, 0, 350, 105, 121, 0, 1, 21, 113401, 3, "笑顔の応援が眩しい、夏の超能力　ガール。超能力を駆使して、どんな障害物もひとっ飛びで乗り越える。", 105);
INSERT INTO `v1_8b5eb77a33173a1c9345a8726ab916378c50845e72b7e60c8ec4ce06392285dc` VALUES (2.5, 22, 0.018, 0, 130, 110, 122, 1, 0, 22, 110101, 1, "海辺で大人の魅力を放つ、夏の　　セクシー先生。その魅力に囚われた対戦相手は、走りを乱してしまう。", 105);
INSERT INTO `v1_8b5eb77a33173a1c9345a8726ab916378c50845e72b7e60c8ec4ce06392285dc` VALUES (3.5, 23, 0.03, 1, 160, 105, 123, 0, 0, 23, 110001, 3, "水着も絶対外さない、夏のカリスマモデル。走りのバイブスは超テンアゲで、スピードはおにやば。", 111);
INSERT INTO `v1_8b5eb77a33173a1c9345a8726ab916378c50845e72b7e60c8ec4ce06392285dc` VALUES (1.5, 24, 0.027, 0, 130, 110, 124, 1, 0, 24, 117201, 1, "南の島も盛り上げる、真夏のアイドル。悪天候でも笑顔を絶やさず後半もリズムを上げて走り切る。", 103);
INSERT INTO `v1_8b5eb77a33173a1c9345a8726ab916378c50845e72b7e60c8ec4ce06392285dc` VALUES (3.5, 25, 0.02, 1, 160, 105, 125, 0, 0, 25, 117301, 2, "海原に美声を響かす、渚の歌姫。　守護の賛歌と精霊の加護により、　障害物から身を守る。", 108);
INSERT INTO `v1_8b5eb77a33173a1c9345a8726ab916378c50845e72b7e60c8ec4ce06392285dc` VALUES (2.5, 26, 0.04, 0, 350, 110, 126, 0, 1, 26, 117401, 3, "夏でも天邪鬼な、夕凪のテーラー。張り巡らされた魔法の糸で、他の走者の自由を奪う。", 105);
INSERT INTO `v1_8b5eb77a33173a1c9345a8726ab916378c50845e72b7e60c8ec4ce06392285dc` VALUES (3.5, 27, 0.009, 0, 350, 110, 127, 0, 1, 27, 117101, 1, "向日葵が良く似合う、海辺の　　　お姉ちゃん。秘密結社の一員で、　抜群の身体能力が一番の武器。", 105);
INSERT INTO `v1_8b5eb77a33173a1c9345a8726ab916378c50845e72b7e60c8ec4ce06392285dc` VALUES (3.5, 28, 0.022, 0, 255, 120, 128, 0, 1, 28, 122901, 1, "渚でおっとり、夏色うさぎっこ。　うさぎさんパワー全開で、ぴょん　ぴょんとコースを跳ね回る。", 105);
INSERT INTO `v1_8b5eb77a33173a1c9345a8726ab916378c50845e72b7e60c8ec4ce06392285dc` VALUES (2.5, 29, 0.028, 0, 130, 110, 129, 1, 0, 29, 122801, 3, "無邪気さ弾ける、夏色いたずら娘。砂浜でも、いたずらの準備は万端！レースに大波乱が巻き起こる！？", 105);
INSERT INTO `v1_8b5eb77a33173a1c9345a8726ab916378c50845e72b7e60c8ec4ce06392285dc` VALUES (3.5, 30, 0.023, 1, 130, 105, 130, 0, 0, 30, 122701, 2, "海でも真面目な、夏色優等生。　　高めた魔力を解き放ち、一瞬の内に他の走者を置き去りにする。", 108);
