INSERT INTO `v1_8b97fcffbb77f09a224b89af094bbbce27a589b60fe8c3f06896c4c2dadda501` VALUES (0.0, 1, "コッコロ", 0, 13, 0, 13002, 1, "2030/12/17 14:59:59", 0.0, -363.0, "2015/12/17 15:00:00", "主さまとの楽しい日々…\nこちらで何度でも\n思い返しましょう♪", 1, 105901);
INSERT INTO `v1_8b97fcffbb77f09a224b89af094bbbce27a589b60fe8c3f06896c4c2dadda501` VALUES (0.0, 1, "シェフィ", 0, 13, 2104010, 13003, 2, "2030/12/17 14:59:59", 0.0, -363.0, "2015/12/17 15:00:00", "みんなとの大切な思い出が\nたくさん詰まってるのね\n一緒に振り返って、\n心に刻みたいわ", 1, 106401);
INSERT INTO `v1_8b97fcffbb77f09a224b89af094bbbce27a589b60fe8c3f06896c4c2dadda501` VALUES (0.0, 1, "ペコリーヌ", 0, 13, 0, 13004, 1, "2030/12/17 14:59:59", 0.0, -363.0, "2015/12/17 15:00:00", "女の子との思い出が\nたくさん見られますよ！\nヒューヒュー♪\nモテモテですねー☆", 1, 105801);
INSERT INTO `v1_8b97fcffbb77f09a224b89af094bbbce27a589b60fe8c3f06896c4c2dadda501` VALUES (0.0, 1, "コッコロ", 0, 14, 0, 14001, 1, "2030/12/17 14:59:59", 0.0, -363.0, "2015/12/17 15:00:00", "育成に必要なアイテムが\n獲得できるクエストです\nわたくし、主さまのために、\n強くなりますよ", 1, 105901);
INSERT INTO `v1_8b97fcffbb77f09a224b89af094bbbce27a589b60fe8c3f06896c4c2dadda501` VALUES (0.0, 1, "シェフィ", 0, 14, 2104010, 14002, 2, "2030/12/17 14:59:59", 0.0, -363.0, "2015/12/17 15:00:00", "このクエストを攻略すると、\nアイテムが手に入るらしいわ。\nもっと強くなるために、\n一生懸命がんばるわね！", 1, 106401);
INSERT INTO `v1_8b97fcffbb77f09a224b89af094bbbce27a589b60fe8c3f06896c4c2dadda501` VALUES (0.0, 1, "ペコリーヌ", 0, 14, 0, 14003, 1, "2030/12/17 14:59:59", 0.0, -363.0, "2015/12/17 15:00:00", "このクエストを攻略すれば、\nアイテムがもらえますよ\nたくさん集めて、どんどん\n強くなっちゃいましょう！", 1, 105801);
INSERT INTO `v1_8b97fcffbb77f09a224b89af094bbbce27a589b60fe8c3f06896c4c2dadda501` VALUES (0.0, 1, "キャル", 0, 14, 0, 14004, 1, "2030/12/17 14:59:59", 0.0, -363.0, "2015/12/17 15:00:00", "クエストを攻略すると、\nアイテムが手に入るんだって\nもらえるものは、\nもらっておきましょ！", 1, 106001);
INSERT INTO `v1_8b97fcffbb77f09a224b89af094bbbce27a589b60fe8c3f06896c4c2dadda501` VALUES (0.0, 1, "コッコロ", 0, 15, 0, 15001, 1, "2030/12/17 14:59:59", 0.0, -470.0, "2015/12/17 15:00:00", "主さま、旅の準備を整えて\nより充実した冒険をいたし\nましょう", 1, 105901);
INSERT INTO `v1_8b97fcffbb77f09a224b89af094bbbce27a589b60fe8c3f06896c4c2dadda501` VALUES (0.0, 1, "シェフィ", 0, 16, 2104010, 16001, 2, "2030/12/17 14:59:59", 0.0, -363.0, "2015/12/17 15:00:00", "ねぇ…クランに加入してみない？\n一人も楽しいけど、みんなと\n一緒も賑やかでいいと思うの", 1, 106401);
INSERT INTO `v1_8b97fcffbb77f09a224b89af094bbbce27a589b60fe8c3f06896c4c2dadda501` VALUES (0.0, 1, "ペコリーヌ", 0, 16, 0, 16002, 1, "2030/12/17 14:59:59", 0.0, -363.0, "2015/12/17 15:00:00", "クランに加入してみませんか？\nみんなといっしょに冒険した方が、\n楽しいですよ！", 1, 105801);
INSERT INTO `v1_8b97fcffbb77f09a224b89af094bbbce27a589b60fe8c3f06896c4c2dadda501` VALUES (0.0, 1, "キャル", 0, 16, 0, 16003, 1, "2030/12/17 14:59:59", 0.0, -363.0, "2015/12/17 15:00:00", "クランに入れるらしいわよ\nせっかくだから入ってみたら？\nけっこう楽しいみたいよ♪", 1, 106001);
INSERT INTO `v1_8b97fcffbb77f09a224b89af094bbbce27a589b60fe8c3f06896c4c2dadda501` VALUES (0.0, 1, "コッコロ", 0, 16, 0, 16004, 1, "2030/12/17 14:59:59", 0.0, -363.0, "2015/12/17 15:00:00", "こちらでクランに加入できます\n素敵なクランがたくさんあって、\n目移りしてしまいますね", 1, 105901);
