INSERT INTO `v1_8b97fcffbb77f09a224b89af094bbbce27a589b60fe8c3f06896c4c2dadda501` VALUES (0.0, 1, "カリン", 0, 18, 0, 18001, 1, "2030/12/17 14:59:59", 0.0, -363.0, "2015/12/17 15:00:00", "ようこそ！\nこちらでは、\nギルドハウスで流れる音楽の\n変更や購入が出来ますよ", 1, 190801);
