INSERT INTO `v1_8b97fcffbb77f09a224b89af094bbbce27a589b60fe8c3f06896c4c2dadda501` VALUES (0.0, 6, "カリン", 0, 20, 0, 20002, 1, "2030/12/17 14:59:59", 0.0, -470.0, "2015/12/17 15:00:00", "いらっしゃいませ！\nＢＧＭの購入はこちらです\n素敵な曲がたくさんあって、\n目移りしちゃいますね", 1, 190801);
INSERT INTO `v1_8b97fcffbb77f09a224b89af094bbbce27a589b60fe8c3f06896c4c2dadda501` VALUES (0.0, 1, "キャル", 0, 21, 0, 21001, 1, "2030/12/17 14:59:59", 0.0, -363.0, "2015/12/17 15:00:00", "ここで手に入れたアイテムで\n専用装備を作成できるみたいね\nアイテムが集まったら\nさっそく作ってみましょ！", 1, 106001);
