INSERT INTO `v1_8b97fcffbb77f09a224b89af094bbbce27a589b60fe8c3f06896c4c2dadda501` VALUES (0.0, 1, "シェフィ", 0, 21, 2104010, 21003, 2, "2030/12/17 14:59:59", 0.0, -363.0, "2015/12/17 15:00:00", "専用装備が欲しいなら\nこのクエストに挑戦するといいわ\n集めた素材でもっともっと\n強くなりましょう", 1, 106401);
