INSERT INTO `v1_8b97fcffbb77f09a224b89af094bbbce27a589b60fe8c3f06896c4c2dadda501` VALUES (0.0, 1, "カリン", 0, 22, 0, 22001, 1, "2030/12/17 14:59:59", 0.0, -430.0, "2015/12/17 15:00:00", "投票はこちらです\nあなたが応援したい女の子に\n思いを込めた一票を\nお願いします！", 1, 190801);
