INSERT INTO `v1_8b97fcffbb77f09a224b89af094bbbce27a589b60fe8c3f06896c4c2dadda501` VALUES (0.0, 3, "カリン", 0, 23, 0, 23001, 1, "2030/12/17 14:59:59", 0.0, -430.0, "2015/12/17 15:00:00", "たくさんの投票、\nありがとうございました\n結果発表を楽しみに\n待っていてくださいね！", 1, 190801);
INSERT INTO `v1_8b97fcffbb77f09a224b89af094bbbce27a589b60fe8c3f06896c4c2dadda501` VALUES (0.0, 4, "カリン", 0, 23, 0, 23002, 1, "2030/12/17 14:59:59", 0.0, -430.0, "2015/12/17 15:00:00", "ただ今、人気投票の\n集計中です\n結果が出るまでもう少しだけ\nお待ちくださいね", 1, 190801);
INSERT INTO `v1_8b97fcffbb77f09a224b89af094bbbce27a589b60fe8c3f06896c4c2dadda501` VALUES (8.0, 1, "エリコ（バレンタイン）", 10011, 24, 0, 24001, 1, "2030/12/17 14:59:59", 4.08, -385.0, "2019/01/31 12:00:00", "あなた様が手伝ってくださった\nチョコレートケーキ\n美味しくないはずがありませんわ", 2, 109001);
INSERT INTO `v1_8b97fcffbb77f09a224b89af094bbbce27a589b60fe8c3f06896c4c2dadda501` VALUES (8.0, 2, "エリコ（バレンタイン）", 10011, 24, 0, 24002, 1, "2030/12/17 14:59:59", 5.0, -385.0, "2019/01/31 12:00:00", "このような失敗、あなた様が\nするはずありません\nもう一度挑戦してみましょう", 2, 109001);
INSERT INTO `v1_8b97fcffbb77f09a224b89af094bbbce27a589b60fe8c3f06896c4c2dadda501` VALUES (8.0, 3, "エリコ（バレンタイン）", 10011, 24, 0, 24003, 1, "2030/12/17 14:59:59", 10.11, -385.0, "2019/01/31 12:00:00", "あなた様のために\n作っているケーキを\nあなた様に手伝っていただく……\nことになるなんて……クスクス", 2, 109001);
INSERT INTO `v1_8b97fcffbb77f09a224b89af094bbbce27a589b60fe8c3f06896c4c2dadda501` VALUES (8.0, 4, "エリコ（バレンタイン）", 10011, 24, 0, 24004, 1, "2030/12/17 14:59:59", 4.0, -385.0, "2019/01/31 12:00:00", "あまり気に病む必要はありませんわ\nこれからは私がたっぷりと\n時間をかけて教えて差し上げますから", 2, 109001);
INSERT INTO `v1_8b97fcffbb77f09a224b89af094bbbce27a589b60fe8c3f06896c4c2dadda501` VALUES (8.0, 5, "エリコ（バレンタイン）", 10011, 24, 0, 24005, 1, "2030/12/17 14:59:59", 6.0, -385.0, "2019/01/31 12:00:00", "今回は調子が悪かった……のですか？\nあなた様が手伝ってくれたものなら\nわたしはどんなものでも……クスクス", 2, 109001);
INSERT INTO `v1_8b97fcffbb77f09a224b89af094bbbce27a589b60fe8c3f06896c4c2dadda501` VALUES (0.0, 6, "エリコ（バレンタイン）", 10011, 24, 0, 24006, 1, "2030/12/17 14:59:59", 0.0, 0.0, "2019/01/31 12:00:00", "あなた様が手がけた\nチョコレートケーキは\n私にとっての宝物……\n侮辱する者は生かしておきませんわ", 1, 109002);
INSERT INTO `v1_8b97fcffbb77f09a224b89af094bbbce27a589b60fe8c3f06896c4c2dadda501` VALUES (0.0, 7, "エリコ（バレンタイン）", 10011, 24, 0, 24007, 1, "2030/12/17 14:59:59", 0.0, 0.0, "2019/01/31 12:00:00", "とても素晴らしい出来栄えですわ！\nあなた様にふさわしい\nチョコレートケーキができました", 1, 109002);
INSERT INTO `v1_8b97fcffbb77f09a224b89af094bbbce27a589b60fe8c3f06896c4c2dadda501` VALUES (0.0, 8, "エリコ（バレンタイン）", 10011, 24, 0, 24008, 1, "2030/12/17 14:59:59", 0.0, 0.0, "2019/01/31 12:00:00", "私を手伝ってくれたあなた様は\nすでに私の虜……\n甘い世界に誘って差し上げますわ", 1, 109002);
INSERT INTO `v1_8b97fcffbb77f09a224b89af094bbbce27a589b60fe8c3f06896c4c2dadda501` VALUES (0.0, 9, "エリコ（バレンタイン）", 10011, 24, 0, 24009, 1, "2030/12/17 14:59:59", 0.0, 0.0, "2019/01/31 12:00:00", "あなた様の愛情\nしかと受け止めました\nこれが……私たちの初めての\n共同作業ですわね……クスクス", 1, 109002);
INSERT INTO `v1_8b97fcffbb77f09a224b89af094bbbce27a589b60fe8c3f06896c4c2dadda501` VALUES (0.0, 10, "エリコ（バレンタイン）", 10011, 24, 0, 24010, 1, "2030/12/17 14:59:59", 0.0, 0.0, "2019/01/31 12:00:00", "これは世界一の\nチョコレートケーキですわ\nだって、あなた様が腕によりをかけて\n作ってくださったんですもの", 1, 109002);
INSERT INTO `v1_8b97fcffbb77f09a224b89af094bbbce27a589b60fe8c3f06896c4c2dadda501` VALUES (8.0, 1, "シズル（バレンタイン）", 10011, 24, 0, 24011, 1, "2030/12/17 14:59:59", 2.12, -385.0, "2019/01/31 12:00:00", "上手にできなくて残念だった？\nでも、弟くんの気持ちが嬉しいな\nいつだってお姉ちゃんは\nそばにいるからね♪", 2, 109101);
