INSERT INTO `v1_8b97fcffbb77f09a224b89af094bbbce27a589b60fe8c3f06896c4c2dadda501` VALUES ("2030/12/17 14:59:59", -363.0, 0.0, 2, "シェフィ", 13003, 2104010, 1, 1, 106401, 13, "2015/12/17 15:00:00", 0.0, 0, "みんなとの大切な思い出が\nたくさん詰まってるのね\n一緒に振り返って、\n心に刻みたいわ");
INSERT INTO `v1_8b97fcffbb77f09a224b89af094bbbce27a589b60fe8c3f06896c4c2dadda501` VALUES ("2030/12/17 14:59:59", -363.0, 0.0, 1, "ペコリーヌ", 13004, 0, 1, 1, 105801, 13, "2015/12/17 15:00:00", 0.0, 0, "女の子との思い出が\nたくさん見られますよ！\nヒューヒュー♪\nモテモテですねー☆");
INSERT INTO `v1_8b97fcffbb77f09a224b89af094bbbce27a589b60fe8c3f06896c4c2dadda501` VALUES ("2030/12/17 14:59:59", -363.0, 0.0, 1, "コッコロ", 14001, 0, 1, 1, 105901, 14, "2015/12/17 15:00:00", 0.0, 0, "育成に必要なアイテムが\n獲得できるクエストです\nわたくし、主さまのために、\n強くなりますよ");
