INSERT INTO `v1_8b97fcffbb77f09a224b89af094bbbce27a589b60fe8c3f06896c4c2dadda501` VALUES ("2030/12/17 14:59:59", -363.0, 0.0, 2, "シェフィ", 16001, 2104010, 1, 1, 106401, 16, "2015/12/17 15:00:00", 0.0, 0, "ねぇ…クランに加入してみない？\n一人も楽しいけど、みんなと\n一緒も賑やかでいいと思うの");
