INSERT INTO `v1_8b97fcffbb77f09a224b89af094bbbce27a589b60fe8c3f06896c4c2dadda501` VALUES ("2030/12/17 14:59:59", -385.0, 8.0, 1, "エリコ（バレンタイン）", 24001, 0, 2, 1, 109001, 24, "2019/01/31 12:00:00", 4.08, 10011, "あなた様が手伝ってくださった\nチョコレートケーキ\n美味しくないはずがありませんわ");
INSERT INTO `v1_8b97fcffbb77f09a224b89af094bbbce27a589b60fe8c3f06896c4c2dadda501` VALUES ("2030/12/17 14:59:59", -385.0, 8.0, 1, "エリコ（バレンタイン）", 24002, 0, 2, 2, 109001, 24, "2019/01/31 12:00:00", 5.0, 10011, "このような失敗、あなた様が\nするはずありません\nもう一度挑戦してみましょう");
INSERT INTO `v1_8b97fcffbb77f09a224b89af094bbbce27a589b60fe8c3f06896c4c2dadda501` VALUES ("2030/12/17 14:59:59", -385.0, 8.0, 1, "エリコ（バレンタイン）", 24003, 0, 2, 3, 109001, 24, "2019/01/31 12:00:00", 10.11, 10011, "あなた様のために\n作っているケーキを\nあなた様に手伝っていただく……\nことになるなんて……クスクス");
INSERT INTO `v1_8b97fcffbb77f09a224b89af094bbbce27a589b60fe8c3f06896c4c2dadda501` VALUES ("2030/12/17 14:59:59", -385.0, 8.0, 1, "エリコ（バレンタイン）", 24004, 0, 2, 4, 109001, 24, "2019/01/31 12:00:00", 4.0, 10011, "あまり気に病む必要はありませんわ\nこれからは私がたっぷりと\n時間をかけて教えて差し上げますから");
INSERT INTO `v1_8b97fcffbb77f09a224b89af094bbbce27a589b60fe8c3f06896c4c2dadda501` VALUES ("2030/12/17 14:59:59", -385.0, 8.0, 1, "エリコ（バレンタイン）", 24005, 0, 2, 5, 109001, 24, "2019/01/31 12:00:00", 6.0, 10011, "今回は調子が悪かった……のですか？\nあなた様が手伝ってくれたものなら\nわたしはどんなものでも……クスクス");
INSERT INTO `v1_8b97fcffbb77f09a224b89af094bbbce27a589b60fe8c3f06896c4c2dadda501` VALUES ("2030/12/17 14:59:59", 0.0, 0.0, 1, "エリコ（バレンタイン）", 24006, 0, 1, 6, 109002, 24, "2019/01/31 12:00:00", 0.0, 10011, "あなた様が手がけた\nチョコレートケーキは\n私にとっての宝物……\n侮辱する者は生かしておきませんわ");
INSERT INTO `v1_8b97fcffbb77f09a224b89af094bbbce27a589b60fe8c3f06896c4c2dadda501` VALUES ("2030/12/17 14:59:59", 0.0, 0.0, 1, "エリコ（バレンタイン）", 24007, 0, 1, 7, 109002, 24, "2019/01/31 12:00:00", 0.0, 10011, "とても素晴らしい出来栄えですわ！\nあなた様にふさわしい\nチョコレートケーキができました");
INSERT INTO `v1_8b97fcffbb77f09a224b89af094bbbce27a589b60fe8c3f06896c4c2dadda501` VALUES ("2030/12/17 14:59:59", 0.0, 0.0, 1, "エリコ（バレンタイン）", 24008, 0, 1, 8, 109002, 24, "2019/01/31 12:00:00", 0.0, 10011, "私を手伝ってくれたあなた様は\nすでに私の虜……\n甘い世界に誘って差し上げますわ");
INSERT INTO `v1_8b97fcffbb77f09a224b89af094bbbce27a589b60fe8c3f06896c4c2dadda501` VALUES ("2030/12/17 14:59:59", 0.0, 0.0, 1, "エリコ（バレンタイン）", 24009, 0, 1, 9, 109002, 24, "2019/01/31 12:00:00", 0.0, 10011, "あなた様の愛情\nしかと受け止めました\nこれが……私たちの初めての\n共同作業ですわね……クスクス");
INSERT INTO `v1_8b97fcffbb77f09a224b89af094bbbce27a589b60fe8c3f06896c4c2dadda501` VALUES ("2030/12/17 14:59:59", 0.0, 0.0, 1, "エリコ（バレンタイン）", 24010, 0, 1, 10, 109002, 24, "2019/01/31 12:00:00", 0.0, 10011, "これは世界一の\nチョコレートケーキですわ\nだって、あなた様が腕によりをかけて\n作ってくださったんですもの");
