INSERT INTO `v1_8ba93c55b759ef154e1d09872081aae614765268e90bc04088d25a2568d3c211` VALUES (100, 24, 129901, 1, "エリコ", "訓練成功、おめでとうございます\n褒めるべき時にはしっかり褒めるのも\n教官の務めですものね。それとも、\n罵られる方が好みですか？　クスクス", 100, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 0.0, 0.0, 10.85, 7, 10142);
INSERT INTO `v1_8ba93c55b759ef154e1d09872081aae614765268e90bc04088d25a2568d3c211` VALUES (101, 24, 129901, 1, "エリコ", "厳しい訓練も、終わってみれば\n短く感じるものですわね。\n正直、私は暴れ足りないくらい…\nいえ、何でもありませんわ。クスクス", 101, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 0.0, 0.0, 11.15, 2, 10142);
INSERT INTO `v1_8ba93c55b759ef154e1d09872081aae614765268e90bc04088d25a2568d3c211` VALUES (102, 24, 129901, 7, "エリコ", "行く手を阻む忌々しい邪魔者を\n蹴散らして、見事ゴールイン…！\nまるで私とあのおかたの人生を\n象徴するような道のりでした…", 102, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 0.0, 0.0, 6.4, 5, 10142);
INSERT INTO `v1_8ba93c55b759ef154e1d09872081aae614765268e90bc04088d25a2568d3c211` VALUES (103, 24, 129901, 3, "エリコ", "まったくもって不甲斐ない結果です\nしかし、挽回の機会はまだありますわ\n次はゴミ虫の地位から脱却できるよう\n死力を尽くしなさい。いいですね？", 103, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 0.0, 0.0, 0.0, 3, 10142);
INSERT INTO `v1_8ba93c55b759ef154e1d09872081aae614765268e90bc04088d25a2568d3c211` VALUES (104, 24, 129901, 2, "エリコ", "クスクス…ああご心配なさらず\nなんとも情けない成績を残す輩には\nどんな罰が相応しいか、考えている\nだけですので…クスクスクス", 104, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 0.0, 0.0, 2.43, 8, 10142);
INSERT INTO `v1_8ba93c55b759ef154e1d09872081aae614765268e90bc04088d25a2568d3c211` VALUES (105, 24, 129901, 4, "エリコ", "私が教官として認められなければ、\nあのおかたを失望させてしまう…\nそれだけはあってはなりません\nあなた方も、わかっていますわね？", 105, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 0.0, 0.0, 9.08, 3, 10142);
INSERT INTO `v1_8ba93c55b759ef154e1d09872081aae614765268e90bc04088d25a2568d3c211` VALUES (106, 24, 129901, 6, "エリコ", "まずまずの結果ではありますが、\nこの程度で満足してもらっては\n困りますわ。エリコ班の一員として、\nより高みを目指していただきますので", 106, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 0.0, 0.0, 0.0, 6, 10142);
INSERT INTO `v1_8ba93c55b759ef154e1d09872081aae614765268e90bc04088d25a2568d3c211` VALUES (107, 24, 129901, 3, "エリコ", "私の指導を受けながら、この結果は…\nもっと真剣に考えねばなりませんね\nあなた方が怠けずにいるには、\nどんなしつけが必要かを…クスクス", 107, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 0.0, 0.0, 10.6, 8, 10142);
INSERT INTO `v1_8ba93c55b759ef154e1d09872081aae614765268e90bc04088d25a2568d3c211` VALUES (108, 24, 129901, 6, "エリコ", "ひとまず訓練をこなしたご褒美か、\n実力を出しきれたとは言えない罰か…\nみなさんにどちらを与えるべきか\n少し悩む結果ですわね", 108, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 0.0, 0.0, 7.54, 4, 10142);
INSERT INTO `v1_8ba93c55b759ef154e1d09872081aae614765268e90bc04088d25a2568d3c211` VALUES (109, 24, 129901, 2, "エリコ", "なかなかいい戦いぶりでしたわ\nこれもあなた方の頑張りと、何より\n優秀な教官である私の教育の賜物\nそうですわね？　クスクスクス…", 109, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 0.0, 0.0, 11.5, 7, 10142);
INSERT INTO `v1_8ba93c55b759ef154e1d09872081aae614765268e90bc04088d25a2568d3c211` VALUES (110, 24, 129901, 2, "エリコ", "これだけ進めたのならば合格点ですわ\nでも、もっと高みを目指したいと\n思っているのなら…さらなる訓練にも\n付き合って差し上げましょう", 110, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 0.0, 0.0, 8.83, 1, 10142);
INSERT INTO `v1_8ba93c55b759ef154e1d09872081aae614765268e90bc04088d25a2568d3c211` VALUES (111, 24, 129901, 1, "エリコ", "未熟で頼りなかったあなた方が、\n見違えるように成長しました\nなるほど…これこそが、教官冥利に\n尽きるという気持ちなのですね", 111, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 0.0, 0.0, 6.68, 2, 10142);
INSERT INTO `v1_8ba93c55b759ef154e1d09872081aae614765268e90bc04088d25a2568d3c211` VALUES (112, 24, 129901, 6, "エリコ", "まさかあなた方が、私の予想すら\n超える力を発揮するとは…驚きました\nもはやゴミ虫などではありません\n一人前の騎士として胸を張りなさい", 112, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 0.0, 0.0, 8.8, 2, 10142);
INSERT INTO `v1_8ba93c55b759ef154e1d09872081aae614765268e90bc04088d25a2568d3c211` VALUES (113, 24, 129901, 2, "エリコ", "クスクス、見事な働きです\nみなさんならできると信じていました\n他でもない私の指導を受けた、\nエリコ班の優秀な新兵なのですから", 113, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 0.0, 0.0, 8.9, 1, 10142);
INSERT INTO `v1_8ba93c55b759ef154e1d09872081aae614765268e90bc04088d25a2568d3c211` VALUES (114, 24, 129901, 2, "エリコ", "なんてすばらしい成績…！\n今日という日は『あのおかたを想い\nエリコが限界を超えた記念日』として\n歴史に刻まれることでしょう…！", 114, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 0.0, 0.0, 3.7, 5, 10142);
INSERT INTO `v1_8ba93c55b759ef154e1d09872081aae614765268e90bc04088d25a2568d3c211` VALUES (200, 24, 129801, 1, "マコト", "よし、いい訓練になったな\nおまえたちもよく動けてたし、\nちゃんと成長できてると思うぜ\nその調子で頑張れよっ", 200, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 0.0, 0.0, 7.6, 2, 10142);
INSERT INTO `v1_8ba93c55b759ef154e1d09872081aae614765268e90bc04088d25a2568d3c211` VALUES (201, 24, 129801, 2, "マコト", "ひとまずゴールおめでとう！\nでも、努力は継続してこそだからな\nおまえたちにやる気があるなら、\n何度だって付き合ってやるよ", 201, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 0.0, 0.0, 2.25, 1, 10142);
INSERT INTO `v1_8ba93c55b759ef154e1d09872081aae614765268e90bc04088d25a2568d3c211` VALUES (202, 24, 129801, 2, "マコト", "行軍訓練はこれにて完了！\n…なーんて思ってないよな？\n学んだことが体に染みつくまで\n地道に繰り返すのが練習ってもんさ", 202, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 0.0, 0.0, 3.28, 1, 10142);
INSERT INTO `v1_8ba93c55b759ef154e1d09872081aae614765268e90bc04088d25a2568d3c211` VALUES (203, 24, 129801, 4, "マコト", "みんなに無理をさせちまったか…\nでもこれで諦めることはないぜ\n次はきっとうまくいく。そう信じて\n鍛え続ければ、道は開けるはずだ！", 203, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 0.0, 0.0, 2.98, 1, 10142);
INSERT INTO `v1_8ba93c55b759ef154e1d09872081aae614765268e90bc04088d25a2568d3c211` VALUES (204, 24, 129801, 4, "マコト", "こいつは悔しい結果だな…\nま、反省点はいろいろあったし\nひとつずつ乗り越えていこう\nあたしたちで力を合わせて、なっ！", 204, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 0.0, 0.0, 3.0, 1, 10142);
INSERT INTO `v1_8ba93c55b759ef154e1d09872081aae614765268e90bc04088d25a2568d3c211` VALUES (205, 24, 129801, 1, "マコト", "おまえたちはまだ未熟な訓練兵、\n失敗を必要以上に悔やむことはねえよ\nでも実戦じゃこうはいかないからな\n次は気合を入れ直していけよっ！", 205, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 0.0, 0.0, 10.3, 2, 10142);
INSERT INTO `v1_8ba93c55b759ef154e1d09872081aae614765268e90bc04088d25a2568d3c211` VALUES (206, 24, 129801, 6, "マコト", "これがおまえたちの全力？\nいやいや、そんなはずはないよなっ\nおまえらならもっと強くなれる！\nあたしが言うんだ、間違いないぜ！", 206, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 0.0, 0.0, 2.93, 1, 10142);
INSERT INTO `v1_8ba93c55b759ef154e1d09872081aae614765268e90bc04088d25a2568d3c211` VALUES (207, 24, 129801, 3, "マコト", "おまえたちの実力を考えりゃ、\nベストを尽くせたとは言えない成績だ\nなら、次やるべきことも決まってる\n気持ちを切り替えて、鍛え直すんだ！", 207, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 0.0, 0.0, 9.3, 1, 10142);
INSERT INTO `v1_8ba93c55b759ef154e1d09872081aae614765268e90bc04088d25a2568d3c211` VALUES (208, 24, 129801, 4, "マコト", "まだまだ課題がある内容だったな～\n無駄な動き、判断の遅れ…\nそういうのをしっかり直していけば、\n記録ももっと伸びるはずだっ", 208, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 0.0, 0.0, 6.9, 1, 10142);
INSERT INTO `v1_8ba93c55b759ef154e1d09872081aae614765268e90bc04088d25a2568d3c211` VALUES (209, 24, 129801, 2, "マコト", "ここまでいけりゃ、合格点！\nむしろ、最初の頼りなさを思えば\nすげえ成長してるんじゃねえか？\n胸を張れる結果だと思うぜっ！", 209, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 0.0, 0.0, 0.0, 2, 10142);
INSERT INTO `v1_8ba93c55b759ef154e1d09872081aae614765268e90bc04088d25a2568d3c211` VALUES (210, 24, 129801, 1, "マコト", "なかなかハードな訓練だったけど、\nみんなしっかりついてこられたな\n地道に続けた基礎トレの成果が\n形になってる、その証だぜっ", 210, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 0.0, 0.0, 5.95, 2, 10142);
INSERT INTO `v1_8ba93c55b759ef154e1d09872081aae614765268e90bc04088d25a2568d3c211` VALUES (211, 24, 129801, 2, "マコト", "今回の訓練もよく頑張ったな！\nしっかり休んで次に備えろよ！\nなんつーか、教え子が成長してくのを\n見るのは楽しいもんだな…", 211, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 0.0, 0.0, 6.73, 1, 10142);
INSERT INTO `v1_8ba93c55b759ef154e1d09872081aae614765268e90bc04088d25a2568d3c211` VALUES (212, 24, 129801, 2, "マコト", "おまえら、すげえじゃねえか！\nこれならもう、ひよっこ新兵なんて\n言えねえな。きっと騎士団でも\n大活躍間違いなしだぜ！", 212, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 0.0, 0.0, 0.0, 2, 10142);
INSERT INTO `v1_8ba93c55b759ef154e1d09872081aae614765268e90bc04088d25a2568d3c211` VALUES (213, 24, 129801, 1, "マコト", "敵との戦い方、状況判断の正確さ\n何より根性…どれも最高だったぞ！\nうかうかしてたら、教官のあたしまで\n追い抜かれちまうかもな…へへっ", 213, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 0.0, 0.0, 6.65, 2, 10142);
INSERT INTO `v1_8ba93c55b759ef154e1d09872081aae614765268e90bc04088d25a2568d3c211` VALUES (214, 24, 129801, 6, "マコト", "あたしの指導が良かった？\nいやいや、ここまで強くなれたのは、\n他でもないおまえたち自身が努力した\nおかげだよ。改めて…おめでとうっ！", 214, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 0.0, 0.0, 3.05, 1, 10142);
