INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("カリン", 1, 1, 0, "", 0, 190804, 1, 60000, 0, 1, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60000, 1, 2, "今日の運勢は姫吉！　あらゆる運気が最大を示しています！　　　今なら「伝説の吸血鬼」をも従え、世界征服も夢じゃありません！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60000, 1, 3, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 2, 0, "", 0, 0, 2, 60000, 1, 4, "今日の運勢は姫吉！　その高い運気はまさにショーグンの如く！　その溢れる威光は人々を惹きつけ、皆の人気者間違いなしです！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60000, 2, 5, "今日の運勢は大吉！　運気がどんどん高まっています！　　　　　何をやっても、最大効率で素晴らしい結果になりますよ♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60000, 2, 6, "今日の運勢は大吉！　なにをやっても上手くいって大活躍！　　　そんな素敵なあなたを見て、後ろから誰かが……クスクス♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 2, 0, "", 0, 0, 2, 60000, 2, 7, "今日の運勢は大吉！　運気が高く、何かに挑戦すると良い結果に！隠れた才能が引き出されそう！　目指せヒデサ……ではなく秀才！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60000, 3, 8, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたいやき！　和菓子パワーで売上アップです！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60000, 3, 9, "今日の運勢は中吉！　旅行運がぐんぐんと上昇中♪　　　　　　　もしかしたら、あの「マホマホ王国」にだって行けるかも♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60000, 3, 10, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60000, 3, 11, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 2, 0, "", 0, 0, 2, 60000, 3, 12, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかの如く、色々な事がうまくいきますよ♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60000, 4, 13, "今日の運勢は小吉！　早朝に幸運の兆しあり！　　　　　　　　　もしかしたら、優しいお姉ちゃんが起こしにきてくれますよ♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60000, 4, 14, "今日の運勢は小吉！　運気上昇中ですが、上がるまでが要注意！　ラッキーアニマルは熊！　本当の家族のように守ってくれるかも♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60000, 4, 15, "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60000, 4, 16, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 2, 0, "", 0, 0, 2, 60000, 4, 17, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("カリン", 2, 0, 0, "", 0, 190804, 3, 60000, 0, 18, "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("カリン", 3, 0, 0, "", 7, 190804, 3, 60000, 0, 19, "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ハツネ", 1, 0, 0, "", 1, 101201, 0, 80001, 0, 20, "キミと出会ってもう\n1年もたつんだね！\nう～ん、いつも\n寝てばっかりだから、");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ハツネ", 2, 0, 0, "", 1, 101201, 0, 80001, 0, 21, "少しでも\n寝る時間を減らして\nキミと一緒にいる\n時間増やしたいなぁ♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("シオリ", 1, 0, 0, "", 1, 103801, 1, 80001, 0, 22, "あなたと知り合ってから\nもう1年…\n時がたつのは\n早いですね");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("シオリ", 2, 0, 0, "", 1, 103801, 1, 80001, 0, 23, "来年も、\n一緒にいられたら…\nうれしいな");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キョウカ", 1, 0, 0, "", 2, 103601, 0, 80001, 0, 24, "あなたと一緒にいるようになって、\nもう1年ですか…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キョウカ", 2, 0, 0, "", 2, 103601, 0, 80001, 0, 25, "最初の頃よりも、頼もしく\nなりましたね。私はどうですか？\n成長していますか？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ミミ", 1, 0, 0, "", 2, 102001, 1, 80001, 0, 26, "おにいちゃんとあってから、\n1年たったよ～！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ミミ", 2, 0, 0, "", 2, 102001, 1, 80001, 0, 27, "ミミね、おにいちゃんと\nいっぱい遊べてたのしかったな～♪\nこれからもずっと\nいっしょにいてね～！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ミソギ", 1, 0, 0, "", 2, 100401, 2, 80001, 0, 28, "にいちゃんがミソギのイタズラに\n引っかかり続けてもう1年かぁ");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ミソギ", 2, 0, 0, "", 2, 100401, 2, 80001, 0, 29, "…これからもいっぱいミソギと\n遊んでね、にいちゃん！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("マホ", 1, 0, 0, "", 3, 101001, 0, 80001, 0, 30, "もう王子はんと会うてから\n1年ぐらいたつそうどすえ\n早いもんやなあ");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("マホ", 2, 0, 0, "", 3, 101001, 0, 80001, 0, 31, "このままやと、\nうちもあっという間に\n干からびてしまいそうどすえ\nなーんてな。うふふ");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("イリヤ", 1, 0, 0, "", 3, 104401, 1, 80001, 0, 32, "お主と会って早1年じゃな\nこれからも存分に尽くすがよい");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("イリヤ", 2, 0, 0, "", 3, 104401, 1, 80001, 0, 33, "わらわもたまには、お主の献身に\n報いてやらぬでもない♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("カオリ", 1, 0, 0, "", 3, 101701, 2, 80001, 0, 34, "もう、キミと出会って結構な時間が\nたつさ～。でも、私たちの旅は\nまだまだ始まったばかりさ～！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("カオリ", 2, 0, 0, "", 3, 101701, 2, 80001, 0, 35, "これからも、どうぞよろしく\nお願いするさ～！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("サレン", 1, 0, 0, "", 4, 102801, 0, 80001, 0, 36, "あんたと出会ってからもうそんなに\nたつのね～");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("サレン", 2, 0, 0, "", 4, 102801, 0, 80001, 0, 37, "あっという間だったって感じるほど\n楽しかったわ！\nこれからも一緒に頑張りましょ！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("スズメ", 1, 0, 0, "", 4, 102501, 1, 80001, 0, 38, "出会ってから1年間、ずっと\nご迷惑をかけっぱなしで、\nすみません…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("スズメ", 2, 0, 0, "", 4, 102501, 1, 80001, 0, 39, "これからはもっと頑張りますから\n見ていてくださいね\nできれば…\n今までよりも、私の近くで♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("タマキ", 1, 0, 0, "", 5, 104601, 0, 80001, 0, 40, "振り返れば、今までいろんなことが\nあったにゃあ…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("タマキ", 2, 0, 0, "", 5, 104601, 0, 80001, 0, 41, "これからも、二人でたくさんの\n思い出をがっぽり作っていく\nにゃー！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ミフユ", 1, 0, 0, "", 5, 104801, 1, 80001, 0, 42, "キミとの師弟関係も、\nもう1年なのね…\nでもキミに教えることは、");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ミフユ", 2, 0, 0, "", 5, 104801, 1, 80001, 0, 43, "まだまだいっぱいあるわ\nこれからも、\nしっかりついて来てね！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("アキノ", 1, 0, 0, "", 6, 103201, 0, 80001, 0, 44, "あなたさまと出会ってもう1年…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("アキノ", 2, 0, 0, "", 6, 103201, 0, 80001, 0, 45, "光陰矢のごとし、とはまさに\nこのことですわ。…思い出は決して\nお金では買えませんわね");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ユカリ", 1, 0, 0, "", 6, 103401, 1, 80001, 0, 46, "ダメな私を1年間も見捨てないで\nくれて、本当にありがとう");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ユカリ", 2, 0, 0, "", 6, 103401, 1, 80001, 0, 47, "情けないところいっぱいみせるかも\nしれないけど、これからも一緒に\nいてくれると、うれしいな");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("クリスティーナ", 1, 0, 0, "", 7, 107101, 0, 80001, 0, 48, "オマエと出会ってもう1年か\nまさかこのワタシの結婚相手に\nなろうとはな…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("クリスティーナ", 2, 0, 0, "", 7, 107101, 0, 80001, 0, 49, "ともかくこれからも\n愉しませてくれよ☆");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ジュン", 1, 0, 0, "", 7, 104701, 1, 80001, 0, 50, "君と出会ってからもう1年か。\n以前と比べて私の表情もいくらか\n柔らかくなった気がするんだが、\nどうだろう？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ジュン", 2, 0, 0, "", 7, 104701, 1, 80001, 0, 51, "見えないから分からないって？\nそれもそうだな…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("トモ", 1, 0, 0, "", 8, 103701, 0, 80001, 0, 52, "もう1年たったみたいだね\nあなたをからかってばかりだった\nけど、楽しかった♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("トモ", 2, 0, 0, "", 8, 103701, 0, 80001, 0, 53, "これからもよろしくね！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("マツリ", 1, 0, 0, "", 8, 100501, 1, 80001, 0, 54, "出会って、もう1年たっちゃった\nんスね。なんだか意外ッス");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("マツリ", 2, 0, 0, "", 8, 100501, 1, 80001, 0, 55, "毎日楽しくて、あっという間に過ぎ\nちゃったみたいなんスよね\nこれからも一緒に悪と戦うッス！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ミヤコ", 1, 0, 0, "", 9, 100701, 0, 80001, 0, 56, "オマエと会って1年なの！\n全然成仏もできなかったし、");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ミヤコ", 2, 0, 0, "", 9, 100701, 0, 80001, 0, 57, "生き返りもしなかったけど、\nすごく楽しかったの～♪\nオマエとプリンがあれば、\nミヤコはハッピーなの～♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("シノブ", 1, 0, 0, "", 9, 103101, 1, 80001, 0, 58, "あなたと出会って1年なんですね\nでも、私の占いでは、私とあなたは");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("シノブ", 2, 0, 0, "", 9, 103101, 1, 80001, 0, 59, "来年も再来年も一緒にいるようです\nふふ、よろしくお願いしますね");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("アンナ", 1, 0, 0, "", 10, 100901, 0, 80001, 0, 60, "貴様が閃光のシグルドとして覚醒し\nもう1年か…。これからも我らの\n長き戦いに終わりはないが、");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("アンナ", 2, 0, 0, "", 10, 100901, 0, 80001, 0, 61, "それでも貴様となら…\n添い遂げる覚悟はできているぞ！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ナナカ", 1, 0, 0, "", 10, 101301, 1, 80001, 0, 62, "君と出会ってもう1年だって！\n早いねー！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ナナカ", 2, 0, 0, "", 10, 101301, 1, 80001, 0, 63, "これからも最強のバディタッグで\n大活躍だー！\n頼りにしてるよ、相棒！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ミツキ", 1, 0, 0, "", 11, 105101, 0, 80001, 0, 64, "1年たつのって、あっという間ね\nまだまだ君の知らない部分が\nたくさんあるし…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ミツキ", 2, 0, 0, "", 11, 105101, 0, 80001, 0, 65, "これからも、いろんなところを\n調べさせてね？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ルカ", 1, 0, 0, "", 11, 105601, 1, 80001, 0, 66, "お前さんと出会ってもう1年か\n助太刀するつもりが");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ルカ", 2, 0, 0, "", 11, 105601, 1, 80001, 0, 67, "逆に助けられたりもしたね\nこれからもよろしく頼むよ！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("エリコ", 1, 0, 0, "", 11, 102701, 2, 80001, 0, 68, "早いものですわね、あなた様と\nお会いして、もう1年…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("エリコ", 2, 0, 0, "", 11, 102701, 2, 80001, 0, 69, "あなた様といると、時があっと\nいう間に過ぎてしまいます\n時間を止める研究をしなくては\nいけませんね…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("アヤネ", 1, 0, 0, "", 12, 102301, 0, 80001, 0, 70, "お兄ちゃんと会ってから、もう、\n1年なんだね。私、少しは\nお姉ちゃんっぽくなったかな？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("アヤネ", 2, 0, 0, "", 12, 102301, 0, 80001, 0, 71, "あ、ぷうきちもお兄ちゃんっぽく\nなったよね？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("チカ", 1, 0, 0, "", 12, 104201, 1, 80001, 0, 72, "あなたと出会って、\nもう1年がたったんですね…\nこれからもそばにいて、");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("チカ", 2, 0, 0, "", 12, 104201, 1, 80001, 0, 73, "私のことを見守ってください\nできることなら、ずっと…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("クルミ", 1, 0, 0, "", 12, 102101, 2, 80001, 0, 74, "お兄ちゃんと出会って1年かぁ\n私、相変わらずダメダメで…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("クルミ", 2, 0, 0, "", 12, 102101, 2, 80001, 0, 75, "で、でもこれからもずっと…\n一緒にいたいなぁ…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("シズル", 1, 0, 0, "", 13, 104901, 0, 80001, 0, 76, "再会してちょうど1年だね\nお祝いにごちそう作ってきたよ～♪\n特製ケーキと、お寿司と、");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("シズル", 2, 0, 0, "", 13, 104901, 0, 80001, 0, 77, "ハンバーグと、カレーと、\nシチューと、あ、隠しきれない隠し\n味は、お姉ちゃんの愛情だよ！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("リノ", 1, 0, 0, "", 13, 101101, 1, 80001, 0, 78, "お兄ちゃんと再会して、もう1年\nなんですね。\n会えなかった時を思うと、");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("リノ", 2, 0, 0, "", 13, 101101, 1, 80001, 0, 79, "信じられないくらい幸せな1年\nでした！もう絶対、私から離れ\nないでくださいね♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ユイ", 1, 0, 0, "", 14, 100201, 0, 80001, 0, 80, "騎士クンと出会ってから今まで、\nいろんなことがあったよね\n楽しいこと、ドキドキしたこと…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ユイ", 2, 0, 0, "", 14, 100201, 0, 80001, 0, 81, "そんな日々をこれからも、\nず、ずっと一緒に…\n過ごしていきたいな…！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ヒヨリ", 1, 0, 0, "", 14, 100101, 1, 80001, 0, 82, "騎士くんと出会って、まだ1年しか\nたってないんだ！？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ヒヨリ", 2, 0, 0, "", 14, 100101, 1, 80001, 0, 83, "なんかもうずっと一緒にいる気が\nするなぁ…えへへ。これからの\n1年もまたよろしくねっ！\n騎士くん！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("レイ", 1, 0, 0, "", 14, 100301, 2, 80001, 0, 84, "初めてキミに出会って1年…\n随分見違えたね私もキミも、\n更に強くなっていく…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("レイ", 2, 0, 0, "", 14, 100301, 2, 80001, 0, 85, "キミに背中を預けられる日が、\n早くきますように");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 1, 0, 0, "", 15, 105801, 0, 80001, 0, 86, "1年もわたしのことを、忘れない\nでいてくれて、ありがとうござい\nます！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 2, 0, 0, "", 15, 105801, 0, 80001, 0, 87, "これからもずっと、覚えていて\nくださいね！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 1, 0, 0, "", 15, 105901, 1, 80001, 0, 88, "こうして1年も、主さまのおそばに\n置いていただけて、とても光栄\nです。どうかこれからも、");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 2, 0, 0, "", 15, 105901, 1, 80001, 0, 89, "末永く主さまをお世話させて\nください。わたくしは、ずっと\n主さまのガイド役でいたいのです");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 1, 0, 0, "", 15, 106001, 2, 80001, 0, 90, "1年か、まっさかこんなに長い\n付き合いになるなんてね～\nでも…わりと楽しい1年だったかも");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 2, 0, 0, "", 15, 106001, 2, 80001, 0, 91, "だから、もうしばらくあんたに\n付き合ってやるわよ喜びなさい！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("スズメ", 1, 1, 0, "", 0, 112101, 1, 60001, 0, 92, "新しい年のはじめに、\nおみくじはいかがでしょうか？\nよい運勢であるほど、\n沢山プレゼントが貰えますよ♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60001, 1, 93, "今日の運勢は姫吉！　あらゆる運気が最大を示しています！　　　今なら「伝説の吸血鬼」をも従え、世界征服も夢じゃありません！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60001, 1, 94, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60001, 1, 95, "今日の運勢は姫吉！　最強の運勢でどんなドジも跳ね除けます！　大切な人と一緒に、最高の一日になること間違いなし！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60001, 1, 96, "今日の運勢は姫吉！　どんな挑戦も上手くいく、最高の運勢です！理想の自分に変身できる絶好の機会！　新しい一歩を踏み出そう！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 2, 0, "", 0, 0, 2, 60001, 1, 97, "今日の運勢は姫吉！　その高い運気はまさにショーグンの如く！　その溢れる威光は人々を惹きつけ、皆の人気者間違いなしです！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60001, 2, 98, "今日の運勢は大吉！　運気がどんどん高まっています！　　　　　何をやっても、最大効率で素晴らしい結果になりますよ♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60001, 2, 99, "今日の運勢は大吉！　なにをやっても上手くいって大活躍！　　　そんな素敵なあなたを見て、後ろから誰かが……クスクス♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60001, 2, 100, "今日の運勢は大吉！　ラッキーワードは「ちぇる～ん♪」　　　　出会う人みんなに言ってあげると、ハッピーになれますよ！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60001, 2, 101, "今日の運勢は大吉！　運勢はバッチリで無敵状態！　　　　　　　龍眼で全てを見通して、思い通りの一日を過ごしましょう！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 2, 0, "", 0, 0, 2, 60001, 2, 102, "今日の運勢は大吉！　運気が高く、何かに挑戦すると良い結果に！隠れた才能が引き出されそう！　目指せヒデサ……ではなく秀才！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60001, 3, 103, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたいやき！　和菓子パワーで売上アップです！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60001, 3, 104, "今日の運勢は中吉！　旅行運がぐんぐんと上昇中♪　　　　　　　もしかしたら、あの「マホマホ王国」にだって行けるかも♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60001, 3, 105, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60001, 3, 106, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60001, 3, 107, "今日の運勢は中吉！　お出かけした先で良縁に恵まれそうです！　ダウナー系あざとい女子が、あま～い飴をくれるかも？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60001, 3, 108, "今日の運勢は中吉！　大抵のことはゴリ押しでなんとかできます！ラッキープレイスはゲーセン。パンチングマシンでストレス発散！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 2, 0, "", 0, 0, 2, 60001, 3, 109, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかの如く、色々な事がうまくいきますよ♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60001, 4, 110, "今日の運勢は小吉！　早朝に幸運の兆しあり！　　　　　　　　　もしかしたら、優しいお姉ちゃんが起こしにきてくれますよ♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60001, 4, 111, "今日の運勢は小吉！　運気上昇中ですが、上がるまでが要注意！　ラッキーアニマルは熊！　本当の家族のように守ってくれるかも♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60001, 4, 112, "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60001, 4, 113, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60001, 4, 114, "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60001, 4, 115, "今日の運勢は小吉！　思わぬ相手と仲良くなれるかも？　　　　　うっかり毒舌をもらしてしまわないように要注意です！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 2, 0, "", 0, 0, 2, 60001, 4, 116, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("スズメ", 2, 0, 0, "", 0, 112101, 3, 60001, 0, 117, "おみくじの結果はいかがでしたか？\n新たに始まる一日が、\nあなたにとってよい日になりますように\n明日もお待ちしていますね♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("スズメ", 3, 0, 0, "", 7, 112101, 3, 60001, 0, 118, "おみくじ、楽しんで頂けましたか？\nあなたの新しい年が\n素晴らしいものになるよう、\n心からお祈りしますね");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 1, 0, 0, "", 1, 105801, 0, 80003, 0, 119, "ヒューヒュー♪\nドンドンパフパフー♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 1, 0, 0, "", 1, 105901, 1, 80003, 0, 120, "ひゅーひゅー\nどんどんぱふぱふー\nでございます");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 2, 0, 0, "", 1, 105901, 1, 80003, 0, 121, "さぁ、キャルさまもご一緒に\nどんどんぱふぱふいたしましょう");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 1, 0, 0, "", 1, 106001, 2, 80003, 0, 122, "え？　ちょっと\nいきなりなにっ？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 2, 0, 0, "", 1, 106001, 2, 80003, 0, 123, "ドンドンパフパフってなんなの？\nあたしにも分かるように\nちゃんと説明しなさいっての！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 2, 0, 0, "", 1, 105801, 0, 80003, 0, 124, "実はですねキャルちゃん！\nもうすぐわたしたちにとっての\n大切な節目──");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 3, 0, 0, "", 1, 105801, 0, 80003, 0, 125, "『プリンセスコネクト！\nRe:Dive』\n2.5周年なんですよ！\nやばいですね☆");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 3, 0, 0, "", 1, 105901, 1, 80003, 0, 126, "ですからその日に向けて\n今から気分を盛り上げているのです");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 3, 0, 0, "", 1, 106001, 2, 80003, 0, 127, "ああ、そういえば\nそんな時期だっけ\nって言っても、もう少し先じゃない\n気が早いんじゃないの？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 4, 0, 0, "", 1, 105901, 1, 80003, 0, 128, "いいえ、キャルさま\nこういったお祝いは準備から\n楽しむものでございます\nそうですよね、ペコリーヌさま");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 4, 0, 0, "", 1, 105801, 0, 80003, 0, 129, "その通りです♪　というわけで！\nカウントダウンの始まりですよ！\nみんなで一緒に2.5周年に向けて\n盛り上がっていきましょー☆");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 4, 0, 0, "", 1, 106001, 2, 80003, 0, 130, "ド、ドンドンパフパフ～\nって何言わせんのよ！\nまったくもう、浮かれちゃってさ");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 5, 0, 0, "", 1, 105901, 1, 80003, 0, 131, "ふふ。キャルさまもワクワクして\nおいでのようですね\nそれでは主さま。大切な記念日まで\nどうぞご期待くださいませ");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 5, 0, 0, "", 2, 106001, 0, 80003, 0, 132, "記念日を盛り上げるったって\n一体どうしたらいいのよ？\nあたし、そういうの\n苦手なんだけど…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("カリン", 1, 0, 0, "", 2, 190801, 1, 80003, 0, 133, "あら、キャルさん\nどうかなさいましたか？\n何かお困りのようですけど");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 6, 0, 0, "", 2, 106001, 0, 80003, 0, 134, "げっ…カリンじゃない\nなんでもないわよ！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("カリン", 2, 0, 0, "", 2, 190801, 1, 80003, 0, 135, "そうなのですか？\nキャルさんのことですからてっきり\n2.5周年をどう盛り上げたら\nいいか悩んでいるのかと…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 7, 0, 0, "", 2, 106001, 0, 80003, 0, 136, "分かってんじゃないのよ！？\nいっ、いや、違うけどね！\nあたしは別にそんなことで\n悩んでないわよっ？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("カリン", 3, 0, 0, "", 2, 190801, 1, 80003, 0, 137, "ふふっ…\nこういう時は、真心を込めて\n気持ちをそのまま口にしたら\nいいのではないでしょうか？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 8, 0, 0, "", 2, 106001, 0, 80003, 0, 138, "気持ちを、ねぇ\nそれが一番難しいんじゃない…\nそもそも、あたしは記念日とか\n別にどうだっていいし");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 9, 0, 0, "", 2, 106001, 0, 80003, 0, 139, "…まあ、ペコリーヌもコロ助も\nやけに楽しそうだったから…\nこういうお祭り騒ぎみたいなのも\nたまにはいいかな、とは思うけど");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("カリン", 4, 0, 0, "", 2, 190801, 1, 80003, 0, 140, "なるほどなるほど～\nキャルさんは【美食殿】で祝う\n2.5周年を心から楽しみに\nしている、ということですね！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 10, 0, 0, "", 2, 106001, 0, 80003, 0, 141, "はぁ！？\nどこ聞いたらそうなるのよ！\nあたしは別に楽しみになんか…！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("カリン", 5, 0, 0, "", 2, 190801, 1, 80003, 0, 142, "キャルさんの心からの想い\nみなさんにも伝わったでしょうか？\n私も2.5周年が楽しみです！\n以上、カリンがお伝えしました！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 11, 0, 0, "", 2, 106001, 0, 80003, 0, 143, "心からの想いって何なのよ！？\n勝手に人の言葉を\n捻じ曲げんじゃないわよ！\nこのくそ眼鏡ーーーっ！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 6, 0, 0, "", 3, 105901, 0, 80003, 0, 144, "ペコリーヌさま、もうすぐ\n記念日でございますね\nこういった節目というのは\n感慨深いものでございます");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 5, 0, 0, "", 3, 105801, 1, 80003, 0, 145, "色んな思い出が蘇ってきますね！\n楽しい出来事が\nいっぱいありました♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 7, 0, 0, "", 3, 105901, 0, 80003, 0, 146, "はい、そうでございますね\n主さまも【美食殿】の活動の他にも\nいろんなご経験をされたと\n聞いておりますよ");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 8, 0, 0, "", 3, 105901, 0, 80003, 0, 147, "たしか…ランドソルで話題沸騰と\nなっていた、魔法少女なるものに\nお会いになられたとか");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 6, 0, 0, "", 3, 105801, 1, 80003, 0, 148, "魔法少女って、あの美味しくって\n栄養たっぷりの大ヒット商品\n『魔法少女ソーセージ』の箱に\n描いてある女の子のことですか！？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 7, 0, 0, "", 3, 105801, 1, 80003, 0, 149, "あのソーセージ\n美味しいんですよね～♪\nわたしもたっくさん食べてますよ！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 9, 0, 0, "", 3, 105901, 0, 80003, 0, 150, "あとは、牧場に素敵な温泉が\n出来たとか…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 8, 0, 0, "", 3, 105801, 1, 80003, 0, 151, "牧場ですか！？　温泉につかって\nチーズもオツですね～♪　考えた\nだけでよだれが出ちゃいそうです！\nやばいですね☆");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 10, 0, 0, "", 3, 105901, 0, 80003, 0, 152, "ペコリーヌさま\n言ってるそばからよだれが…\n今お拭きしますね。ふき、ふき");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 9, 0, 0, "", 3, 105801, 1, 80003, 0, 153, "えへへ、ありがとうございます\nコッコロちゃん！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 11, 0, 0, "", 3, 105901, 0, 80003, 0, 154, "それにしても、主さまは\n【美食殿】の一員として様々な\n美食と出会われていたのですね\nわたくし、感服いたしました");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 10, 0, 0, "", 3, 105801, 1, 80003, 0, 155, "これからもたくさん冒険をして\nめいっぱい美食を\n味わい尽くしましょうね！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 11, 0, 0, "", 4, 105801, 0, 80003, 0, 156, "2.5周年カウントダウン♪\nキャルちゃんキャルちゃん！\nわくわくしますね☆");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 12, 0, 0, "", 4, 106001, 1, 80003, 0, 157, "あぁもう、うるさいわね～\nまだまだ先でしょ？\nちょっとは落ち着きなさいよ");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 12, 0, 0, "", 4, 105801, 0, 80003, 0, 158, "キャルちゃんだって最近\nソワソワしてるじゃないですか～\nさっきもご機嫌そうに鼻歌とか\n歌ってましたよね？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 13, 0, 0, "", 4, 106001, 1, 80003, 0, 159, "なっ…！　き、聞いてたの！？\nじゃない、歌ってないわよ！？\nあんたいい加減なこと言うと\nぶっ殺すわよ！？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 13, 0, 0, "", 4, 105801, 0, 80003, 0, 160, "えっへへ～♪\nわたしは最近のキャルちゃんが\nもっともっと大好きですよ♪\nすごく積極的になりましたよね！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 14, 0, 0, "", 4, 106001, 1, 80003, 0, 161, "…積極的？\nなんの話よ？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 14, 0, 0, "", 4, 105801, 0, 80003, 0, 162, "またまた～♪\nアイドルを目指そうとしたこと\n忘れちゃったんですか？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 15, 0, 0, "", 4, 105801, 0, 80003, 0, 163, "ムーサの町で\nきゃるきゃる～ん☆　って！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 15, 0, 0, "", 4, 106001, 1, 80003, 0, 164, "あっ…！？　ちちち違うわよっ！\nあれは操られてただけで\nあたしはアイドルなんか\n目指してないっての！！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 16, 0, 0, "", 4, 105801, 0, 80003, 0, 165, "本当に本当ですか？\nわたし、キャルちゃんとっても\n乗り気だなって\n思ってたんですけど～");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 16, 0, 0, "", 4, 106001, 1, 80003, 0, 166, "まぁ、悪くはなかったけど…って\nだ、だから目指してないって\n言ってるでしょ！！\nなんてこと思い出させんのよ！？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 17, 0, 0, "", 4, 106001, 1, 80003, 0, 167, "ああ…ダメだわ…\nもう殺すしかない…\n2.5周年が来る前に\n記憶ごと消し去ってやるー！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 18, 0, 0, "", 5, 106001, 0, 80003, 0, 168, "2.5周年まであと10日…\nふ～ん？\n案外早いもんね？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 12, 0, 0, "", 5, 105901, 1, 80003, 0, 169, "そうでございますね…\nその日のためにやるべきことは\nまだまだございます\n気を引き締めねばなりません…！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 19, 0, 0, "", 5, 106001, 0, 80003, 0, 170, "やる気十分ねコロ助…\nそんな気負ってるとしんどいわよ？\nお気楽にやりましょ？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 13, 0, 0, "", 5, 105901, 1, 80003, 0, 171, "キャルさま…\nありがとうございます\nキャルさまは本当に\n優しいおかたです");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 20, 0, 0, "", 5, 106001, 0, 80003, 0, 172, "な、なにわけの分かんないこと\n言ってんのよ…\nあたしは優しくなんかないっての");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 14, 0, 0, "", 5, 105901, 1, 80003, 0, 173, "そんなことはございません\n…あの、実はわたくし\nキャルさまに\nひとつお願いがございまして");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 21, 0, 0, "", 5, 106001, 0, 80003, 0, 174, "お願い？　珍しいわね\nコロ助がそんなこと言うなんて\n…まあ、あたしに出来ることなら\n別にいいけど");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 15, 0, 0, "", 5, 105901, 1, 80003, 0, 175, "ありがとうございます\n実は記念日へ向けて\n『かくし芸』というものを\n用意してみようかと思いまして");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 22, 0, 0, "", 5, 106001, 0, 80003, 0, 176, "かくし芸？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 16, 0, 0, "", 5, 105901, 1, 80003, 0, 177, "はい。きゃるきゃる～ん☆　を\n参考に、ころころ～ん☆　という\nご挨拶を考えてみたのですが\nポーズがうまくいかなくて…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 23, 0, 0, "", 5, 106001, 0, 80003, 0, 178, "～～！！　ちょっと褒められて\nいい気になって損したわ！！\nふざけんなふざけんな\nふ～ざ～け～ん～な～っ！！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 17, 0, 0, "", 6, 105801, 0, 80003, 0, 179, "あと９日！　もうすぐですよ♪\nえへへ、楽しみで楽しみで\nごはんが進みますね☆");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 24, 0, 0, "", 6, 106001, 2, 80003, 0, 180, "ごはんが進みますね☆\nじゃなーい！\nいくらなんでも食べ過ぎよ！\n記念日、迎えられなくなるわよ！？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 17, 0, 0, "", 6, 105901, 1, 80003, 0, 181, "大丈夫ですよキャルさま\n食事はわたくしたちの\n歩んできた道そのもの\nなのですから");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 18, 0, 0, "", 6, 105801, 0, 80003, 0, 182, "さっすがコッコロちゃん\nいいことを言いますね！\nそう、わたしたち\n【美食殿】の活動目的は─");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 25, 0, 0, "", 6, 106001, 2, 80003, 0, 183, "─みんなで楽しく食事をすること\nでしょ？\nあれだけ食べ歩いてきたんだから\n分かってるわよ");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 18, 0, 0, "", 6, 105901, 1, 80003, 0, 184, "はい…そうでございますね♪\nそれにしてもわたくしたち\n本当にいろいろなものを\n食べ歩いてきましたね…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 26, 0, 0, "", 6, 106001, 2, 80003, 0, 185, "そうね…ほんっとうにね…\n毒まみれのニセドラゴンとか\n恐竜…は逃げられちゃったっけ\nあとワニゴ…ワニの魔物とかね");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 27, 0, 0, "", 6, 106001, 2, 80003, 0, 186, "そして、とうとう本物の\nドラゴンまで相手に\nしちゃったのよね、あたしたちって");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 28, 0, 0, "", 6, 106001, 2, 80003, 0, 187, "信じられない強さだったわね\nあのドラゴン…やっぱり\n作戦はしっかり立てていかないと\nダメよね");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 19, 0, 0, "", 6, 105801, 0, 80003, 0, 188, "えへへ…☆\nキャルちゃんが色んなことを\n覚えていてくれて\nうれしいです♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 19, 0, 0, "", 6, 105901, 1, 80003, 0, 189, "わたくしも鮮明に覚えておりますよ\n主さまもきっと…♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 29, 0, 0, "", 6, 106001, 2, 80003, 0, 190, "あったり前でしょ！\n忘れないわよ、あんな滅茶苦茶な\nものばかり食べさせられて！\n…忘れられるわけ、ないじゃない");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 30, 0, 0, "", 7, 106001, 0, 80003, 0, 191, "ねえちょっと、ちょっと待って！？\nカリンから、2.5周年記念の\n生放送に出演してほしいって\n言われたんだけど！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 31, 0, 0, "", 7, 106001, 0, 80003, 0, 192, "なんでそういう大事なことを\n急に言うわけ！？\nそんなの聞いてないわよ～！？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 32, 0, 0, "", 7, 106001, 0, 80003, 0, 193, "いっぱい人に見られちゃうのよね？\nなんであたしがそんなこと…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 33, 0, 0, "", 7, 106001, 0, 80003, 0, 194, "あー\nあー\n…声、これで大丈夫かしら？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 34, 0, 0, "", 7, 106001, 0, 80003, 0, 195, "聞き取りにくいかもしれないわね…\nもうちょっと明るい方がいいかも\nえっと…こんな感じ？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 35, 0, 0, "", 7, 106001, 0, 80003, 0, 196, "オイッスー☆");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 36, 0, 0, "", 7, 106001, 0, 80003, 0, 197, "……\nいや、ないわ\nいつもどおりが一番ね、うん");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 37, 0, 0, "", 7, 106001, 0, 80003, 0, 198, "もう、記念の生放送なんて\n超責任重大なやつじゃない…\nもしなにか失敗とかしたら…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 38, 0, 0, "", 7, 106001, 0, 80003, 0, 199, "ど、どど、どうしよう！？\n緊張してきちゃった…！！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 20, 0, 0, "", 8, 105901, 0, 80003, 0, 200, "主さま、わたくし\nペコリーヌさまとキャルさまと共に\n2.5周年を祝う生放送に\n出演する大役を仰せつかりました");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 21, 0, 0, "", 8, 105901, 0, 80003, 0, 201, "多くの方々に\n『プリンセスコネクト！\nRe:Dive』の魅力をお伝えする\n重要な役割でございます");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 22, 0, 0, "", 8, 105901, 0, 80003, 0, 202, "わたくし、全身全霊をもって\n生放送に取り組みたいと\n思っております");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 23, 0, 0, "", 8, 105901, 0, 80003, 0, 203, "ですが、もしかしたら\nセリフを噛んでしまったり\n段取りを間違えてしまったり\nするかもしれません…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 24, 0, 0, "", 8, 105901, 0, 80003, 0, 204, "そのようなことがないよう\n練習はしっかりしておかなくては\nまずはこちらから…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 25, 0, 0, "", 8, 105901, 0, 80003, 0, 205, "オイッスー☆");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 26, 0, 0, "", 8, 105901, 0, 80003, 0, 206, "先ほどキャルさまが練習されて\nいるのをお見かけしたので\nわたくしもと思いまして…\nいかがでしょうか？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 27, 0, 0, "", 8, 105901, 0, 80003, 0, 207, "良い感じ、ですか？\n…ふふ、ありがとうございます\n主さまのあたたかいお言葉があれば\nわたくしは百人力でございます");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 28, 0, 0, "", 8, 105901, 0, 80003, 0, 208, "この大役を無事に成し遂げ\n主さまに素敵な2.5周年を\n迎えていただけるよう…\nわたくし、尽力いたしますね");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 20, 0, 0, "", 9, 105801, 0, 80003, 0, 209, "オイッスー☆");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 21, 0, 0, "", 9, 105801, 0, 80003, 0, 210, "…う～ん、今のはちょっと\n違う感じがしますね\n堅苦しかったかもしれません");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 22, 0, 0, "", 9, 105801, 0, 80003, 0, 211, "あと６日で2.5周年ですか…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 23, 0, 0, "", 9, 105801, 0, 80003, 0, 212, "この年月がオイッスーの\n歴史なんだと思うと\nじ～んとしちゃいます！\nやばいですね☆");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 24, 0, 0, "", 9, 105801, 0, 80003, 0, 213, "生放送で最高のオイッスー☆　を\nみなさんにお届けするために\n頑張らないと！\nそれじゃあもう一度…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 25, 0, 0, "", 9, 105801, 0, 80003, 0, 214, "オイッスー☆");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 26, 0, 0, "", 9, 105801, 0, 80003, 0, 215, "…うう～ん、どうしてでしょう\nまだ何かが違う気がします…\nもしかしたら\nおなかが空きすぎてるとか？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 27, 0, 0, "", 9, 105801, 0, 80003, 0, 216, "今さっき食べたばっかりですけど\nおなかが減っては\n良いオイッスー☆　は\n出せませんよね！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 28, 0, 0, "", 9, 105801, 0, 80003, 0, 217, "そうと決まれば、コッコロちゃんと\nキャルちゃんを誘って\nごはんにしましょう！\n善は急げです！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 29, 0, 0, "", 9, 105801, 0, 80003, 0, 218, "あ、二人が丁度いいところに！\nオイッス～☆　…ああ！！\n今の、今のですよ～♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 30, 0, 0, "", 9, 105801, 0, 80003, 0, 219, "なるほど、どんな時も\n自然体が一番ってことですね！\nこの調子で、生放送も\n頑張っちゃいます♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 31, 0, 0, "", 10, 105801, 0, 80003, 0, 220, "2.5周年まで残り５日ですが\nその前にもうすぐ生放送ですよ！\nコッコロちゃん\n準備の方はどうですか？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 29, 0, 0, "", 10, 105901, 1, 80003, 0, 221, "はい。わたくし、しっかりと\n備えてまいりました\n万事抜かりはございません");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 32, 0, 0, "", 10, 105801, 0, 80003, 0, 222, "おお～すごい自信を感じます…！\nいい生放送になりそうですね☆\n……あれ？　なんだかこの辺り\n揺れていませんか？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 39, 0, 0, "", 10, 106001, 2, 80003, 0, 223, "…………");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 30, 0, 0, "", 10, 105901, 1, 80003, 0, 224, "キャルさま？\nお風邪をひかれたんでしょうか\nひどい震えですが…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 40, 0, 0, "", 10, 106001, 2, 80003, 0, 225, "かかかか風邪じゃないわよ！\nここここれはただ\nななな生放送が楽しみ過ぎて\nワワワワクワクが止まらないだけ！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 33, 0, 0, "", 10, 105801, 0, 80003, 0, 226, "大丈夫ですかキャルちゃん！？\nそうだ、これを飲んでください！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 41, 0, 0, "", 10, 106001, 2, 80003, 0, 227, "きき気がきくじゃない、ありがとっ\nごくっ、んく…ぶはぁっ！？\nちょっと何飲ませたの！？\n辛っ！　苦ぁ！？　甘ぁっ！？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 34, 0, 0, "", 10, 105801, 0, 80003, 0, 228, "これは魔物のエキスに薬草に\nその他色んなものを混ぜた\n特製の栄養ドリンクです！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 35, 0, 0, "", 10, 105801, 0, 80003, 0, 229, "これを飲めば体ぽかぽかで\n元気いっぱいですよ♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 42, 0, 0, "", 10, 106001, 2, 80003, 0, 230, "このアホリーヌ！\n風邪じゃないって言ったでしょ！？\nどさくさに紛れて\nそんな恐ろしいもん飲ませんな～！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 31, 0, 0, "", 10, 105901, 1, 80003, 0, 231, "キャルさま\n震えが収まっておりますね\nよかったです…\nわたくし、心配いたしました");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 43, 0, 0, "", 10, 106001, 2, 80003, 0, 232, "え？　ああ、ホントね…って\nだから風邪じゃないって\n言ってるのに…\nまあいいわこの際なんでも");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 36, 0, 0, "", 10, 105801, 0, 80003, 0, 233, "えっへへ～♪　いつも通りの\nキャルちゃんに戻りましたし\n素敵な生放送が出来そうですね！\n今から楽しみです☆");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("カリン", 6, 0, 0, "", 11, 190801, 0, 80003, 0, 234, "ランドソルのみなさま、こんにちは\n生放送の会場設営を担当しています\n【ギルド管理協会】のカリンです");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("カリン", 7, 0, 0, "", 11, 190801, 0, 80003, 0, 235, "【美食殿】のみなさんは\nまもなく始まる生放送に向けて\n最後の調整のため\n席を外しています");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("カリン", 8, 0, 0, "", 11, 190801, 0, 80003, 0, 236, "頑張っている３人の\n邪魔をするわけには\nいきませんので、ご容赦ください");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("カリン", 9, 0, 0, "", 11, 190801, 0, 80003, 0, 237, "ですので、本日は私から\n【美食殿】のみなさんの様子を\nお伝えさせていただこうと思います");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("カリン", 10, 0, 0, "", 11, 190801, 0, 80003, 0, 238, "まずはペコリーヌさんですが…\n何やら大量のおにぎりを\n食べているようです\nえっと…栄養は大事ですよね");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("カリン", 11, 0, 0, "", 11, 190801, 0, 80003, 0, 239, "次にコッコロさんですが\nこちらは発声練習をしています");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("カリン", 12, 0, 0, "", 11, 190801, 0, 80003, 0, 240, "一生懸命、小さなお口を開けて\n声を出す姿が応援したくなりますね");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("カリン", 13, 0, 0, "", 11, 190801, 0, 80003, 0, 241, "そしてキャルさんは…\nこれは…堂々としたものですね\n何もせず、ただ立ち尽くして…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("カリン", 14, 0, 0, "", 11, 190801, 0, 80003, 0, 242, "あれ？\nもしかして緊張で気を失って…？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("カリン", 15, 0, 0, "", 11, 190801, 0, 80003, 0, 243, "…ええと\n【美食殿】のみなさんは\nやる気十分なようですね！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("カリン", 16, 0, 0, "", 11, 190801, 0, 80003, 0, 244, "３人とも生放送を\nいいものにしようと\n全力で頑張ってくれていますので\nどうかご期待ください");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("カリン", 17, 0, 0, "", 11, 190801, 0, 80003, 0, 245, "それでは\nまた明日お会いしましょう♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 44, 0, 0, "", 12, 106001, 0, 80003, 0, 246, "2.5周年まであと３日！\nそして生放送は明日！\nなんかいよいよって感じね…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 37, 0, 0, "", 12, 105801, 2, 80003, 0, 247, "３人でい～っぱい練習しましたし\nきっと大丈夫ですよ☆");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 32, 0, 0, "", 12, 105901, 1, 80003, 0, 248, "ペコリーヌさまに\n大丈夫と言ってもらえると\nとても心強いですね");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 45, 0, 0, "", 12, 106001, 0, 80003, 0, 249, "信じすぎちゃだめよコロ助\nこいつはなーんにも\n考えてないだけなんだから");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 38, 0, 0, "", 12, 105801, 2, 80003, 0, 250, "ひどいですよぉキャルちゃ～ん！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 33, 0, 0, "", 12, 105901, 1, 80003, 0, 251, "ふふ。お二人とも\n普段通りの様子で\n安心いたしました");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 39, 0, 0, "", 12, 105801, 2, 80003, 0, 252, "はいっ☆\n生放送でもなんでも\n全力全開、わたしらしく\nぶつかっていくだけです！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 46, 0, 0, "", 12, 106001, 0, 80003, 0, 253, "そうね。おめでたいことなんだし\nここまで来たんなら\n楽しめるだけ\n楽しむしかないわよね！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 34, 0, 0, "", 12, 105901, 1, 80003, 0, 254, "わたくしも、普段よりも心が\n熱くなっております\nこの胸の気持ちをお伝えできるよう\n精一杯努力いたします…！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 40, 0, 0, "", 12, 105801, 2, 80003, 0, 255, "それでは、いきましょう！\n明日の生放送、絶対に\n成功させましょうね☆");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 41, 0, 0, "", 13, 105801, 0, 80003, 0, 256, "えっと、最初のひと言は…あれ？\nキャルちゃん、わたし\n何て言うんでしたっけ？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 47, 0, 0, "", 13, 106001, 2, 80003, 0, 257, "は！？\nなにいきなりど忘れしてるの！？\nっていうかあんた、台本は！？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 42, 0, 0, "", 13, 105801, 0, 80003, 0, 258, "忘れてきちゃいました☆");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 48, 0, 0, "", 13, 106001, 2, 80003, 0, 259, "忘れてきちゃいました☆\nで済ませられると思ってんの！？\nほら、あたし二冊持ってきたから\nこれ使いなさい！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 35, 0, 0, "", 13, 105901, 1, 80003, 0, 260, "マイクとは、こんな高い位置に\nあるものなのですね…！\n背伸びをすれば、何とか…\nう～ん、うぅ～ん…！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 49, 0, 0, "", 13, 106001, 2, 80003, 0, 261, "あぁもう\n少し下げればいいでしょ！？\nこれくらいでいい？\nまったく、世話が焼けるわね…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 43, 0, 0, "", 13, 105801, 0, 80003, 0, 262, "あれ？\nキャルちゃん、後ろ髪が\nはねちゃってますよ？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 50, 0, 0, "", 13, 106001, 2, 80003, 0, 263, "え？　うそっ！？\nちゃんと確かめてきたのに！\nどこどこ！？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 36, 0, 0, "", 13, 105901, 1, 80003, 0, 264, "キャルさま、動かないで\nくださいまし。今くしで…\nはい、直りましたよ");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 51, 0, 0, "", 13, 106001, 2, 80003, 0, 265, "あ、ありがと…はぁ。生放送\n本番までもうすぐなのに\nこんなことしてる場合じゃ\nないわよね");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 37, 0, 0, "", 13, 105901, 1, 80003, 0, 266, "はい、時間は残りわずかです\nですが、慌ただしく\n動き回るこの時間も\nなんだか楽しくて…♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 52, 0, 0, "", 13, 106001, 2, 80003, 0, 267, "コロ助、あんた…って\nそうじゃなくて！\n胸にジンワリきてる\n暇なんかないでしょ…！？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 44, 0, 0, "", 13, 105801, 0, 80003, 0, 268, "あはは♪\nキャルちゃんの動きが\nとっても面白いです♪\nやばいですね☆");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 38, 0, 0, "", 13, 105901, 1, 80003, 0, 269, "ふふ\nきっと面白い生放送になると\nわたくし、確信しております");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 45, 0, 0, "", 13, 105801, 0, 80003, 0, 270, "絶対絶対、間違いありません！\n見てくれるみなさんが\n笑顔になれる時間を\nお届けしましょう！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 53, 0, 0, "", 13, 106001, 2, 80003, 0, 271, "そんなの当然でしょ！\n誰が見たって面白いって\n言ってもらえるように\nあれだけ準備したんだから！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 46, 0, 0, "", 13, 105801, 0, 80003, 0, 272, "それでは\n記念日直前生放送！\n楽しみにしててくださいね☆");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("カリン", 18, 0, 0, "", 14, 190801, 0, 80003, 0, 273, "こんにちは\n【ギルド管理協会】のカリンです");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("カリン", 19, 0, 0, "", 14, 190801, 0, 80003, 0, 274, "昨日の生放送は\nいかがだったでしょうか？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("カリン", 20, 0, 0, "", 14, 190801, 0, 80003, 0, 275, "たくさんの笑顔と喜びと\nそして驚きを受け取って\nいただけたのではないかと思います");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("カリン", 21, 0, 0, "", 14, 190801, 0, 80003, 0, 276, "そしていよいよ\n明日は2.5周年の当日\n待ちに待った記念日を\n迎えることとなります");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("カリン", 22, 0, 0, "", 14, 190801, 0, 80003, 0, 277, "【美食殿】のみなさんも\n張り切って様々な準備を\nされていますよ");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("カリン", 23, 0, 0, "", 14, 190801, 0, 80003, 0, 278, "【ギルド管理協会】も\n万全のサポート体制で\n協力してまいりたいと思います");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("カリン", 24, 0, 0, "", 14, 190801, 0, 80003, 0, 279, "…ふふ、本当に楽しみですね\n【ギルド管理協会】の職員として\nだけではなく、私個人としても…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("カリン", 25, 0, 0, "", 14, 190801, 0, 80003, 0, 280, "この記念日を迎えられることが\n心から楽しみですし\n幸せを感じています");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("カリン", 26, 0, 0, "", 14, 190801, 0, 80003, 0, 281, "きっと【美食殿】のみなさんは\nそれ以上に心を弾ませて\nいることでしょう");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("カリン", 27, 0, 0, "", 14, 190801, 0, 80003, 0, 282, "それでは\n明日からの2.5周年を\nどうぞ、お楽しみに…♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 47, 0, 0, "", 15, 105801, 0, 80003, 0, 283, "準備はいいですかっ？\n…いっきますよー！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 54, 0, 0, "", 15, 106001, 2, 80003, 0, 284, "祝！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 39, 0, 0, "", 15, 105901, 1, 80003, 0, 285, "2.5周年！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 48, 0, 0, "", 15, 105801, 0, 80003, 0, 286, "おめでとうございま～す☆");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 55, 0, 0, "", 15, 106001, 2, 80003, 0, 287, "少し前まではまだまだ先じゃない\nとか言ってたけど\n本当にあっという間だったわね");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 40, 0, 0, "", 15, 105901, 1, 80003, 0, 288, "光陰矢の如し…\nとはこのことでございますね\nでも、今日という日を無事に\n迎えることができて良かったです");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 49, 0, 0, "", 15, 105801, 0, 80003, 0, 289, "コッコロちゃん！　キャルちゃん！\n落ち着くには\nまだ早いですよ！\n盛り上がるのはこれからです☆");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 56, 0, 0, "", 15, 106001, 2, 80003, 0, 290, "ほんとあんたはいつも\n全力全開ねー\nでもそうね、記念日なんだし\nパーっといきましょ！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 41, 0, 0, "", 15, 105901, 1, 80003, 0, 291, "ええ、わたくしも全力全開で\nまいります…！\n主さま、さあ\nどうぞ手を取ってくださいまし…♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 57, 0, 0, "", 15, 106001, 2, 80003, 0, 292, "あんたもコロ助も、浮かれて\n転ぶんじゃないわよ？\n…仕方ないから、あたしも\n手を繋いであげるわよ");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 50, 0, 0, "", 15, 105801, 0, 80003, 0, 293, "あー！　二人ともずるいです！\nわたしもっ、ギュー♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 58, 0, 0, "", 15, 106001, 2, 80003, 0, 294, "ちょっ、ペコリーヌ！\nくっつき過ぎよ！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 51, 0, 0, "", 15, 105801, 0, 80003, 0, 295, "記念日だから\n平気平気♪\nやばいですね☆");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 59, 0, 0, "", 15, 106001, 2, 80003, 0, 296, "意味分かんないわよ！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 42, 0, 0, "", 15, 105901, 1, 80003, 0, 297, "ふふ。このめでたき日に\n喜びがあふれておりますね\n主さまは…いかがでしょうか？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 43, 0, 0, "", 15, 105901, 1, 80003, 0, 298, "喜んでいただけているならば\nわたくしはとてもとても\n幸せでございます");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 52, 0, 0, "", 15, 105801, 0, 80003, 0, 299, "さぁ、2.5周年を\n楽しみ尽くしますよ☆\nまだまだ食べたいものもありますし\nやりたいこともたくさんです！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 60, 0, 0, "", 15, 106001, 2, 80003, 0, 300, "分かった、分かったから\n引っ張るんじゃないわよ！\n…そんな掴まなくても\nちゃんとついていくから");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 44, 0, 0, "", 15, 105901, 1, 80003, 0, 301, "ペコリーヌさま…キャルさま…\n主さま…共にまいりましょう\nどこまでも…！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 53, 0, 0, "", 15, 105801, 0, 80003, 0, 302, "はい！　それでは2.5周年\n食べつくしツアーへ！\nそして、その先の未来へ\n向かって出発しましょう！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ・コッコロ・キャル", 54, 0, 1, "105901,45,106001,61", 15, 105801, 0, 80003, 0, 303, "2.5周年\nありがとうございます！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("カリン", 1, 1, 0, "", 0, 190804, 1, 60002, 0, 304, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60002, 1, 305, "今日の運勢は姫吉！　どんな不可能も可能にできちゃう！　　　　仲間と食卓を囲めばさらに運気上昇よ！　ただし魔物料理以外！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60002, 1, 306, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60002, 1, 307, "今日の運勢は姫吉！　最強の運勢でどんなドジも跳ね除けます！　大切な人と一緒に、最高の一日になること間違いなし！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60002, 1, 308, "今日の運勢は姫吉！　どんな挑戦も上手くいく、最高の運勢です！理想の自分に変身できる絶好の機会！　新しい一歩を踏み出そう！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 2, 0, "", 0, 0, 2, 60002, 1, 309, "今日の運勢は姫吉！　やること全てが上手くいく最高の一日！　　あなたの活躍に先生もにっこりです。いい子いい子♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60002, 2, 310, "今日の運勢は大吉！　お裁縫好きな女の子のお墨付きです！　　　お世話したがりの妹にめいっぱい愛されちゃうかも！？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60002, 2, 311, "今日の運勢は大吉！　ラッキーカラーは赤！　　　　　　　　　　湧き上がるアイデアの数々に、高笑いが止まりませんわ！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60002, 2, 312, "今日の運勢は大吉！　ラッキーワードは「ちぇる～ん♪」　　　　出会う人みんなに言ってあげると、ハッピーになれますよ！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60002, 2, 313, "今日の運勢は大吉！　運勢はバッチリで無敵状態！　　　　　　　龍眼で全てを見通して、思い通りの一日を過ごしましょう！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 2, 0, "", 0, 0, 2, 60002, 2, 314, "今日の運勢は大吉！　悩んでいたことが解決しそうです！　　　　たまには勉強も仕事も忘れて、心あたたまる家族と過ごしましょ！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60002, 3, 315, "今日の運勢は中吉！　家の中でゆっくり過ごすのがよさそう！　　ラッキーアイテムはゲームよ！　ベストスコアを更新できるかも？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60002, 3, 316, "今日の運勢は中吉！　上手くなりたいことに積極的に挑戦してみてサックス好きの妹が、手取り足取りサポートしてくれるかも～？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60002, 3, 317, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60002, 3, 318, "今日の運勢は中吉！　日頃の努力が報われます！　　　　　　　　親しい友人がいるなら報告してみて。一緒に喜んでくれるはずだ。");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60002, 3, 319, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたい焼き！　和菓子パワーで売上アップです！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60002, 3, 320, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 2, 0, "", 0, 0, 2, 60002, 3, 321, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかのごとく、色々な事がうまくいきますよ♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60002, 4, 322, "今日の運勢は小吉！　まだ見ぬ危険が隠れているかも！　　　　　でも安心しろ！　提督が貴公を守ってくれるはずだぞ！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60002, 4, 323, "今日の運勢は小吉！　いろんな事件が起こる一日になりそう！　　状況をよく観察してみようじゃないか！　まさに探偵のごとくね！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60002, 4, 324, "今日の運勢は小吉！　気の向くままに過ごすのが幸せへの近道！　美味い魚を食べて猫を眺めて、自然体で過ごすのはどうだい？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60002, 4, 325, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60002, 4, 326, "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60002, 4, 327, "今日の運勢は小吉！　周りに流されず、マイペースな一日を！　　ただし寝すぎには注意ぐうたらし過ぎず適度な運動を、だってさ～");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 2, 0, "", 0, 0, 2, 60002, 4, 328, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("カリン", 2, 0, 0, "", 0, 190804, 3, 60002, 0, 329, "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("カリン", 3, 0, 0, "", 7, 190804, 3, 60002, 0, 330, "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 1, 0, 0, "", 1, 105901, 4, 80005, 0, 331, "ささ、シェフィさまはこちらへ…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("シェフィ", 1, 0, 0, "", 1, 106401, 0, 80005, 0, 332, "コッコロさん、どこへ行くの…？\n…え？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 1, 0, 0, "", 1, 105801, 3, 80005, 0, 333, "『おめでとうございます！\nわ～パチパチー！』\n…という感じでどうですかね！？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 2, 0, 0, "", 1, 105901, 4, 80005, 0, 334, "ではわたくしも\n後ろからクラッカーを鳴らして\n応戦いたします\nああ、当日が待ち遠しいですね");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("シェフィ", 2, 0, 0, "", 1, 106401, 0, 80005, 0, 335, "ええと…\n二人とも何をしているの？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 1, 0, 0, "", 1, 106001, 1, 80005, 0, 336, "シェフィは初めてだもんね\nあの日が近いからって\n予行練習してんのよ\n気が早いわよね～");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("シェフィ", 3, 0, 0, "", 1, 106401, 0, 80005, 0, 337, "あの日…？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 2, 0, 0, "", 1, 105801, 3, 80005, 0, 338, "その通りです…\nいよいよ記念すべきあの日が\n近づいてきましたよ！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 3, 0, 0, "", 1, 105801, 3, 80005, 0, 339, "『プリンセスコネクト！\nRe:Dive』3.5周年！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("カリン", 1, 0, 0, "", 1, 190801, 2, 80005, 0, 340, "やばいですね☆");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 2, 0, 0, "", 1, 106001, 1, 80005, 0, 341, "って、なにしれっと\n参加してんのよカリン！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("カリン", 2, 0, 0, "", 1, 190801, 2, 80005, 0, 342, "ふふ、いいじゃないですか\n【ギルド管理協会】として\n私もみなさんと一緒に\nお祝いをさせてください！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("カリン", 3, 0, 0, "", 1, 190801, 2, 80005, 0, 343, "それに…記念日が近づけば\nとっておきの情報を\nお届けしますから♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 4, 0, 0, "", 1, 105801, 3, 80005, 0, 344, "というわけで\n今日からシェフィちゃんも一緒に\nカウントダウンしちゃいましょう！\n3.5周年まで…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("シェフィ", 4, 0, 0, "", 1, 106401, 0, 80005, 0, 345, "えっ！？　ええとええと…！\nあ、あと14日…！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 5, 0, 0, "", 2, 105801, 3, 80005, 0, 346, "3.5周年まであと13日\n豪華に、そして盛大に\nお祝いしたいですね☆");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 3, 0, 0, "", 2, 105901, 1, 80005, 0, 347, "豪華といえば前にとある貴族さまが\n舞踏会を開かれたそうで\n主さまがそのお手伝いを\nしておりました");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 4, 0, 0, "", 2, 105901, 1, 80005, 0, 348, "そこで美しい所作を学ばれたので\nわたくしも教えていただきました\nしゃなり、しゃなり…\nいかがでしょう？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 6, 0, 0, "", 2, 105801, 3, 80005, 0, 349, "すっごく上手ですよ☆\nまるでお姫様みたいです\nせっかくなのでわたしも\nしゃなりしゃなり");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 5, 0, 0, "", 2, 105901, 1, 80005, 0, 350, "さすがでございます\nペコリーヌさま");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 3, 0, 0, "", 2, 106001, 0, 80005, 0, 351, "いや、あんたは\nできなきゃダメでしょ\n本物のお姫様なんだし");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("シェフィ", 5, 0, 0, "", 2, 106401, 2, 80005, 0, 352, "しゃなり、しゃなり…\nふふ、案外できるものね");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 7, 0, 0, "", 2, 105801, 3, 80005, 0, 353, "わぁ！\nシェフィちゃん\n姿勢がとってもキレイです♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 6, 0, 0, "", 2, 105901, 1, 80005, 0, 354, "さ、次はキャルさまの\n番でございますよ");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 4, 0, 0, "", 2, 106001, 0, 80005, 0, 355, "あ、あたしはいいわよ！\nそういうの全然分かんないし…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("シェフィ", 6, 0, 0, "", 2, 106401, 2, 80005, 0, 356, "大丈夫\nやってみると結構楽しいわよ\nほらほらっ");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 5, 0, 0, "", 2, 106001, 0, 80005, 0, 357, "そ、そこまで言うなら…\nしゃなにゅわきゃあっ！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 6, 0, 0, "", 2, 106001, 0, 80005, 0, 358, "いたたた…もぉお～！\nなんであたしがこんな目に～！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 7, 0, 0, "", 2, 105901, 1, 80005, 0, 359, "まぁまぁ\n記念日までに練習すれば\nできるようになるかもしれません\nよろしければお教えいたしますよ");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 7, 0, 0, "", 2, 106001, 0, 80005, 0, 360, "よし！\nあんたたちができて\nあたしだけできないのはしゃくだし\nやってやろうじゃないの！！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 8, 0, 0, "", 3, 105901, 1, 80005, 0, 361, "3.5周年まであと12日\n記念日当日はもちろんですが\nその日を待つ時間というのも\n心躍ります");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 8, 0, 0, "", 3, 105801, 3, 80005, 0, 362, "分かりますっ！\nどんな特別な料理を食べられるのか\n考えるだけでワクワクしますよね！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 9, 0, 0, "", 3, 105901, 1, 80005, 0, 363, "特別な料理といえば、お祭りですね\nそういえば主さまは学生として\n聖学祭に参加されたこと\nがあるとか…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("シェフィ", 7, 0, 0, "", 3, 106401, 2, 80005, 0, 364, "聖学祭…って、なにかしら？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 10, 0, 0, "", 3, 105901, 1, 80005, 0, 365, "聖テレサ女学院の祭典ですよ\n二つのチームが競い合い\n青春を満喫するのが趣旨のようです");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 8, 0, 0, "", 3, 106001, 0, 80005, 0, 366, "それってカルミナの\nサプライズライブがあった\nお祭りよね？\nちょっと見たかったかも…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 9, 0, 0, "", 3, 106001, 0, 80005, 0, 367, "カルミナの後の\nライブも好評だったらしいじゃない\n確か、なかよし部\nだったっけ…？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 9, 0, 0, "", 3, 105801, 3, 80005, 0, 368, "なかよし部…\n仲が良さそうな名前で\nやばいですね☆");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 10, 0, 0, "", 3, 106001, 0, 80005, 0, 369, "いや、そのまんますぎでしょ…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("シェフィ", 8, 0, 0, "", 3, 106401, 2, 80005, 0, 370, "聖学祭かぁ…なんだか楽しそうね\nそれになかよし部の人たちも\n気になるわ");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 10, 0, 0, "", 3, 105801, 3, 80005, 0, 371, "それじゃあわたしたちも、\n負けじと仲良くしましょう♪\nほらほらシェフィちゃん、\nぎゅーっ☆　ですよ♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("シェフィ", 9, 0, 0, "", 3, 106401, 2, 80005, 0, 372, "えっ？　ええ…\nぎゅーっ…？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 11, 0, 0, "", 4, 106001, 0, 80005, 0, 373, "3.5周年まであと11日！？\nお祝いの準備進んでないんだけど…\nああ～昨日に戻りたいわ…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("シェフィ", 10, 0, 0, "", 4, 106401, 2, 80005, 0, 374, "そういえばそんな魔道具があるって\n聞いたことがあるわね\n確か、名前はタイムラーベ…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 12, 0, 0, "", 4, 106001, 0, 80005, 0, 375, "なにそれヤバいわね\nでも本当にあるんだったら\n準備よりももっとやり直したいこと\nたくさんあるんだけど…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 11, 0, 0, "", 4, 105901, 1, 80005, 0, 376, "もしや正月の件でしょうか…？\nあの時はお金がなくて\n苦労しましたから");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 13, 0, 0, "", 4, 106001, 0, 80005, 0, 377, "あれはそうね…\nお金を湯水のように使う\nぺコリーヌを止められたら\nあんな苦労をすることもなかったし");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 11, 0, 0, "", 4, 105801, 3, 80005, 0, 378, "そんなこともありましたね！\nやり直したいといえば\n『きゃるきゃる～ん♪』\nの方かと思いましたよ！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 14, 0, 0, "", 4, 106001, 0, 80005, 0, 379, "バラすんじゃないわよ\nこのアホリーヌ！\nシェフィはそれ\n知らないんだから！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 15, 0, 0, "", 4, 106001, 0, 80005, 0, 380, "こうなったらもう、なんとか\nタイムラーべを探し出して\n今の会話をなかったことに…\nって、どうしたのよシェフィ");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("シェフィ", 11, 0, 0, "", 4, 106401, 2, 80005, 0, 381, "うん…もし、私だったら…\n私が知らないみんなとの時間を\n見てみたいな、って…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 12, 0, 0, "", 4, 105801, 3, 80005, 0, 382, "シェフィちゃん！　タイムなんとか\nなんかなくても大丈夫ですよ！\n一緒の時間はこれから\nどんどん作ればいいんですから！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 12, 0, 0, "", 4, 105901, 1, 80005, 0, 383, "はい、今この瞬間も\n未来にはかけがえのない思い出に\nなっていることでしょう");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 16, 0, 0, "", 4, 106001, 0, 80005, 0, 384, "だからそんな顔しないの\nほら、お祝いの準備、進めるわよ！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("シェフィ", 12, 0, 0, "", 4, 106401, 2, 80005, 0, 385, "みんな…そうね\n最高のお祝いにして\n最高の思い出にしましょう！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 13, 0, 0, "", 5, 105801, 3, 80005, 0, 386, "3.5周年まで、あと10日！\nもうすぐですよー！\n楽しみすぎて、やばいですね！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 14, 0, 0, "", 5, 105801, 3, 80005, 0, 387, "そういえば…たくさんのおもちゃが\n子どもたちを襲うっていう\n騒動もありましたね…\nなんでも勝手に動き出したとか！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 15, 0, 0, "", 5, 105801, 3, 80005, 0, 388, "学校や孤児院とかに\nちっちゃなおもちゃが乗り込んで\n暴れたみたいですよ！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 17, 0, 0, "", 5, 106001, 0, 80005, 0, 389, "暴れたって言っても\n所詮はおもちゃじゃない\nどうせちっさいんでしょ？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 18, 0, 0, "", 5, 106001, 0, 80005, 0, 390, "だったら魔法で\n全部ぶっ飛ばせばいいじゃない！\n大したことないわよ！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("シェフィ", 13, 0, 0, "", 5, 106401, 2, 80005, 0, 391, "キャルってば…\n相手は子どもが\n大事にしてる物なのよ…？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 13, 0, 0, "", 5, 105901, 1, 80005, 0, 392, "乱暴な発言は\nめっ、でございますよ？\n子どもの教育に悪影響を\n与えてしまいますから…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 16, 0, 0, "", 5, 105801, 3, 80005, 0, 393, "キャルちゃんだって\n意外と猫グッズとか\nたっくさん集めてますよね？\nもしその子たちが暴れたら…？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 19, 0, 0, "", 5, 106001, 0, 80005, 0, 394, "う、うっさいわね！\n意外とか言うな！\n…ま、まあ確かに正面切って\n魔法でぶっ飛ばせないかもね…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 17, 0, 0, "", 5, 105801, 3, 80005, 0, 395, "あはは☆\nキャルちゃんのそういうところ\n好きですよ♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 18, 0, 0, "", 5, 105801, 3, 80005, 0, 396, "そんなキャルちゃんに猫グッズを\nプレゼントしちゃいますね！\n一部で流行っていた、たい焼き屋の\nおまけフィギュアですよ♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 19, 0, 0, "", 5, 105801, 3, 80005, 0, 397, "乱暴に扱ったりしないでくださいね\nひとりでに暴れ出しちゃうかも\nしれませんから！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("カリン", 4, 0, 0, "", 6, 190801, 2, 80005, 0, 398, "3.5周年まであと９日\nここでみなさんに\nビックニュースですよ！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("カリン", 5, 0, 0, "", 6, 190801, 2, 80005, 0, 399, "3.5周年を祝う生放送に\n去年と同様【美食殿】の\nみなさんにご出演いただくことが\n決定しましたー！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 20, 0, 0, "", 6, 106001, 1, 80005, 0, 400, "…えっ！？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 20, 0, 0, "", 6, 105801, 3, 80005, 0, 401, "本当ですか！？\nそれじゃ今から張り切って\n生放送中に食べる魔物料理を\n作らなくちゃですね☆");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("シェフィ", 14, 0, 0, "", 6, 106401, 0, 80005, 0, 402, "いや…がんばるところは\nそこじゃないような…\nというか去年も出演してたのね\nすごいじゃない！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 14, 0, 0, "", 6, 105901, 4, 80005, 0, 403, "はい。とても緊張しますが\nやりがいのある大役です\n一緒にがんばりましょうね\nシェフィさま");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("シェフィ", 15, 0, 0, "", 6, 106401, 0, 80005, 0, 404, "わ、私も出るの！？　でも…\n私も【美食殿】の一人だものね\nそれなら今からしっかり準備をして\nコンディションを整えないと…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 21, 0, 0, "", 6, 106001, 1, 80005, 0, 405, "ふふん♪\n生放送直前のカチコチになった\nシェフィの姿が楽しみだわ！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 21, 0, 0, "", 6, 105801, 3, 80005, 0, 406, "去年のキャルちゃんは\n緊張しすぎてずっと震えて\nかわいかったですもんね！\n今年もあの姿が見たいです！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 22, 0, 0, "", 6, 106001, 1, 80005, 0, 407, "ぶっ殺すぞ！？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("カリン", 6, 0, 0, "", 6, 190801, 2, 80005, 0, 408, "というわけで\n『プリンセスコネクト！\nRe:Dive』の魅力を\nお伝えする生放送をお楽しみに♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 15, 0, 0, "", 7, 105901, 1, 80005, 0, 409, "3.5周年まであと８日\nでございますよ\n楽しみですね、主さま");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 23, 0, 0, "", 7, 106001, 0, 80005, 0, 410, "そういえばあんた\n夏休みの宿題は\nきちんとやってるの？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("シェフィ", 16, 0, 0, "", 7, 106401, 2, 80005, 0, 411, "宿題…学校に通うのも\n楽しいことばかりじゃ\nないのね");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 22, 0, 0, "", 7, 105801, 3, 80005, 0, 412, "あれ？　そういえば\n少し前にルーセント学院で\n何かあったような…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 24, 0, 0, "", 7, 106001, 0, 80005, 0, 413, "ああ、始業式の日に\n大捕り物があったって\n話題になってたわね");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("シェフィ", 17, 0, 0, "", 7, 106401, 2, 80005, 0, 414, "夏休みを終わらせないって\n騒いでたっていう話？\nあれ本当なの？\n都市伝説だと思ってたわ");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 16, 0, 0, "", 7, 105901, 1, 80005, 0, 415, "その一件ならよく存じ上げて\nおります。ですから主さま\n次の機会にはわたくしと一緒に\nがんばりましょう");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("シェフィ", 18, 0, 0, "", 7, 106401, 2, 80005, 0, 416, "楽しそう…私も一緒にやりたいな\nママ…じゃ、じゃなくて！\nコッコロさん、私もまざっていい？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 17, 0, 0, "", 7, 105901, 1, 80005, 0, 417, "もちろんでございます\n仲良くお勉強いたしましょう");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 25, 0, 0, "", 7, 106001, 0, 80005, 0, 418, "しれっと教育ママが\n生まれてる…？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 23, 0, 0, "", 7, 105801, 3, 80005, 0, 419, "お勉強は大切ですよ！\nキャルちゃんも一緒に\nがんばりましょう！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 26, 0, 0, "", 7, 106001, 0, 80005, 0, 420, "え～～～～！？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 27, 0, 0, "", 8, 106001, 0, 80005, 0, 421, "3.5周年まであと７日〜♪\nふふ～ん♪\nふんふふ～ん♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("シェフィ", 19, 0, 0, "", 8, 106401, 2, 80005, 0, 422, "…\nねぇコッコロさん\nキャル、どうしちゃったの？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 18, 0, 0, "", 8, 105901, 1, 80005, 0, 423, "とてもご機嫌そうに\n鼻歌を歌ってらっしゃいますね\n耳も尻尾もリズミカルに\n揺れて…ノリノリでございます");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 24, 0, 0, "", 8, 105801, 3, 80005, 0, 424, "あれはですね！\nカルミナのみなさんの\nサマーライブのチケットが\n手に入ったからなんですよ♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("シェフィ", 20, 0, 0, "", 8, 106401, 2, 80005, 0, 425, "ええっ！？\nあのライブのチケットって確か\n入手困難だったんじゃ…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 28, 0, 0, "", 8, 106001, 0, 80005, 0, 426, "ふふふふ～ん♪\nしゃいにんぐふゅ～ちゃ～♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 19, 0, 0, "", 8, 105901, 1, 80005, 0, 427, "なるほど…\nキャルさまがあのように\nなってしまうのも頷けますね");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("シェフィ", 21, 0, 0, "", 8, 106401, 2, 80005, 0, 428, "…カルミナの\n『Shining Future』\nの中に、シャイニングフューチャー\nって歌詞はなかったはずだけど…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 29, 0, 0, "", 8, 106001, 0, 80005, 0, 429, "ふんふ…はっ！？\nあっ、あんたたち！\nなに人の鼻歌を\n勝手に聞いてるのよ！？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 30, 0, 0, "", 8, 106001, 0, 80005, 0, 430, "こっ、これは別に\nカルミナの曲じゃなくて…\nあたしのオリジナルソングよ！\n聞いたらお金取るわよっ！？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 25, 0, 0, "", 8, 105801, 3, 80005, 0, 431, "えへへ、キャルちゃん\nカルミナのサマーライブ\n思いっきり楽しみましょうね☆");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 31, 0, 0, "", 8, 106001, 0, 80005, 0, 432, "も～っ！\n生暖かい目で見るな～～！\nぶっ殺すわよ～～～！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("シェフィ", 22, 0, 0, "", 9, 106401, 0, 80005, 0, 433, "3.5周年まであと６日\n３人も言ってたけど\nあっという間だったわね");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("シェフィ", 23, 0, 0, "", 9, 106401, 0, 80005, 0, 434, "…えっ？\n生放送の意気込み！？\n突然なに！？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("シェフィ", 24, 0, 0, "", 9, 106401, 0, 80005, 0, 435, "ええと…\nとにかく失敗しないように\n台本たくさん読んで…\nそれから…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("シェフィ", 25, 0, 0, "", 9, 106401, 0, 80005, 0, 436, "えっ！？\nトラブルがあったら\nどうするのかって！？\nそ、それは…！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("シェフィ", 26, 0, 0, "", 9, 106401, 0, 80005, 0, 437, "とにかく落ち着いて\nスタッフさんの指示を聞いて…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("シェフィ", 27, 0, 0, "", 9, 106401, 0, 80005, 0, 438, "それから、それから…！\nぅ、ぅぅっ…！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("シェフィ", 28, 0, 0, "", 9, 106401, 0, 80005, 0, 439, "―びえぇぇ～んっ！！！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 20, 0, 0, "", 9, 105901, 1, 80005, 0, 440, "どうされましたか、シェフィさま");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("シェフィ", 29, 0, 0, "", 9, 106401, 0, 80005, 0, 441, "ううっ…ひっく…\nママッ…！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 21, 0, 0, "", 9, 105901, 1, 80005, 0, 442, "よしよし、緊張されてるのですね\nママが傍におりますよ\n主さま、少々お待ちくださいませ");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 22, 0, 0, "", 9, 105901, 1, 80005, 0, 443, "…ふぅ\nシェフィさま\n生放送への意気込み\nお話しできそうですか？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("シェフィ", 30, 0, 0, "", 9, 106401, 0, 80005, 0, 444, "…ううっ\n恥ずかしすぎて\nもう生放送出られない…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 23, 0, 0, "", 10, 105901, 0, 80005, 0, 445, "主さま\n3.5周年まであと５日\nでございます");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 24, 0, 0, "", 10, 105901, 0, 80005, 0, 446, "ですがその前に\n生放送がありますね");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 25, 0, 0, "", 10, 105901, 0, 80005, 0, 447, "…生放送に向けた\n意気込みでございますか？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 26, 0, 0, "", 10, 105901, 0, 80005, 0, 448, "もちろんわたくしは\n主さまのために\n全身全霊を込めて\n楽しい時間をご提供いたします");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 27, 0, 0, "", 10, 105901, 0, 80005, 0, 449, "…と、意気込んでみましたが\nうまくできるか少し不安です\n声が小さくなったり\n間違えてしまったらどうしようと…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 28, 0, 0, "", 10, 105901, 0, 80005, 0, 450, "ですがわたくしは\n一人ではありません\n【美食殿】のみなさまが\nおります");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 29, 0, 0, "", 10, 105901, 0, 80005, 0, 451, "どんな困難も\nみなさまと一緒なら\n乗り越えられると\nわたくし、確信しております");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 30, 0, 0, "", 10, 105901, 0, 80005, 0, 452, "それに今回はシェフィさまも\nご参加されるわけですし\nママとしてお手本に\nならねばなりませんね");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 31, 0, 0, "", 10, 105901, 0, 80005, 0, 453, "主さま。3.5周年も\n食卓を囲んでいる時のような\n楽しい思い出を\n一緒に作りましょうね");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 26, 0, 0, "", 11, 105801, 0, 80005, 0, 454, "3.5周年まであと４日！\nやばいですね☆");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 32, 0, 0, "", 11, 106001, 1, 80005, 0, 455, "はいはい、やばいわね\nというかもうすぐ生放送だけど\nあんた準備はできてるの？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 27, 0, 0, "", 11, 105801, 0, 80005, 0, 456, "ばっちりオイッスー☆　ですよ！\n『王家の装備』も全開です！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 28, 0, 0, "", 11, 105801, 0, 80005, 0, 457, "あとは前回を超える\n最高のオイッスー☆　を\nお届けできるように\n練習あるのみですかね！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 29, 0, 0, "", 11, 105801, 0, 80005, 0, 458, "キャルちゃんは\nどうなんですか？\nオイッスー☆　を\n毎日ちゃんと練習してますか？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 33, 0, 0, "", 11, 106001, 1, 80005, 0, 459, "はぁ！？　なんであたしまで\nその変なあいさつを練習しないと\nいけないのよ！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 30, 0, 0, "", 11, 105801, 0, 80005, 0, 460, "もしかしてやってないんですか？\nダメですよキャルちゃん！\nシェフィちゃんだってちゃんと\n毎日練習してるのに！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 31, 0, 0, "", 11, 105801, 0, 80005, 0, 461, "こうなったらわたしがキャルちゃん\nにオイッスー☆　の『オ』から\nしっかり教えてあげないと！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 32, 0, 0, "", 11, 105801, 0, 80005, 0, 462, "オイッスー☆　の『オ』は\n口を大きく開いて〜\nドラゴンの骨つき肉に\nかぶりつく直前をイメージして〜");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 34, 0, 0, "", 11, 106001, 1, 80005, 0, 463, "だからあたしはやるなんて一言も…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 33, 0, 0, "", 11, 105801, 0, 80005, 0, 464, "オイッスー☆");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 35, 0, 0, "", 11, 106001, 1, 80005, 0, 465, "聞きなさいっての！！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("シェフィ", 31, 0, 0, "", 12, 106401, 2, 80005, 0, 466, "3.5周年まであと３日…\nそして生放送は、明日…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("シェフィ", 32, 0, 0, "", 12, 106401, 2, 80005, 0, 467, "もっと台本を\n読み込んでおかないと…\nあと発声練習も…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("シェフィ", 33, 0, 0, "", 12, 106401, 2, 80005, 0, 468, "おいっす！\n…こうじゃなかったわね\nええと確か…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("シェフィ", 34, 0, 0, "", 12, 106401, 2, 80005, 0, 469, "うう…あんなに練習したのに\nこれじゃみんなに迷惑かけちゃう…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("シェフィ", 35, 0, 0, "", 12, 106401, 2, 80005, 0, 470, "【美食殿】の一員として\n認められるように\n頑張らないといけないのに\nよし、もう一度…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 34, 0, 0, "", 12, 105801, 3, 80005, 0, 471, "やってますねシェフィちゃん！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 36, 0, 0, "", 12, 106001, 0, 80005, 0, 472, "なによ、考えることは\nみんな同じってこと？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 32, 0, 0, "", 12, 105901, 1, 80005, 0, 473, "ふふ。シェフィさま\nよろしければ\n仲間に入れてください\n一緒にやればきっとうまくできます");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("シェフィ", 36, 0, 0, "", 12, 106401, 2, 80005, 0, 474, "みんな…もちろんよ！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 35, 0, 0, "", 12, 105801, 3, 80005, 0, 475, "それじゃ明日の生放送に向けて\n一緒に練習がんばりましょう☆");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("カリン", 7, 0, 0, "", 13, 190801, 2, 80005, 0, 476, "3.5周年まであと２日と\n迫った本日は…\nいよいよ生放送です！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("カリン", 8, 0, 0, "", 13, 190801, 2, 80005, 0, 477, "【美食殿】のみなさんが\n準備に入られていますので\nそれぞれ繋いでみましょう！\nまずはペコリーヌさんです！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 36, 0, 0, "", 13, 105801, 3, 80005, 0, 478, "今日もオイッスー☆\nみんなで一緒に\nオイッスー☆");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("カリン", 9, 0, 0, "", 13, 190801, 2, 80005, 0, 479, "お手本になるような\n発声練習ですね！\nそれでは続いてコッコロさん");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 33, 0, 0, "", 13, 105901, 4, 80005, 0, 480, "はじめちょろちょろ\nなかぱっぱ～♪\n生放送前にみなさまで\n食卓を囲んで英気を養いましょう");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("カリン", 10, 0, 0, "", 13, 190801, 2, 80005, 0, 481, "さすが【美食殿】のママですね！\nそしてキャルさんは…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 37, 0, 0, "", 13, 106001, 1, 80005, 0, 482, "大丈夫…大丈夫…\nあたしは大丈夫…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("カリン", 11, 0, 0, "", 13, 190801, 2, 80005, 0, 483, "す、すごく震えてますけど\n大丈夫でしょうか…？\nそして今回初参加となる\n期待の新人シェフィさん！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("シェフィ", 37, 0, 0, "", 13, 106401, 0, 80005, 0, 484, "あうー？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("みんな", 37, 0, 1, "105901,34,106001,38,190801,12", 13, 105801, 4, 80005, 0, 485, "あ…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 39, 0, 0, "", 13, 106001, 1, 80005, 0, 486, "ちょ、ちょっとシェフィ！？\nなに赤ちゃん返りしてん―\nあ痛ぁっ！\n噛んだわこいつーっ！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 35, 0, 0, "", 13, 105901, 4, 80005, 0, 487, "シェフィさま\n緊張しすぎて\nしまったのでしょうね…\nおーよしよし…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 38, 0, 0, "", 13, 105801, 3, 80005, 0, 488, "シェフィちゃん！\nそういう時は手のひらに\n好きな文字を書いて\n食べちゃうといいですよ！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("カリン", 13, 0, 0, "", 13, 190801, 2, 80005, 0, 489, "…ええと、現場からは以上です！\n生放送はもうすぐ！\n特別なお知らせもありますので\nどうぞお楽しみに！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("シェフィ", 38, 0, 0, "", 14, 106401, 2, 80005, 0, 490, "3.5周年の記念日も\nいよいよ明日ね\n…って、あれ？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 39, 0, 0, "", 14, 105801, 3, 80005, 0, 491, "記念日はこの森へ行きませんか！？\nすっごく美味しいお肉が\nとれるんです☆");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 40, 0, 0, "", 14, 106001, 0, 80005, 0, 492, "うげ、またあの魔物？\nさすがに今度は\n丸焼きとかはやめてよね…\n見た目がヤバすぎるし");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 36, 0, 0, "", 14, 105901, 1, 80005, 0, 493, "ふふ、キャルさまもだいぶ\n魔物肉に慣れてきたようですね\n調理ならばわたくしに\nお任せください♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("シェフィ", 39, 0, 0, "", 14, 106401, 2, 80005, 0, 494, "みんな、何をしているの？\nなんだか、\n不穏な単語が聞こえたんだけど…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 40, 0, 0, "", 14, 105801, 3, 80005, 0, 495, "シェフィちゃん！\n明日の食べ尽くしツアーの\n相談をしてたんですよ！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 37, 0, 0, "", 14, 105901, 1, 80005, 0, 496, "はい。明日は【美食殿】の\n一員となったシェフィさまと一緒に\n初めて記念日を過ごすのですから\n特別なコース料理を用意しませんと");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 41, 0, 0, "", 14, 106001, 0, 80005, 0, 497, "ふふん♪\n猫の毛も逆立つような\nあれこれを食べさせられるから\n覚悟しておいたほうがいいわよ？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("シェフィ", 40, 0, 0, "", 14, 106401, 2, 80005, 0, 498, "へ、へぇ…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 41, 0, 0, "", 14, 105801, 3, 80005, 0, 499, "期待しててくださいね！\nシェフィちゃんとあなたに\n『おいちー！』って喜んでもらえる\n最高のごちそうを準備しますから☆");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("シェフィ", 41, 0, 0, "", 14, 106401, 2, 80005, 0, 500, "ええと…その…うーんと…\nわ、分かったわ\n私も【美食殿】の一員だもの");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("シェフィ", 42, 0, 0, "", 14, 106401, 2, 80005, 0, 501, "それにあなたも一緒みたいだし\n最後までがんばってみるわ…！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 42, 0, 0, "", 15, 105801, 3, 80005, 0, 502, "それではいつものあれを\nやりましょう！\n準備はいいですかっ？\n…いっきますよー！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 42, 0, 0, "", 15, 106001, 1, 80005, 0, 503, "祝！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 38, 0, 0, "", 15, 105901, 4, 80005, 0, 504, "3.5周年！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("シェフィ", 43, 0, 0, "", 15, 106401, 0, 80005, 0, 505, "おめでとうございます！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("カリン", 14, 0, 0, "", 15, 190801, 2, 80005, 0, 506, "この日を迎えられたのも\nあなたが『プリンセスコネクト！\nRe:Dive』を遊んでくれている\nおかげです！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("カリン", 15, 0, 0, "", 15, 190801, 2, 80005, 0, 507, "ということで\nみなさんからあなたへ一言ずつ\n感謝の言葉をどうぞ！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 43, 0, 0, "", 15, 105801, 3, 80005, 0, 508, "感謝の言葉ですか？　わたしは\n毎日あなたに感謝してますよ\n【美食殿】の仲間になってくれて…\n一緒にごはんを食べてくれて…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 44, 0, 0, "", 15, 105801, 3, 80005, 0, 509, "なにより今でもわたしをぺコリーヌ\nって呼んでくれてありがとうござい\nます。これからもわたしはあなたの\nぺコリーヌです！　やばいですね☆");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 43, 0, 0, "", 15, 106001, 1, 80005, 0, 510, "えっ！？　今度はあたし！？\n感謝って…まぁ、そうね…\nあんたにはあたしが\n辛くて苦しい時に助けてもらって…");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("キャル", 44, 0, 0, "", 15, 106001, 1, 80005, 0, 511, "でもおかげで色々吹っ切れたわ！\nもしあんたが困ってたら今度は\nあたしが助けてあげるから\nその時はちゃんと言いなさいよ！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("シェフィ", 44, 0, 0, "", 15, 106401, 0, 80005, 0, 512, "ええと…次は私ね\n実はよく覚えてないのだけど\n空から落ちてきた私を受け止めて\nくれたのはあなたなのでしょう？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("シェフィ", 45, 0, 0, "", 15, 106401, 0, 80005, 0, 513, "最初に私を見つけてくれたのが\nあなたで良かったって思ってるの\nおかげで私は居場所を見つけたから\nあなたがくれたもの、大切にするわ");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 39, 0, 0, "", 15, 105901, 4, 80005, 0, 514, "では最後にわたくしから…\nわたくしは主さまの元を離れた日…\nこのように記念日をお祝いする\nことは二度とないと思っていました");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 40, 0, 0, "", 15, 105901, 4, 80005, 0, 515, "ですが、主さまはわたくしを\n迎えにきてくださり、今もこうして\nおそばにおいてくださっています\nわたくしにとって毎日が記念日です");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("コッコロ", 41, 0, 0, "", 15, 105901, 4, 80005, 0, 516, "わたくしたちの物語は\nまだ続いていきます\nこれからも一緒にページを\n作っていきましょうね");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("ペコリーヌ", 45, 0, 0, "", 15, 105801, 3, 80005, 0, 517, "それじゃ\nいつものあれでしめますよ！\nみんなで一緒に、さんはい☆");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("みんな", 46, 0, 1, "105901,42,106001,45,106401,46,190801,16", 15, 105801, 4, 80005, 0, 518, "これからも\nよろしくおねがいします！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("カリン", 1, 1, 0, "", 0, 190804, 1, 60003, 0, 519, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60003, 1, 520, "今日の運勢は姫吉！　どんな不可能も可能にできちゃう！　　　　仲間と食卓を囲めばさらに運気上昇よ！　ただし魔物料理以外！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60003, 1, 521, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60003, 1, 522, "今日の運勢は姫吉！　羽が生えたように飛躍できる絶好調の運気！新しいことに挑戦するなら今しかないわ！　やってみましょう！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60003, 1, 523, "今日の運勢は姫吉！　精霊も新年の幸福をお祝いしています！　　主さまの笑顔が見られて、わたくしも幸せいっぱいでございます♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 2, 0, "", 0, 0, 2, 60003, 1, 524, "今日の運勢は姫吉！　やること全てが上手くいく最高の一日！　　あなたの活躍に先生もにっこりです。いい子いい子♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60003, 2, 525, "今日の運勢は大吉！　お裁縫好きな女の子のお墨付きです！　　　お世話したがりの妹にめいっぱい愛されちゃうかも！？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60003, 2, 526, "今日の運勢は大吉！　ラッキーカラーは赤！　　　　　　　　　　湧き上がるアイデアの数々に、高笑いが止まりませんわ！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60003, 2, 527, "今日の運勢は大吉！　ラッキーカラーは鮮やかな黄色！　　　　　さらにシマシマのアイテムを持つと、運気倍増らしいッスよ♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60003, 2, 528, "今日の運勢は大吉！　秘められた力が開花するでしょう！　　　　じゃあ、私の龍眼と力比べしてみようか？　ふふ、冗談だよ～");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 2, 0, "", 0, 0, 2, 60003, 2, 529, "今日の運勢は大吉！　悩んでいたことが解決しそうです！　　　　たまには勉強も仕事も忘れて、心あたたまる家族と過ごしましょ！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60003, 3, 530, "今日の運勢は中吉！　家の中でゆっくり過ごすのがよさそう！　　ラッキーアイテムはゲームよ！　ベストスコアを更新できるかも？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60003, 3, 531, "今日の運勢は中吉！　上手くなりたいことに積極的に挑戦してみてサックス好きの妹が、手取り足取りサポートしてくれるかも～？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60003, 3, 532, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60003, 3, 533, "今日の運勢は中吉！　日頃の努力が報われます！　　　　　　　　親しい友人がいるなら報告してみて。一緒に喜んでくれるはずだ。");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60003, 3, 534, "今日の運勢は中吉！　ラッキーアイテムはかわいいもの！　　　　ア、アタシはかわいいじゃなくてキレイだってば！　も～っ！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60003, 3, 535, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 2, 0, "", 0, 0, 2, 60003, 3, 536, "今日の運勢は中吉！　悩まず行動すれば強運に恵まれる！　　　　守るよりも攻めるが吉だ！　勝負事をするにはいい運勢だな！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60003, 4, 537, "今日の運勢は小吉！　まだ見ぬ危険が隠れているかも！　　　　　でも安心しろ！　提督が貴公を守ってくれるはずだぞ！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60003, 4, 538, "今日の運勢は小吉！　いろんな事件が起こる一日になりそう！　　状況をよく観察してみようじゃないか！　まさに探偵のごとくね！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60003, 4, 539, "今日の運勢は小吉！　気の向くままに過ごすのが幸せへの近道！　美味い魚を食べて猫を眺めて、自然体で過ごすのはどうだい？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60003, 4, 540, "今日の運勢は小吉！　難しい頼み事をされるかもしれません！　　うまくやれればきっと成長につながります、挑戦してみましょう！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60003, 4, 541, "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60003, 4, 542, "今日の運勢は小吉！　周りに流されず、マイペースな一日を！　　ただし寝すぎには注意ぐうたらし過ぎず適度な運動を、だってさ～");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 2, 0, "", 0, 0, 2, 60003, 4, 543, "今日の運勢は小吉！　無理は禁物、お家で静かにしていましょう　プロぼっちの私が、お家での過ごし方……教えてあげましょうか？");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("カリン", 2, 0, 0, "", 0, 190804, 3, 60003, 0, 544, "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("カリン", 3, 0, 0, "", 7, 190804, 3, 60003, 0, 545, "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("カリン", 1, 1, 0, "", 0, 190804, 1, 60004, 0, 546, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60004, 1, 547, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60004, 1, 548, "今日の運勢は姫吉！　羽が生えたように飛躍できる絶好調の運気！新しいことに挑戦するなら今しかないわ！　やってみましょう！");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60004, 1, 549, "今日の運勢は姫吉！　宴でも開きたくなるほどの絶対幸運！\nイメチェンでもしてみるのはどうだ？　ぴょんぴょん、なんてな♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60004, 1, 550, "今日の運勢は姫吉！　ずっと祈り続けていた願いが叶うかも！\nきっとあんたならたどり着けるわ、ハッピーエンドのその先に");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60004, 1, 551, "今日の運勢は姫吉！　やること全てが上手くいく最高の一日！　　あなたの活躍に先生もにっこりです。いい子いい子♪");
INSERT INTO `v1_8d315da1f41da15ab5768ec99230e923726c852670cf000ce16cd691183635fc` VALUES ("", 0, 1, 0, "", 0, 0, 2, 60004, 2, 552, "今日の運勢は大吉！　悩んでいたことが解決しそうです！　　　　たまには勉強も仕事も忘れて、心あたたまる家族と過ごしましょ！");
