INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (1, 1800, 10, 0, 1, 11001, "ＴＰ上昇＋5", 11001, "迷宮内で見つかったイヤリング。\n装備者に活力加速を与える。", 1, "加速の玉飾り");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (1, 1800, 10, 0, 1, 11002, "ダメージを受けるたびＴＰ100回復（最大１回）", 11002, "迷宮内で見つかった円盾。受けた\n攻撃をエネルギーに変換できる。", 1, "水蓮の盾");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (1, 1800, 10, 0, 1, 11003, "バトル開始時ＴＰ100回復", 11003, "迷宮内で見つかった腕輪。戦いの\n始まりとともに、装備者にエネル\nギーを与える。", 1, "青輝の環");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (1, 1800, 10, 0, 1, 11004, "ＴＰ消費軽減＋2", 11004, "迷宮内で⾒つかった杖。装備者に\n力を温存させる効果をもたらす。", 1, "冷玉の杖");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (1, 1800, 10, 0, 1, 11005, "重複可能の行動速度＋10％", 11005, "迷宮内で⾒つかった靴。装備した\n者は迅速に動くことができる。", 1, "風渡りの靴");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (2, 1800, 10, 0, 1, 12001, "状態異常命中＋20", 12001, "迷宮内で見つかった首飾り。\n敵に異常を与えやすくなる。", 1, "紫晶ビザリオ");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (2, 1800, 10, 0, 1, 12002, "敵全体に呪い付与（毎秒【弱体】数×500ダメージ）", 12002, "迷宮内で見つかった杖。所持する\nだけで敵を呪うことができる。", 1, "魔杖メジナエ");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (2, 1800, 10, 0, 1, 12003, "敵全体に毒付与（毎秒【弱体】数×500ダメージ）", 12003, "迷宮内で見つかった短剣。持つだ\nけで敵を毒にすることができる。", 1, "暗剣ポワゾン");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (2, 1800, 10, 0, 1, 12004, "敵の物理/魔法攻撃力－10％", 12004, "迷宮内で見つかった帽子。装備す\nると敵の攻撃を弱体させる。", 1, "破守帽ディスペル");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (2, 1800, 10, 0, 1, 12005, "敵の物理/魔法防御力－10％", 12005, "迷宮内で見つかった刀。\n敵の守りを断ち崩す力がある。", 1, "崩刀ヤマツキ");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (3, 1800, 10, 0, 1, 13001, "敵の物理/魔法被クリダメ＋10％", 13001, "迷宮内で見つかった槍。鋭い一撃\nで敵に深い傷を負わせる。", 1, "キラースピア");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (3, 1800, 10, 0, 1, 13002, "物理/魔法クリダメ＋10％", 13002, "迷宮内で見つかった刀。致命の攻\n撃を与えたときの威力を高める。", 1, "ハナミノカタナ");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (3, 1800, 10, 0, 1, 13003, "通常攻撃強化＋30", 13003, "迷宮内で見つかった籠手。強化\nされた一撃を放つことができる。", 1, "クリーヴフィスト");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (3, 1800, 10, 0, 1, 13004, "物理/魔法クリティカル＋10％", 13004, "迷宮内で⾒つかった⼸。装備する\nと致命の一射を与えやすくなる。", 1, "クリティカルアーク");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (3, 1800, 10, 0, 1, 13005, "ＨＰ吸収＋10", 13005, "迷宮内で見つかった斧。攻撃と共\nに敵の体力を吸い取る力を宿す。", 1, "アブゾーブハチェット");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (4, 1800, 10, 0, 1, 14001, "バフ強化＋30", 14001, "迷宮内で見つかった耳飾り。装備\n者の強化効果を高める。", 1, "強化のジェム");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (4, 1800, 10, 0, 1, 14002, "物理/魔法攻撃力アップ\n（先頭キャラの物理防御力×5）", 14002, "迷宮内で⾒つかった鎧。同朋の\n強靭を、自身の攻撃に変換する。", 1, "抗鉄のプレート");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (4, 1800, 10, 0, 1, 14003, "物理/魔法攻撃力アップ\n（先頭キャラの魔法防御力×5）", 14003, "迷宮内で⾒つかったドレス。装備\n者の抗魔の才を攻撃⼒に換える。", 1, "抗魔のドレス");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (4, 1800, 10, 0, 1, 14004, "物理/魔法攻撃力＋20％", 14004, "迷宮内で見つかった大剣。装備者\nの攻撃力を強化する。", 1, "蛇紋のツルギ");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (4, 1800, 10, 0, 1, 14005, "物理/魔法防御力＋10％", 14005, "迷宮内で見つかった鎧。装備者の\n守りの力を高めることができる。", 1, "防守のメイル");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (5, 1800, 10, 0, 1, 15001, "回復強化＋10", 15001, "迷宮内で⾒つかった杖。\n癒しの⼒を高めてくれる。", 1, "回生杖");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (5, 1800, 10, 0, 1, 15002, "ＨＰ＋10％", 15002, "迷宮内で見つかった指輪。\n装備者の生命力を強化する。", 1, "豊命輪");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (5, 1800, 10, 0, 1, 15003, "20秒ごとに物理無効バリア展開（【守備】数×500無効）", 15003, "迷宮内で見つかった鎧。\n装備者を物理攻撃から守る。", 1, "鉄防甲");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (5, 1800, 10, 0, 1, 15004, "20秒ごとに魔法無効バリア展開（【守備】数×500無効）", 15004, "迷宮内で見つかったローブ。\n装備者を魔法攻撃から守る。", 1, "魔封衣");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (5, 1800, 10, 0, 1, 15005, "継続ＨＰ回復（毎秒【守備】数×500回復）", 15005, "迷宮内で見つかった腕輪。\n装備者に再生の力を与える。", 1, "再生ノ腕輪");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (1, 2800, 10, 0, 2, 21001, "ＴＰ上昇＋10", 21001, "迷宮内部で発見された耳飾りの形\nをした遺物。流水の激しさのよう\nに、活力加速を増幅させる。", 2, "流烈の耳飾り");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (1, 2800, 10, 0, 2, 21002, "ダメージを受けるたびＴＰ100回復（最大２回）", 21002, "迷宮内部で発見された円盾の形を\nした遺物。装備者に星の癒しを与\nえ攻撃をエネルギーに変換する。", 2, "星涙の輝盾");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (1, 2800, 10, 0, 2, 21003, "バトル開始時ＴＰ200回復", 21003, "迷宮内部で発⾒された腕輪の遺物\nあしらわれた蒼珠が、敵を感知し\n力を授ける。", 2, "蒼珠の腕輪");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (1, 2800, 10, 0, 2, 21004, "ＴＰ消費軽減＋4", 21004, "迷宮内部で発⾒された杖の遺物。\n封じられた星々の神秘が、力の消\n耗を肩代わりしてくれる。", 2, "星明りの儀杖");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (1, 2800, 10, 0, 2, 21005, "重複可能の行動速度＋20％", 21005, "迷宮内部で発見された、翼の生え\nた靴形の遺物。装備した者に疾風\nの力を施し、行動を速める。", 2, "疾風の翼靴");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (2, 2800, 10, 0, 2, 22001, "状態異常命中＋40", 22001, "迷宮内部で発⾒された、⾸飾りの\n遺物。宝⽯の輝きで敵を虜にし、\n怪異をもたらすという。", 2, "闇至宝アノマリーラ");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (2, 2800, 10, 0, 2, 22002, "敵全体に呪い付与（毎秒【弱体】数×1000ダメージ）", 22002, "迷宮内部で発見された、杖の形を\nした遺物。杖先のコアから流れる\n邪気が敵に呪いを付与する。", 2, "邪眼杖ドゥーム");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (2, 2800, 10, 0, 2, 22003, "敵全体に毒付与（毎秒【弱体】数×1000ダメージ）", 22003, "迷宮内部で発見された、短剣の形\nをした遺物。刃に仕込まれた毒が\n気化し、接敵と共に毒を与える。", 2, "影刃剣プラール");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (2, 2800, 10, 0, 2, 22004, "敵の物理/魔法攻撃力－20％", 22004, "迷宮内部で発見された、帽子の形\nをした遺物。絡みつく茨の様に、\n敵の攻撃力を阻害する。", 2, "薔薇飾帽イレース");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (2, 2800, 10, 0, 2, 22005, "敵の物理/魔法防御力－20％", 22005, "迷宮内部で発⾒された、⼑の遺物\n血で⾚く染まった⼑⾝で、敵の装\n甲をたやすく断ち割る。", 2, "斬赤刃ディバイド");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (3, 2800, 10, 0, 2, 23001, "敵の物理/魔法被クリダメ＋20％", 23001, "迷宮内部で発見された、槍の遺物\n特殊な形の穂先が、敵により深い\n傷を刻み込む。", 2, "フェイタリティ");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (3, 2800, 10, 0, 2, 23002, "物理/魔法クリダメ＋20％", 23002, "迷宮内部で発見された、刀の形を\nした遺物。秋の紅を映す美しき刃\nは致命の攻撃をさらに強化する。", 2, "クレナイノタチ");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (3, 2800, 10, 0, 2, 23003, "通常攻撃強化＋60", 23003, "迷宮内部で発⾒された籠⼿の遺\n物。装備者は研ぎ澄まされた強\n力な一撃を放つことができる。", 2, "ラプチャーガントレット");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (3, 2800, 10, 0, 2, 23004, "物理/魔法クリティカル＋20％", 23004, "迷宮内部で発⾒された、⼸の遺物\n装備者は烈⾵の⼒を授かり、獲物\nの急所を貫きやすくなる。", 2, "スカイストライク");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (3, 2800, 10, 0, 2, 23005, "ＨＰ吸収＋20", 23005, "迷宮内部で発⾒された斧の遺物。\n繰り出される⼀撃は、掠めるだけ\nで獲物の魂を吸い取る。", 2, "インヘイルアックス");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (4, 2800, 10, 0, 2, 24001, "バフ強化＋60", 24001, "迷宮内部で発見された、耳飾りの\n形をした遺物。魔石に込められた\n魔力が強化の力を増幅させる。", 2, "進化のイヤリング");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (4, 2800, 10, 0, 2, 24002, "物理/魔法攻撃力アップ\n（先頭キャラの物理防御力×10）", 24002, "迷宮内部で発⾒された、鎧の遺物\n紅き鋼の加護は、守備力を攻撃力\nへと変換する力を持つ。", 2, "紅反鋼のアーマー");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (4, 2800, 10, 0, 2, 24003, "物理/魔法攻撃力アップ\n（先頭キャラの魔法防御力×10）", 24003, "迷宮内部で発⾒された、ドレスの\n遺物。精霊の加護が、防魔結界か\nら力を集めてくれる。", 2, "聖減術のテラドレス");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (4, 2800, 10, 0, 2, 24004, "物理/魔法攻撃力＋40％", 24004, "迷宮内部で発見された、大剣の形\nをした遺物。湧き出る炎の力が、\n攻撃の能力を大きく高める。", 2, "炎王のグレートソード");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (4, 2800, 10, 0, 2, 24005, "物理/魔法防御力＋20％", 24005, "迷宮内部で発⾒された鎧の遺物。\n千年咲く桜の如く、盤石な堅牢さ\nを装備者に与える。", 2, "桜堅牢のコート");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (5, 2800, 10, 0, 2, 25001, "回復強化＋20", 25001, "迷宮内部で発⾒された杖の遺物。\n装備者に⼤樹の恵みを施し、治癒\n術の才覚を授けてくれる。", 2, "創世緑杖");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (5, 2800, 10, 0, 2, 25002, "ＨＰ＋20％", 25002, "迷宮内部で発見された、指輪の形\nをした遺物。装備者を天空の護り\nで包み込み生命力を増幅させる。", 2, "空命護珠");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (5, 2800, 10, 0, 2, 25003, "20秒ごとに物理無効バリア展開（【守備】数×1000無効）", 25003, "迷宮内部で発見された、鎧の形を\nした遺物。高度な技術で精錬され\nた鋼は、物理攻撃から身を護る。", 2, "鋼装堅鎧");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (5, 2800, 10, 0, 2, 25004, "20秒ごとに魔法無効バリア展開（【守備】数×1000無効）", 25004, "迷宮内部で発⾒された、ローブの\n遺物。神樹の蔦が織り込まれ、装\n備者を魔法から護る。", 2, "魔護霊装");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (5, 2800, 10, 0, 2, 25005, "継続ＨＰ回復（毎秒【守備】数×1000回復）", 25005, "迷宮内部で発⾒された、腕輪の遺\n物。溢れ出す⾃然の⼒は装備者に\n生命の息吹をもたらす。", 2, "緑護ノ盾輪");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (0, 3800, 10, 0, 0, 30001, "属性ボーナス＋100％", 30001, "迷宮中枢部に隠された黎明の秘宝\nデータ保管のため属性の⼒の断⽚\nが封じられており、内なるアスト\nラルを著しく上昇させる。", 3, "エレメントサージオーブ");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (1, 3800, 10, 0, 3, 31001, "ＴＰ上昇＋15", 31001, "迷宮の奥深くに眠っていた月を象\nる耳飾り。内部から発する魔力は\n装備者に蝶のような瞬麗さを授け\n活力加速を大きく上昇させる。", 3, "瞬閃蝶の月飾り");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (1, 3800, 10, 0, 3, 31002, "ダメージを受けるたびＴＰ100回復（最大３回）", 31002, "迷宮の奥深くに眠る宝石を飾った\n円盾。海のような青き魔力は装備\n者に庇護を与え、迫り来る脅威す\nらエネルギーへと昇華させる。", 3, "海宝華の煌盾");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (1, 3800, 10, 0, 3, 31003, "バトル開始時ＴＰ300回復", 31003, "迷宮の奥深くに眠る聖なる腕輪。\n失われた技術で装飾された宝石が\n装備者に加護を齎し、危機が迫る\nと膨大な力を分け与える。", 3, "碧貴石の聖輪");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (1, 3800, 10, 0, 3, 31004, "ＴＰ消費軽減＋6", 31004, "迷宮の奥深くに眠る、かつて天界\nに伝えられし杖。天にかざすと鏡\n⽟に篭められた祈りが溢れ出して\n尽きることなき力を授ける。", 3, "永天鏡の王笏");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (1, 3800, 10, 0, 3, 31005, "重複可能の行動速度＋30％", 31005, "迷宮の奥深くに眠る、天使の加護\nを宿した戦靴。装備するだけで神\n性を授かり、天使のような自在さ\nで空を翔ることができる。", 3, "熾天使の翔靴");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (1, 9999, 10, 0, 5, 31006, "ＴＰ上昇＋100", 31006, "迷宮最奥部に鎮祀されし神業の遺\n物。風を司る神々の恩寵が宿され\nており、装備者は無尽蔵の活力が\n疾風の如き速さで体を駆け巡る。", 3, "風神の護風恩寵");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (2, 3800, 10, 0, 3, 32001, "状態異常命中＋60", 32001, "迷宮の奥深くに眠る、幻獣を象る\n首飾り。この宝石の神秘に魅せら\nれた者は、一切の異変や脅威に抗\nうことができなくなってしまう。", 3, "幻獣飾テンタシオン");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (2, 3800, 10, 1, 3, 32002, "敵全体に呪い付与（毎秒【弱体】数×1500ダメージ）", 32002, "迷宮の奥深くに眠る、月を蝕む杖\n存在するだけで、絶えず呪力の奔\n流を溢れさせ、近づく全ての敵を\n呪いで浸食していく。", 3, "深淵蝕杖マリディクト");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (2, 3800, 10, 0, 3, 32003, "敵全体に毒付与（毎秒【弱体】数×1500ダメージ）", 32003, "迷宮の奥深くに眠る、黒刃の刺剣\n刃に埋め込まれた宝玉が、絶え間\nなく邪毒を撒き散らし、破滅へと\n刻々と時を数える。", 3, "滅刻凶刃ヴェノマス");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (2, 3800, 10, 0, 3, 32004, "敵の物理/魔法攻撃力－30％", 32004, "迷宮の奥深くに眠る、宝飾をあし\nらったベール。蔽布の下から滲み\n出るオーラが、相手の敵意を解き\nほぐし、無力化させてしまう。", 3, "燦然御纏アノラ・ベイラ");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (2, 3800, 10, 0, 3, 32005, "敵の物理/魔法防御力－30％", 32005, "迷宮の奥深くに眠る、⼑⾝に鎖を\n這わせた妖⼑。主の意思に逆らい\n暴れる鎖が怨敵の守りを打ち崩し\n紫黒の凶刃が悉くを断ち斬る。", 3, "断壁鎖刃フラグメント");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (2, 9999, 10, 0, 5, 32006, "敵の物理/魔法与ダメ－25％\n敵の物理/魔法被ダメ＋25％", 32006, "迷宮深層部に静眠せし虚影の装衣\n深淵から出でし蝕影は、存在全て\nを霜雪の如く凍てつかせ、抗う敵\nの剣盾両翼を虚壊させる。", 3, "霜影虚装アビスモナーク");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (3, 3800, 10, 0, 3, 33001, "敵の物理/魔法被クリダメ＋30％", 33001, "迷宮の奥深くに眠る、地神の力を\n宿した烈槍。大地の力を糧に放つ\n無比の一閃は、地に立つすべての\n敵に甚大なダメージを与える。", 3, "リーサルハルバード");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (3, 3800, 10, 0, 3, 33002, "物理/魔法クリダメ＋30％", 33002, "迷宮の奥深くに眠る、伝説に語ら\nれる極みの一振り。鳳凰の力を宿\nした真紅の刃は、焔の如き赫く激\n烈な一撃を与える。", 3, "オオトリノカタナ");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (3, 3800, 10, 0, 3, 33003, "通常攻撃強化＋90", 33003, "迷宮の奥深くに眠る、地核のエネ\nルギーを封じた籠手。振るう一撃\nは、世界を破壊するかの如く、通\n常攻撃を大きく強化する。", 3, "アースデストラクション");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (3, 3800, 10, 0, 3, 33004, "物理/魔法クリティカル＋30％", 33004, "迷宮の奥深くに眠る、咎⼈を裁き\nし聖⼸。この弓に選ばれし装備者\nは神⾵の加護を得て、対象に慈悲\nを与えず一瞬で急所を射貫く。", 3, "ジャッジメントアロー");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (3, 3800, 10, 0, 3, 33005, "ＨＰ吸収＋30", 33005, "迷宮の奥深くに眠る、金色の⼤戦\n斧。刈り取った獲物の魂を糧に、\nその絢爛なる刃は燦燦と輝きを増\nしてゆく。", 3, "ザ・ハーヴェスト");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (3, 9999, 10, 0, 5, 33006, "通常攻撃強化＋500\nＨＰ吸収＋50", 33006, "迷宮最深部に残された久遠の遺物\n神代の大戦より聖騎士に脈々と受\nけ継がれ、装備者へ戦いの記憶と\nともに不滅の力を分け与える。", 3, "セレスティアルエイジ");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (4, 3800, 10, 0, 3, 34001, "バフ強化＋90", 34001, "迷宮の奥深くに眠る、炎天の魔力\nを封じた耳飾り。潜在能力を究極\nまで呼び覚まし、強化の力を大幅\nに上昇させる。", 3, "極醒のアミュレット");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (4, 3800, 10, 0, 3, 34002, "物理/魔法攻撃力アップ\n（先頭キャラの物理防御力×15）", 34002, "迷宮の奥深くに眠る、⽩⾦の鎧。\n⽩⻯の鱗を繋ぎ合わせた外装が盟\n友の力を集結させる効果を持ち強\n力無比な一撃を可能にする。", 3, "断刃拒絶のアルギュロス");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (4, 3800, 10, 0, 3, 34003, "物理/魔法攻撃力アップ\n（先頭キャラの魔法防御力×15）", 34003, "迷宮の奥深くに眠る、栄華の象徴\nとして仕立てられたドレス。深紅\nに咲き乱れる花の加護は、装備者\nの絶魔の⼒を攻撃⼒へ変換する。", 3, "緋華栄華のグラナート");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (4, 3800, 10, 0, 3, 34004, "物理/魔法攻撃力＋60％", 34004, "迷宮の奥深くに眠る、光⿓の⼒を\n授かりし⼤剣。その⼀振りはかつ\nての栄光の羽ばたきを想起させ、\n極限の覇気を世界に放つ。", 3, "覇光帝龍のグローリア");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (4, 3800, 10, 0, 3, 34005, "物理/魔法防御力＋30％", 34005, "迷宮の奥深くに眠る、東の国に伝\nわりし聖鎧。玉の様に透き通る輝\nきを放つ鎧は、装備者に華の加護\nを授け、守護の力を高める。", 3, "華舞玲瓏のハルナデシコ");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (4, 9999, 10, 0, 5, 34006, "バトル開始時、物理/魔法攻撃力アップフィールド展開\n（現在値＋50％）", 34006, "迷宮深淵部に佇む歴戦無敗の⼤剣\n台座から剣を⼿にした者は天その\nものを冠すかの如き全能感を得て\n千軍を容易く退ける剛⼒を得る。", 3, "天戴冠のゼニスブレード");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (5, 3800, 10, 0, 3, 35001, "回復強化＋30", 35001, "迷宮の奥深くに眠る、妖精の輝き\nを封じた杖。宝⽟から無限の⽣命\n⼒が溢れ出し、増幅された快癒の\n⼒であらゆる苦しみを取り除く。", 3, "妖精霊杖・癒光");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (5, 3800, 10, 0, 3, 35002, "ＨＰ＋30％", 35002, "迷宮の奥深くに眠る、翠緑に彩ら\nれた指輪。装備者に、かつて豊穣\nに溢れた楽園の光景を想起させ、\n際限なき⽣命⼒を与える。", 3, "泉湧命環・豊穣");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (5, 3800, 10, 0, 3, 35003, "20秒ごとに物理無効バリア展開（【守備】数×1500無効）", 35003, "迷宮の奥深くに眠る、白金の輝き\nを放つ鎧。顕現する翅は、意思を\n持つかのよう、装備者に呼応して\n物理攻撃を断つ障壁を展開する。", 3, "白光輝鎧・断壁");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (5, 3800, 10, 0, 3, 35004, "20秒ごとに魔法無効バリア展開（【守備】数×1500無効）", 35004, "迷宮の奥深くに眠る、幽庵の煌き\nを宿す鎧。顕現する翅は、意思を\n持つかのよう、装備者に呼応して\n魔法攻撃を絶つ障壁を展開する。", 3, "幽玄煌装・絶魔");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (5, 3800, 10, 0, 3, 35005, "継続ＨＰ回復（毎秒【守備】数×1500回復）", 35005, "迷宮の奥深くに眠る、翠宝玉をあ\nしらった腕輪。常に滾々とマナが\n湧き出し、生命力を湛え続ける。", 3, "輪廻ノ翡翠珠輪");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (5, 9999, 10, 0, 5, 35006, "ＨＰ＋100％", 35006, "迷宮最下層に供奉された伝承の環\n選ばれし者がひとたび⼿にすれば\n創造神話の如く、千万無量の⽣命\n⼒を齎すと云われる。", 3, "天命授環・伊邪那");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (5, 3200, 10, 0, 1, 40001, "ＨＰ＋15％\n物理/魔法与ダメ＋15％", 40001, "【美⾷殿】と共に迷宮を冒険する\nための遺物。王家の装備を精巧に\n模しており、味方の⾝体能⼒を⼤\nきく向上させてくれる。", 3, "プリンセスフォース");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (4, 3200, 10, 0, 1, 40002, "属性ボーナス＋50％", 40002, "【トゥインクルウィッシュ】と共\nに迷宮を冒険するための遺物。\n３人のアストラルに呼応して、属\n性の力を増幅させてくれる。", 3, "アストラルブースト");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (1, 3200, 10, 0, 1, 40003, "ショップの販売価格－10％", 40003, "【サレンディア救護院】と共に迷\n宮を冒険するための遺物。主人が\n築いた信頼関係のおかげで、商品\nの価格を値引きしてもらえる。", 3, "サレンディアエンゲージ");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (3, 3200, 10, 0, 1, 40004, "物理/魔法クリダメ＋50％\n物理/魔法クリティカル－25％", 40004, "【王宮騎士団】と共に迷宮を冒険\nするための遺物。団長の愛⽤品を\n模し会⼼率と引き換えに、会⼼時\nの威⼒を⾼めてくれる。", 3, "ナイツオブフェイス");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (2, 3200, 10, 0, 1, 40005, "挑戦開始時にラビリスタが加入", 40005, "【ラビリンス】と共に迷宮を冒険\nするための遺物。迷宮女王はその\n権能でナイトがどこに居ようとも\n即座に参戦してくれる。", 3, "ラビリンスオーソリティ");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (4, 3200, 10, 0, 1, 40006, "物理/魔法攻撃力＋15％\nバフ強化＋15", 40006, "【カルミナ】と共に迷宮を冒険す\nるための遺物。これを振るう者の\n熱き声援は、ステージに立つ者の\n能力をより一層高めてくれる。", 3, "イルミネイトカルミナ");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (1, 3200, 10, 0, 1, 40007, "敵のＴＰ上昇－15", 40007, "【悪魔偽王国軍】と共に迷宮を冒\n険するための遺物。紅き両刃から\n禍々しく迸る魔力の前では、いか\nなる敵だろうと戦意を失う。", 3, "ブルートナハト");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (5, 3200, 10, 0, 1, 40008, "バトル開始時に最もＨＰが多いキャラが敵を挑発", 40008, "【牧場】と共に迷宮を冒険するた\nめの遺物。被ったらたちまちその\n場で最も目立つ存在となり、すべ\nての攻撃を引きつけてしまう。", 3, "エリザベスガーディアン");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (3, 3200, 10, 0, 1, 40009, "初期アルファルピ+3000\nアルファルピ獲得量－30%", 40009, "【メルクリウス財団】と共に迷宮\nを冒険するための遺物。人から決\nして多く受け取らず、生まれ持っ\nた財のみで戦いぬく高潔さの証。", 3, "ノブレスウィスタリア");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (2, 3200, 10, 0, 1, 40010, "デバフ強化＋20", 40010, "【トワイライトキャラバン】と共\nに迷宮を冒険するための遺物。二\n本の刀が見せる煌めきは、悪党ど\nもの気炎を容易く挫く。", 3, "兵法二海一流");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (4, 3200, 10, 0, 1, 50001, "物理/魔法攻撃力アップ（加入キャラ数×4％、最大100％）", 50001, "【美⾷殿】と共に迷宮を冒険する\nための遺物。冒険の道中で絆を結\nべば結ぶほどに、味⽅の攻撃⼒を\n⾼めてくれる。", 3, "プリンセスエンチャント");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (5, 3200, 10, 1, 1, 50002, "ボスバトルを除いて一度だけバトル敗北を免除", 50002, "【トゥインクルウィッシュ】と共\nに迷宮を冒険するための遺物。ギ\nルドマスターの諦めない心が、味\n方を一度だけ敗北から守る。", 3, "トゥインクルブレス");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (3, 3200, 10, 0, 1, 50003, "通常攻撃強化アップ（アルファルピ所持数×0.01、最大100）", 50003, "【サレンディア救護院】と共に迷\n宮を冒険するための遺物。主人の\n気品を漂わせる剣は、築いた富を\n⼒に変転させる効果を持つ。", 3, "サレンディアノーブル");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (2, 3200, 10, 0, 1, 50004, "敵の物理/魔法被ダメ＋25％\n味方の物理/魔法被ダメ＋25％", 50004, "【王宮騎士団】と共に迷宮を冒険\nするための遺物。副団長の催す闘\n争の宴は、敵味⽅が受けるダメー\nジをみな悉く増加させる。", 3, "レジーナガーラ");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (1, 3200, 10, 0, 1, 50005, "挑戦開始時にランダムな★1の迷宮遺物を１個獲得", 50005, "【ラビリンス】と共に迷宮を冒険\nするための遺物。マスターの⼒を\n冠した剣は、開発者権限の⼀部を\n解放し、遺物を即座に獲得する。", 3, "マスターオブラビリンス");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (4, 3200, 10, 0, 1, 50006, "30秒ごとに物理/魔法攻撃力ダウン解除", 50006, "【カルミナ】と共に迷宮を冒険す\nるための遺物。ハンドマイクを通\nじて拡がっていく歌声は、味方の\n疲労をふき飛ばす。", 3, "ヴォイスフォーユー");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (1, 3200, 10, 0, 1, 50007, "15秒ごとにＴＰ固定量100回復\n敵のＴＰ100ダウン", 50007, "【悪魔偽王国軍】と共に迷宮を冒\n険するための遺物。重く振りかざ\nす一撃は、斬り裂いた獲物の生気\nを我が物とする。", 3, "ディアボロスフィアー");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (5, 3200, 10, 0, 1, 50008, "行動不能無効１回", 50008, "【牧場】と共に迷宮を冒険するた\nめの遺物。持ち主のやる気を呼び\n起こし、ぐうたらさを１回だけ無\n効化できる。あくまで１回だけ。", 3, "ドングリースピリット");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (3, 3200, 10, 0, 1, 50009, "物理/魔法クリダメアップ（アルファルピ所持数×0.01%、最大100%）", 50009, "【メルクリウス財団】と共に迷宮\nを冒険するための遺物。富を積め\nば積むほど精神を集中させ、必殺\nの一突きを敵に見舞う。", 3, "リヴァイアサンコール");
INSERT INTO `v1_8d3f034fa36ed3b42d4e332b2aebdc2958323e3b71bc2a357867bbd81ff3795d` VALUES (2, 3200, 10, 0, 1, 50010, "敵への継続ダメージアップ（加入キャラ数×10％、最大50万ダメージ）", 50010, "【トワイライトキャラバン】と共\nに迷宮を冒険するための遺物。さ\nまざまな者たちの個性を学習し、\n敵を苦しめる術に転化する。", 3, "バインディングローズ");
