INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12531, "", "ハイキング", "ハイキング", "これ、何と言う？", 12464, 0, 5, 12461, 5, 12495, 12452, 1, 1070, 5, "軽微な装備で、自然や景観を楽しみながら歩くアクティビティ", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12531, "ピークハント", "ピクハント", "ピークハント", "これ、何と言う？", 12488, 0, 5, 12495, 5, 12500, 12463, 1, 1071, 5, "登山のスタイルの一つで、山頂を目指して登る場合をこう呼ぶ", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12521, "", "バタフライ", "バタフライ", "これ、何と言う？", 12452, 0, 5, 12501, 5, 12496, 12479, 1, 1075, 5, "水泳で両腕を同時に前後に、両足を同時に上下に動かす泳ぎ方。動きがとある生き物に似ている", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12531, "", "カーリング", "カーリング", "これ、何と言う？", 12464, 0, 5, 12522, 5, 12459, 12540, 1, 1076, 5, "戦略性の高さから「氷上のチェス」とも呼ばれるウィンタースポーツ", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12525, "", "デカスロン", "デカスロン", "これ、何と言う？", 12531, 0, 5, 12473, 5, 12487, 12459, 1, 1077, 5, "「10種」の種目を行い、その合計点を競う陸上競技。「10」を意味する言葉が含まれる", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12394, "", "ともえなげ", "ともえなげ", "これ、何と言う？", 12370, 0, 4, 12360, 4, 12392, 12418, 1, 1078, 4, "柔道で、相手の下にもぐりこみ、後方にけり投げる技。その姿勢が、「とある漢字」に似ている", 4, 1, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12452, "", "ストライク", "ストライク", "これ、何と言う？", 12463, 0, 5, 12521, 5, 12473, 12488, 1, 1079, 5, "野球の判定の一つ。これが3回宣言されると三振となる", 5, 1, 5);
