INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12457, "フリーフォール", "フリフォル", "フリーフォール", "これ、何と言う？", 12523, 0, 5, 12501, 5, 12501, 12522, 1, 1115, 5, "「絶叫マシーン」の種類のひとつ。高所から一気に垂直落下するスリルを楽しむ", 5, 3, 5);
