INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12466, "コールドゲーム", "コルドゲム", "コールドゲーム", "これ、何と言う？", 12512, 0, 5, 12489, 5, 12467, 12523, 1, 1121, 5, "野球の試合が、悪天候や、大きな点差がついたなどの事情により途中で打ち切り終了となること", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12540, "", "トルネード", "トルネード投法", "これ、何と言う？", 12489, 0, 5, 12493, 5, 12488, 12523, 1, 1122, 5, "野球の投球方法の一つ。大きく身体をひねって投げる動きが、まるで竜巻を思わせる", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12457, "ムーンウォーク", "ムンウォク", "ムーンウォーク", "これ、何と言う？", 12463, 0, 5, 12454, 5, 12512, 12531, 1, 1123, 5, "ダンスのステップの一つ。前に歩いているように見せながら後ろに下がっていく不思議な動き", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12489, "", "シールド戦", "シールド戦", "これ、何と言う？", 25126, 0, 5, 12523, 5, 12471, 12540, 1, 1127, 6, "対戦カードゲームで人気のルールの一つ。その場で開封したパックで即興デッキを作り勝負する", 5, 3, 5);
