INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12483, "", "ストラック", "ストラックアウト", "これ、何と言う？", 12463, 0, 5, 12521, 5, 12473, 12488, 1, 1208, 5, "野球のピッチングの精度を競うゲーム。1から9の数字が書かれたパネルを狙って当てる", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12383, "", "けさがため", "けさがため", "これ、何と言う？", 12417, 0, 4, 12364, 4, 12369, 12373, 1, 1209, 4, "柔道の固め技の一つ。お坊さんが身につける、とある衣装から名付けられた", 4, 2, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 31293, "", "掛かり稽古", "掛かり稽古", "これ、何と言う？", 21476, 0, 4, 12426, 6, 25499, 12363, 1, 1210, 6, "剣道の練習法。ふた手に分かれて、一方がもう一方に対し、休憩をせずに繰り返しかかっていく", 4, 3, 6);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 23556, "", "クレー射撃", "クレー射撃", "これ、何と言う？", 25731, 0, 5, 12540, 5, 12463, 12524, 1, 1211, 6, "射撃競技の一つ。高速で空中に射出された皿を、ショットガンで撃ち壊す精度を競う", 5, 3, 6);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12459, "イエローカード", "イエロカド", "イエローカード", "これ、何と言う？", 12489, 0, 5, 12525, 5, 12452, 12456, 1, 1212, 5, "サッカーで悪質な反則に対して示される警告。これが2回出ると、さらに重いレッドカードとなる", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12469, "", "フットサル", "フットサル", "これ、何と言う？", 12523, 0, 5, 12488, 5, 12501, 12483, 1, 1213, 5, "サッカーから派生したスポーツで、5人対5人形式のサッカー。室内でプレイされることも。", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12394, "", "せおいなげ", "せおいなげ", "これ、何と言う？", 12370, 0, 4, 12356, 4, 12379, 12362, 1, 1214, 4, "柔道の花形の技としてよく知られる投げ技。相手を背負うような形で、肩越しに投げる", 4, 1, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12521, "", "ウルトラC", "ウルトラC", "これ、何と言う？", 67, 0, 5, 12488, 5, 12454, 12523, 1, 1215, 2, "最近では「秘密兵器」という意味でも使われるが、元々は体操競技で非常に難しい技を指す言葉", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12523, "", "カタパルト", "カタパルト", "これ、何と言う？", 12488, 0, 5, 12497, 5, 12459, 12479, 1, 1216, 5, "かつて使われた攻城兵器。てこの原理を利用して、遠くまで石を射出する", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12473, "", "イージス艦", "イージス艦", "これ、何と言う？", 33382, 0, 5, 12472, 5, 12452, 12540, 1, 1217, 6, "高度な対空射撃設備を搭載した軍艦。神話の中でゼウスが娘に与えた盾の名前が由来", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12475, "ツーマンセル", "ツマンセル", "ツーマンセル", "これ、何と言う？", 12523, 0, 5, 12531, 5, 12484, 12510, 1, 1218, 5, "軍隊や警察において使われる言葉で、「二人一組」のこと", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12452, "", "ホロサイト", "ホロサイト", "これ、何と言う？", 12488, 0, 5, 12469, 5, 12507, 12525, 1, 1219, 5, "銃で狙いを定めるための「照準器」の種類の一つ。いわゆる「ホログラム」の技術を使っている", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12531, "", "サイレンサ", "サイレンサー", "これ、何と言う？", 12469, 0, 5, 12524, 5, 12469, 12452, 1, 1220, 5, "スパイ映画でもよく見る、銃の発砲音を抑えるために銃口につける装置。「サプレッサー」とも", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12473, "", "点バースト", "3点バースト", "これ、何と言う？", 12488, 0, 5, 12540, 6, 28857, 12496, 1, 1221, 5, "ライフルなどの仕組みで、一度引き金を引くたびに、銃弾が3発ずつ発射されるもの", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12479, "", "ホルスター", "ホルスター", "これ、何と言う？", 12540, 0, 5, 12473, 5, 12507, 12523, 1, 1222, 5, "拳銃を収めるために、主に腰に装備するケース。西部劇での決闘シーンなどでもよく目立つ", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12412, "", "ハロンぼう", "ハロンぼう", "これ、何と言う？", 12358, 0, 5, 12531, 5, 12495, 12525, 1, 1223, 4, "競馬で、ゴールから一定距離ごとに目印として置かれる棒。赤と白のシマシマ柄をしている", 5, 3, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12516, "", "ステイヤー", "ステイヤー", "これ、何と言う？", 12540, 0, 5, 12452, 5, 12473, 12486, 1, 1224, 5, "競馬で、スタミナが多く長距離レースを得意とする馬のこと", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (1, 12473, "", "テンペスト", "テンペストブレード", "これ、何と言う？", 12488, 1225, 5, 12506, 5, 12486, 12531, 1, 1225, 5, "『ジータ』のユニオンバーストの名前", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12540, "", "フィジーク", "フィジーク", "これ、何と言う？", 12463, 0, 5, 12472, 5, 12501, 12451, 1, 1226, 5, "肉体美を競うコンテスト。よくボディビルと比較されるが、評価の観点や、対象の部位が異なる", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12531, "", "レスリング", "レスリング", "これ、何と言う？", 12464, 0, 5, 12522, 5, 12524, 12473, 1, 1227, 5, "「プロレス」の「レス」、何という言葉の略？", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12540, "", "ストレート", "ストレート", "これ、何と言う？", 12488, 0, 5, 12524, 5, 12473, 12488, 1, 1228, 5, "格闘技のパンチの種類の一つ。「ジャブ」と並んで重要となる、最も威力の出やすいもの", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12454, "ノックアウト", "ノクアウト", "ノックアウト", "これ、何と言う？", 12488, 0, 5, 12450, 5, 12494, 12463, 1, 1229, 5, "格闘技での勝敗判定の一つ。相手をダウンさせたまま、既定の時間が過ぎたとき", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12459, "", "テクニカル", "テクニカル", "これ、何と言う？", 12523, 0, 5, 12491, 5, 12486, 12463, 1, 1230, 5, "格闘技で、ただのノックアウトとは異なった特殊な判定である「TKO」。この「T」は何の略？", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12479, "", "ウェルター", "ウェルター級", "これ、何と言う？", 12540, 0, 5, 12523, 5, 12454, 12455, 1, 1231, 5, "ボクシングの階級で「ライト」などのつくものを省いて考えた場合、「ミドル級とライト級の間」は何？", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12521, "", "ブーメラン", "ブーメラン", "これ、何と言う？", 12531, 0, 5, 12513, 5, 12502, 12540, 1, 1232, 5, "狩猟のために生まれた武器。「くの字型」をしており、投げた後Uターンして手元に戻ってくる", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12510, "", "ネクロマン", "ネクロマンサー", "これ、何と言う？", 12531, 0, 5, 12525, 5, 12493, 12463, 1, 1233, 5, "「死霊使い」とも呼ばれる、ファンタジーでもおなじみの存在。死者を蘇らせ操る術を持つ", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12451, "", "グラディウ", "グラディウス", "これ、何と言う？", 12454, 0, 5, 12487, 5, 12464, 12521, 1, 1234, 5, "かつての剣闘士（グラディエーター）が用いた、幅広の剣", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12475, "", "コロッセオ", "コロッセオ", "これ、何と言う？", 12458, 0, 5, 12483, 5, 12467, 12525, 1, 1235, 5, "「コロシアム」「コロセウム」とも表記される、非常によく知られた円形闘技場", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12483, "", "ノールック", "ノールックパス", "これ、何と言う？", 12463, 0, 5, 12523, 5, 12494, 12540, 1, 1236, 5, "球技スポーツで、そちらに目線を向けることなくパスをして、相手を出し抜くテクニックのこと", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12460, "", "スタンガン", "スタンガン", "これ、何と言う？", 12531, 0, 5, 12531, 5, 12473, 12479, 1, 1237, 5, "護身用や警官が持つ装備として知られた、手持ちサイズの武器。高圧の電撃で相手を無力化する", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12412, "", "にょいぼう", "にょいぼう", "これ、何と言う？", 12358, 0, 4, 12356, 4, 12395, 12423, 1, 1238, 4, "西遊記の「孫悟空」が武器に使った、自由自在に拡大・縮小できる棒", 4, 1, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12452, "", "デスサイズ", "デスサイズ", "これ、何と言う？", 12474, 0, 5, 12469, 5, 12487, 12473, 1, 1239, 5, "「死神」が持つと言われる大きな鎌のこと", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (1, 12364, "", "りゅうがん", "りゅうがん", "これ、何と言う？", 12435, 1240, 4, 12358, 4, 12426, 12421, 1, 1240, 4, "『ホマレ』が持つ能力で、発動中は遠く離れたものも見ることができる", 4, 2, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12531, "", "ドーピング", "ドーピング", "これ、何と言う？", 12464, 0, 5, 12500, 5, 12489, 12540, 1, 1241, 5, "スポーツにおいて、薬物などの禁止された手段を用いて、不当に能力を高めること", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12480, "", "スライダー", "スライダー", "これ、何と言う？", 12540, 0, 5, 12452, 5, 12473, 12521, 1, 1242, 5, "野球の投球における球種の一つ。文字通り、スルリと滑るような変化をすることから", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12540, "", "ツーシーム", "ツーシーム", "これ、何と言う？", 12512, 0, 5, 12471, 5, 12484, 12540, 1, 1243, 5, "野球の投球における球種の一つ。球が1回転する間に、縫い目が合計で2回通過することから", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12452, "", "アンパイア", "アンパイア", "これ、何と言う？", 12450, 0, 5, 12497, 5, 12450, 12531, 1, 1244, 5, "広義にはスポーツ全般の審判員を指すが、一般には特に野球の審判員を指すことが多い", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12540, "", "デイゲーム", "ナイター⇔デイゲーム", "これ、何と言う？", 12512, 0, 5, 12466, 5, 12487, 12452, 1, 1245, 5, "野球の試合の、開催形式の分類。時間帯によって、2つに分かれる", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12452, "", "ロスタイム", "ロスタイム", "これ、何と言う？", 12512, 0, 5, 12479, 5, 12525, 12473, 1, 1246, 5, "サッカーで「アディショナルタイム」と呼ばれるルールの、かつての呼び方", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12522, "", "センタリン", "センタリング", "これ、何と言う？", 12531, 0, 5, 12479, 5, 12475, 12531, 1, 1247, 5, "サッカーで、サイドから中央のゴール付近の味方へ大きなパスを出すこと", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12468, "オウンゴール", "オウンゴル", "オウンゴール", "これ、何と言う？", 12523, 0, 5, 12531, 5, 12458, 12454, 1, 1248, 5, "サッカーで、間違って自陣側のゴールにボールを入れてしまい、相手に得点が入ること", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12531, "", "フェイント", "フェイント", "これ、何と言う？", 12488, 0, 5, 12452, 5, 12501, 12455, 1, 1249, 5, "多くのスポーツで、相手の意表を突くためにわざと行う「ふり動作」のこと。「フェイク」とも", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12353, "", "ごっつぁん", "ごっつぁんです", "これ、何と言う？", 12435, 0, 4, 12388, 4, 12372, 12387, 1, 1250, 4, "力士の間で、お礼を言うときに広く使われるあいさつ。昨今は相撲以外の一般層にも浸透している", 4, 3, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12524, "", "聖火リレー", "聖火リレー", "これ、何と言う？", 12540, 0, 6, 12522, 6, 32854, 28779, 1, 1251, 5, "オリンピックで、トーチに灯した火をランナーが次々と受け継いで、会場までリレーをする催し", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12531, "", "ダイビング", "ダイビングキャッチ", "これ、何と言う？", 12464, 0, 5, 12499, 5, 12480, 12452, 1, 1252, 5, "野球の野手が、大きく頭から飛び込んで打球をキャッチするプレー。成功すると非常に盛り上がる", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12531, "フレーミング", "フレミング", "フレーミング", "これ、何と言う？", 12464, 0, 5, 12511, 5, 12501, 12524, 1, 1253, 5, "野球の捕手が、判定の際どいボールを、自分がうまく動くことで審判にストライクと判定させる技術", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (1, 12452, "", "ストライク", "プリンセスストライク", "これ、何と言う？", 12463, 1255, 5, 12521, 5, 12473, 12488, 1, 1255, 5, "『ペコリーヌ』のユニオンバースト", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (1, 25731, "", "物理攻撃力", "物理攻撃力", "これ、何と言う？", 21147, 0, 6, 25915, 6, 29289, 29702, 1, 1256, 6, "プリコネRのバトルで、「物理攻撃で与えられるダメージ」に大きくかかわるステータス", 6, 1, 6);
