INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12496, "", "オムニバス", "オムニバス形式", "これ、何と言う？", 12473, 0, 5, 12491, 5, 12458, 12512, 3, 3176, 5, "音楽業界で、「総集編」の意味で使われる言葉。似た言葉として「コンピレーション」も使われる", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12525, "", "アンソロジ", "アンソロジー", "これ、何と言う？", 12472, 0, 5, 12477, 5, 12450, 12531, 3, 3177, 5, "創作物に用いられる、複数の作者が同じテーマの作品を持ち寄って作る作品のこと", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 25293, "", "二礼二拍一", "二礼二拍一礼", "これ、何と言う？", 19968, 0, 6, 20108, 6, 20108, 31036, 3, 3178, 6, "神社への参拝の作法の一つ。お辞儀を2回、拍手を2回、お辞儀を1回、という一連の動作を指す", 6, 3, 6);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (1, 12473, "ワードマスター", "ワドマスタ", "ワードマスター", "これ、何と言う？", 12479, 3179, 5, 12510, 5, 12527, 12489, 3, 3179, 5, "ミニゲーム「しりとりドラ\nゴンズ」でワード図鑑を\nコンプリートした者の称号", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12451, "", "セルフィー", "セルフィー", "これ、何と言う？", 12540, 0, 5, 12501, 5, 12475, 12523, 3, 3180, 5, "「自撮り」とも呼ばれる、自分で自分を写真撮影する行為、英語での呼び方は？", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12531, "", "シャボン玉", "シャボン玉", "これ、何と言う？", 29577, 0, 5, 12508, 5, 12471, 12515, 3, 3181, 6, "子どもに人気の遊び道具。ストローの先にせっけん水をつけ、大きな球を吹き出して楽しむ", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12521, "H⇒「ハード」　B⇒「ブラック」", "ハドブラク", "H⇒「ハード」　B⇒「ブラック」", "これ、何と言う？", 12463, 0, 5, 12502, 5, 12495, 12489, 3, 3182, 5, "鉛筆の芯の種類である「HB」。「H」と「B」はそれぞれ何の略？", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12519, "イリュージョン", "リュジョン", "イリュージョン", "これ、何と言う？", 12531, 0, 5, 12472, 5, 12522, 12517, 3, 3183, 5, "手品や奇術の別名だが、特に大掛かりな装置やステージを使ったものを指すことが多い", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12451, "クラファン＝クラウド ファンディング", "ウドディン", "クラファン＝クラウド ファンディング", "これ、略さずに言うと？", 12531, 0, 5, 12487, 5, 12454, 12489, 3, 3184, 5, "", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12486, "", "バラエティ", "バラエティ番組", "これ、何と言う？", 12451, 0, 5, 12456, 5, 12496, 12521, 3, 3185, 5, "テレビ番組のジャンルの一つ。お笑いやトーク、ゲームなどの企画が多く、娯楽の性質が強い", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12393, "", "ししおどし", "ししおどし", "これ、何と言う？", 12375, 0, 4, 12362, 4, 12375, 12375, 3, 3186, 4, "和式の庭園に置かれる、竹製の装置。水の重みで動作し、「カポーン」と風情のある音が鳴る", 4, 2, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12364, "", "えびすがお", "えびすがお", "これ、何と言う？", 12362, 0, 4, 12377, 4, 12360, 12403, 3, 3187, 4, "目を細めてニコニコと笑う顔つきのこと。「七福神」のとある神様をほうふつとさせることから", 4, 2, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12502, "スリーセブン", "スリセブン", "スリーセブン", "これ、何と言う？", 12531, 0, 5, 12475, 5, 12473, 12522, 3, 3188, 5, "数字の「7」が三つ並んだもの。スロットマシーンでもおなじみで、非常に縁起が良いとされる", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12523, "", "ユアセルフ", "DIY\n＝ドゥ イット ユアセルフ", "これ、略さずに言うと？", 12501, 0, 5, 12475, 5, 12518, 12450, 3, 3189, 5, "", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12356, "", "ごしゅいん", "ごしゅいん集め", "これ、何と言う？", 12435, 0, 4, 12421, 4, 12372, 12375, 3, 3190, 4, "寺や神社を参拝した証となる印を集めること。スタンプラリー感覚で楽しめるとして、人気上昇中", 4, 3, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12471, "", "キルレシオ", "キルレシオ", "これ、何と言う？", 12458, 0, 5, 12524, 5, 12461, 12523, 3, 3191, 5, "ゲームで、自分が敵を倒した回数と、敵に倒された回数との比率。「キルレート」とはやや異なる", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12406, "", "ろてんぶろ", "ろてんぶろ", "これ、何と言う？", 12429, 0, 4, 12435, 4, 12429, 12390, 3, 3192, 4, "温泉の形式の一つで、屋外に開放されている風呂のこと。温泉の醍醐味の一つとして親しまれる", 4, 1, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12390, "", "えびすてん", "えびすてん（ひらがなで）", "これ、何と言う？", 12435, 0, 4, 12377, 4, 12360, 12403, 3, 3193, 4, "「七福神」のうち、漁業や商売を司るとされる神様。ニコニコ顔と、釣り竿と鯛を持った姿が有名", 4, 2, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12540, "", "Eスポーツ", "Eスポーツ", "これ、何と言う？", 12484, 0, 5, 12509, 2, 69, 12473, 3, 3194, 5, "デジタルゲームをスポーツとして捉える、近年普及した呼称", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12540, "", "プリザーブ", "プリザーブド・フラワー", "これ、何と言う？", 12502, 0, 5, 12470, 5, 12503, 12522, 3, 3195, 5, "生花に特殊な加工を行い、観賞用に枯れないようにしたもの", 5, 2, 5);
