INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12435, "", "うらせんけ", "うらせんけ", "これ、何と言う？", 12369, 0, 4, 12379, 4, 12358, 12425, 3, 3199, 4, "「三千家」と呼ばれる茶道の三流派、「表千家」と「武者小路千家」ともう一つは？", 4, 3, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12423, "", "りんしょう", "りんしょう", "これ、何と言う？", 12358, 0, 4, 12375, 4, 12426, 12435, 3, 3200, 4, "カエルのうたでおなじみの歌唱法。複数グループに分かれ、一定間隔で後を追いかけるように歌う", 4, 2, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12498, "アンチヒーロー", "アンチヒロ", "アンチヒーロー", "これ、何と言う？", 12525, 0, 5, 12481, 5, 12450, 12531, 3, 3201, 5, "ダークヒーローとも呼ばれるフィクションのキャラ像。非道徳的でありながら魅力的な要素も持つ", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12409, "読まずに食べた", "読ま食べた", "読まずに食べた", "この歌の歌詞は？", 12383, 0, 4, 39135, 6, 35501, 12414, 3, 3202, 4, "有名な童謡の歌い出し。♪白ヤギさんからお手紙着いた　黒ヤギさんたら…", 6, 1, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12364, "", "しおひがり", "しおひがり", "これ、何と言う？", 12426, 0, 4, 12402, 4, 12375, 12362, 3, 3204, 4, "干潟で、潮が引いているあいだに貝類や生き物をとって遊ぶこと。春の訪れとともに楽しまれる", 4, 1, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12364, "", "もみじがり", "もみじがり", "これ、何と言う？", 12426, 0, 4, 12376, 4, 12418, 12415, 3, 3205, 4, "紅葉の時期に野山に出かけ風景を楽しむこと。何かを採取するわけではなく、あくまで見て楽しむ", 4, 1, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12358, "", "ふくはうち", "鬼は外、ふくはうち", "これ、何と言う？", 12385, 0, 4, 12399, 4, 12405, 12367, 3, 3206, 4, "「節分」で鬼に豆を投げるときに言う、お決まりのかけ声", 4, 1, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12412, "", "こいのぼり", "こいのぼり", "これ、何と言う？", 12426, 0, 4, 12398, 4, 12371, 12356, 3, 3207, 4, "季節行事として5月5日前後に外に飾るもの。「♪屋根より高い～」という歌でもおなじみ", 4, 1, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12531, "アングラ＝アンダーグラウンド", "ダーウンド", "アングラ＝アンダーグラウンド", "これ、略さずに言うと？", 12489, 0, 5, 12454, 5, 12480, 12540, 3, 3208, 5, "", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12452, "", "ントマイム", "パントマイム", "これ、何と言う？", 12512, 0, 5, 12510, 5, 12531, 12488, 3, 3209, 5, "古くから親しまれる大道芸で、壁や階段などが、あたかも本当に存在するかのように振るまう", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 33457, "", "ねずみ花火", "ねずみ花火", "これ、何と言う？", 28779, 0, 4, 12415, 4, 12397, 12378, 3, 3210, 6, "花火の一種。手には持たず、着火して地面に放つと火花を発しながら、ある動物のように動き回る", 4, 2, 6);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12531, "", "アンダンテ", "アンダンテ", "これ、何と言う？", 12486, 0, 5, 12480, 5, 12450, 12531, 3, 3211, 5, "音楽用語で、「歩くような速さで」を意味する言葉", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12515, "", "オーシャン", "レッドオーシャン", "これ、何と言う？", 12531, 0, 5, 12471, 5, 12458, 12540, 3, 3212, 5, "ビジネス用語で、既に激しい競争状態にあるので新規参入には向いていない市場のこと", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12515, "", "ドンシャリ", "ドンシャリ", "これ、何と言う？", 12522, 0, 5, 12471, 5, 12489, 12531, 3, 3213, 5, "イヤホンなどについての俗語。低音がドンドン、高音がシャリシャリと強調されて聴こえるもの", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12379, "", "しゃみせん", "しゃみせん", "これ、何と言う？", 12435, 0, 4, 12415, 4, 12375, 12419, 3, 3214, 4, "非常によく知られた和楽器で、3本の弦を弾いて演奏する弦楽器。よく似た楽器に「三線」がある", 4, 1, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12452, "", "ストライプ", "ストライプ柄", "これ、何と言う？", 12503, 0, 5, 12521, 5, 12473, 12488, 3, 3215, 5, "しましま柄のこと。縦方向の縞のみを指すと誤解されがちだが、縦と横どちらも該当する", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12531, "", "サラウンド", "サラウンド", "これ、何と言う？", 12489, 0, 5, 12454, 5, 12469, 12521, 3, 3216, 5, "ホームシアターなどで自分を取り囲むようにスピーカーを置くことで音が立体的に聴こえる仕組み", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12423, "", "だんしょく", "だんしょく系", "これ、何と言う？", 12367, 0, 4, 12375, 4, 12384, 12435, 3, 3217, 4, "赤、オレンジ、黄色など、あたたかい印象を与える系統の色のこと", 4, 2, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12531, "", "マスキング", "マスキングテープ", "これ、何と言う？", 12464, 0, 5, 12461, 5, 12510, 12473, 3, 3218, 5, "模型やフィギュアを塗装する際などに、塗料をつけたくない箇所を覆うように使うテープ", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12471, "ピアニッシモ", "ピアニシモ", "ピアニッシモ", "これ、何と言う？", 12514, 0, 5, 12491, 5, 12500, 12450, 3, 3219, 5, "音楽用語で「弱く」は「ピアノ」、「やや弱く」は「メゾピアノ」、では「ごく弱く」は何と言う？", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12416, "", "ほんのむし", "ほんのむし", "これ、何と言う？", 12375, 0, 4, 12398, 4, 12411, 12435, 3, 3220, 4, "読書が好きでたまらない人を、まるで本を餌にして住みついている生き物かのように表した言葉", 4, 2, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12461, "", "クロッキー", "クロッキー", "これ、何と言う？", 12540, 0, 5, 12483, 5, 12463, 12525, 3, 3221, 5, "小中学校の美術教育でもよく用いられる、10分程度の短時間でモデルを素早く描くこと", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12474, "", "キュビズム", "キュビズム", "これ、何と言う？", 12512, 0, 5, 12499, 5, 12461, 12517, 3, 3222, 5, "「立体派」「立体主義」とも呼ばれる、美術の世界で起きた大きな運動", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12524, "フリー・トゥ・プレイ", "フリプレイ", "フリー・トゥ・プレイ", "これ、何と言う？", 12452, 0, 5, 12503, 5, 12501, 12522, 3, 3223, 5, "オンラインゲームで、「F2P」と略される言葉、正式には何と言う？", 5, 3, 5);
