INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12524, "サーキュレーター", "サキュレタ", "サーキュレーター", "これ、何と言う？", 12479, 0, 5, 12517, 5, 12469, 12461, 4, 4217, 5, "空調家電の一種。扇風機に似た仕組みだが、直接風を受けるのではなく空気を循環させるためのもの", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12452, "", "カプサイシ", "カプサイシン", "これ、何と言う？", 12471, 0, 5, 12469, 5, 12459, 12503, 4, 4218, 5, "とうがらしなどの、辛さのもととなっている成分。食欲増進、発汗、血行促進などの効果がある", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12451, "", "スコヴィル", "スコヴィル値", "これ、何と言う？", 12523, 0, 5, 12532, 5, 12473, 12467, 4, 4219, 5, "とうがらしの辛さを計る単位。ハバネロは1万程度だが、世界一辛いものだと数百万に到達する", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12531, "", "ボーリング", "ボーリング", "これ、何と言う？", 12464, 0, 5, 12522, 5, 12508, 12540, 4, 4220, 5, "地面に円柱状の穴を掘って、地盤の調査を行うこと。「とある球技」の名前としばしば混同される", 5, 2, 5);
