INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (1, 12387, "", "なかぱっぱ", "はじめちょろちょろなかぱっぱ", "これ、何と言う？", 12401, 0, 4, 12401, 4, 12394, 12363, 5, 5022, 4, "釜で米を炊くときの、火加減のコツ。コッコロとの出会いのシーンでもおなじみのフレーズ", 4, 1, 4);
