INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (1, 28857, "", "断崖の点心", "断崖の点心", "これ、何と言う？", 24515, 5024, 6, 12398, 6, 26029, 23830, 5, 5024, 6, "ダンジョン「断崖の遺跡」を制覇した証として美食殿が手にした料理、兼ギルドハウス家具", 4, 3, 6);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (1, 26524, "", "密林の果実", "密林の果実", "これ、何と言う？", 23455, 5025, 6, 12398, 6, 23494, 26519, 5, 5025, 6, "ダンジョン「密林の大樹」を制覇した証として美食殿が手にした料理、兼ギルドハウス家具", 4, 3, 6);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12418, "", "ひかりもの", "ひかりもの", "これ、何と言う？", 12398, 0, 4, 12426, 4, 12402, 12363, 5, 5026, 4, "寿司屋で使われる言葉。サバ・イワシ・サンマなど、背中が青く、腹が銀白色に光る魚のこと", 4, 2, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12419, "", "ぎんしゃり", "ぎんしゃり", "これ、何と言う？", 12426, 0, 4, 12375, 4, 12366, 12435, 5, 5027, 4, "寿司屋でもおなじみの、炊いた白米の別名。炊き立ての米が銀色に輝いて見えることに由来する", 4, 1, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12406, "", "ゆきみぶろ", "ゆきみぶろ", "これ、何と言う？", 12429, 0, 4, 12415, 4, 12422, 12365, 5, 5028, 4, "雪景色を眺めながら湯に浸かるという、風情溢れた温泉の嗜み方", 4, 2, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12426, "", "かたくり粉", "かたくり粉", "これ、何と言う？", 31881, 0, 4, 12367, 4, 12363, 12383, 5, 5029, 6, "料理のとろみ付けや、揚げ物の衣に使われる粉。原料はジャガイモからとられるデンプンが主", 4, 1, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12458, "", "カフェオレ", "カフェオレ", "これ、何と言う？", 12524, 0, 5, 12455, 5, 12459, 12501, 5, 5030, 5, "ドリップコーヒーにミルクを加えた飲み物。似たような名前の飲み物が他にもあるので注意！", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12521, "", "カフェラテ", "カフェラテ", "これ、何と言う？", 12486, 0, 5, 12455, 5, 12459, 12501, 5, 5031, 5, "エスプレッソにミルクを加えた類の飲み物の中で、一般に最もよく飲まれるもの", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (1, 12427, "", "たべあるき", "たべあるき", "これ、何と言う？", 12365, 1255, 4, 12354, 4, 12383, 12409, 5, 5032, 4, "『ペコリーヌ』の趣味のひとつで、その土地ゆかりのグルメをあちこち食べてまわること", 4, 3, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (1, 12490, "", "ゼラチナ丼", "ゼラチナ丼", "これ、何と言う？", 20028, 0, 5, 12481, 5, 12476, 12521, 5, 5033, 6, "ゼリー状の魔物の身をどんぶりご飯に乗せた料理。ペコリーヌ行きつけの魔物料理屋で出される", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (1, 12531, "", "ファウンテ", "チョコレートファウンテン", "このギルドハウスアイテムは何？", 12486, 5034, 5, 12454, 5, 12501, 12449, 5, 5034, 5, "上からチョコレートがどんどん湧き出る家具", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (1, 12398, "", "カカオの実", "カカオの実", "これは何？", 23455, 5035, 5, 12458, 5, 12459, 12459, 5, 5035, 6, "この魔物に幸せな言葉をかけて育てると、尻尾の先から生えると言われる食材", 5, 2, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (1, 12365, "", "たい焼き屋", "たい焼き屋", "これは何？", 23627, 5036, 4, 28988, 4, 12383, 12356, 5, 5036, 6, "『タマキ』が主なアルバイト先としている飲食店。とある和菓子を販売している", 6, 2, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (1, 12471, "", "ウィッシュ", "トゥインクルウィッシュ", "このギルドの名前は？", 12517, 5037, 5, 12483, 5, 12454, 12451, 5, 5037, 5, "", 5, 1, 5);
