INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (1, 12531, "", "ルーセント", "ルーセント学院", "このギルドの名前は？", 12488, 5045, 5, 12475, 5, 12523, 12540, 5, 5045, 5, "", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (1, 12419, "", "ユニちゃん", "ユニちゃんズ", "これ、何と言う？", 12435, 6004, 5, 12385, 5, 12518, 12491, 5, 5047, 4, "【聖テレサ女学院（なかよし部）】のメンバー中、『ユニ』だけはこのギルド名にしたいらしい", 4, 2, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12510, "", "マシュマロ", "マシュマロ", "これ、何と言う？", 12525, 0, 5, 12517, 5, 12510, 12471, 5, 5049, 5, "メレンゲをゼリーで固めた白くてフワフワの菓子。ほっぺなど柔らかい物のたとえでもおなじみ", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12429, "", "三枚おろし", "三枚おろし", "これ、何と言う？", 12375, 0, 6, 12362, 6, 19977, 26522, 5, 5050, 4, "魚の身をおろす方法の中で、最も基本的とされるもの。右身・左身・中骨の3つに切り離す", 4, 2, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12499, "", "グレイビー", "グレイビーソース", "これ、何と言う？", 12540, 0, 5, 12452, 5, 12464, 12524, 5, 5051, 5, "ステーキやハンバーグのソースとして人気。肉を調理したときに出る肉汁をベースとしたソース", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12540, "", "アラモード", "プリンアラモード", "これ、何と言う？", 12489, 0, 5, 12514, 5, 12450, 12521, 5, 5052, 5, "プリンを中心に、周りに様々な甘味を盛りつけたスイーツ", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12494, "", "ホトケノザ", "ホトケノザ", "これ、何？", 12470, 0, 5, 12465, 5, 12507, 12488, 5, 5053, 5, "「七草がゆ」の材料、セリ・ナズナ・ゴギョウ・ハコベラ・スズナ・スズシロ、あと一つは？", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12367, "", "たんぱく質", "たんぱく質", "これ、何？", 36074, 0, 4, 12401, 4, 12383, 12435, 5, 5054, 6, "「５大栄養素」、脂質・糖質・ビタミン・ミネラル、あと一つは？", 4, 1, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12487, "", "ブランデー", "麦⇒ウイスキー　果物⇒ブランデー", "これ、何？", 12540, 0, 5, 12531, 5, 12502, 12521, 5, 5055, 5, "", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12514, "", "カルダモン", "カルダモン", "これ、何と言う？", 12531, 0, 5, 12480, 5, 12459, 12523, 5, 5056, 5, "「スパイスの女王」「香りの王様」などの別名を持つ香辛料。カレーやチャイには欠かせない", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12390, "", "かしわてん", "かしわてん", "これ、何と言う？", 12435, 0, 4, 12431, 4, 12363, 12375, 5, 5057, 4, "鶏肉の天ぷらのこと", 4, 1, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12459, "", "ボタニカル", "ボタニカル", "これ、何と言う？", 12523, 0, 5, 12491, 5, 12508, 12479, 5, 5058, 5, "近年シャンプーや化粧品によく見る「植物由来の」の意味の言葉。オーガニックとはやや異なる", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12364, "", "ごりんがり", "ごりんがり", "これ、何と言う？", 12426, 0, 4, 12435, 4, 12372, 12426, 5, 5059, 4, "髪の毛を「２ミリメートル」ほどに短く丸刈りにした髪型", 4, 2, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12483, "ツーブロック", "ツブロック", "ツーブロック", "これ、何と言う？", 12463, 0, 5, 12525, 5, 12484, 12502, 5, 5060, 5, "髪型の一種。髪の毛を伸ばしてある部分と、短く刈り上げた部分の２つにきっぱり分かれている", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12451, "", "ピラティス", "ピラティス", "これ、何と言う？", 12473, 0, 5, 12486, 5, 12500, 12521, 5, 5061, 5, "マット上で体幹を鍛え、身体の機能を整えるエクササイズ。ヨガと近い要素もあるが別物である", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12483, "", "デグラッセ", "デグラッセ", "これ、何と言う？", 12475, 0, 5, 12521, 5, 12487, 12464, 5, 5062, 5, "洋食のテクニック。肉を焼いた鍋底に焦げ付いた旨味をワインでこそぎ取り、味付けに利用する", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12511, "", "グルタミン", "グルタミン酸", "これ、何と言う？", 12531, 0, 5, 12479, 5, 12464, 12523, 5, 5063, 5, "和食に欠かせない「旨味」のうち、三大旨味成分と呼ばれるのは、イノシン酸・グアニル酸と何？", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12452, "", "カフェイン", "カフェイン", "これ、何と言う？", 12531, 0, 5, 12455, 5, 12459, 12501, 5, 5064, 5, "一般にコーヒーを飲んで摂取することが多い、眠気覚ましの効果がある成分", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12470, "", "パルメザン", "パルメザンチーズ", "これ、何と言う？", 12531, 0, 5, 12513, 5, 12497, 12523, 5, 5065, 5, "「粉チーズ」と言うと、これを指すのが一般的。パスタなどの風味づけに使われることが多い", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12401, "", "しょっぱい", "しょっぱい", "これ、何と言う？", 12356, 0, 4, 12387, 4, 12375, 12423, 5, 5066, 4, "「甘味」なら「あまい」、「酸味」なら「すっぱい」。では「塩味」なら？", 4, 1, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12515, "", "ホアジャオ", "花椒＝ホアジャオ", "これ、何と読む？", 12458, 0, 5, 12472, 5, 12507, 12450, 5, 5067, 5, "スパイスの一つ。「かしょう」以外の読み方は？", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12521, "", "モンブラン", "モンブラン", "これ、何と言う？", 12531, 0, 5, 12502, 5, 12514, 12531, 5, 5068, 5, "ケーキの種類の一つ。「栗（クリ）」のクリームを、しぼるように飾り付ける", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12502, "", "セミダブル", "セミダブル", "これ、何と言う？", 12523, 0, 5, 12480, 5, 12475, 12511, 5, 5069, 5, "ベッドの大きさの種類。「シングル」と「ダブル」の間の大きさは？", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12540, "", "レモネード", "レモネード", "これ、何と言う？", 12489, 0, 5, 12493, 5, 12524, 12514, 5, 5070, 5, "レモン果汁に砂糖やシロップ、はちみつなどを加えて薄めた飲み物", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12496, "ユニットバス", "ユニトバス", "ユニットバス", "これ、何と言う？", 12473, 0, 5, 12488, 5, 12518, 12491, 5, 5071, 5, "一般には、浴槽とトイレと洗面台が一緒になったタイプのバスルームのこと", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12491, "", "バルコニー", "バルコニー", "これ、何と言う？", 12540, 0, 5, 12467, 5, 12496, 12523, 5, 5072, 5, "住宅の設備の一つ。ベランダと混同されるが、上に屋根が無く解放されているという点で異なる", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12423, "", "ようしょく", "ようしょく", "これ、何と言う？", 12367, 0, 4, 12375, 4, 12424, 12358, 5, 5073, 4, "マグロなどの食用の魚介で、「天然」の反対をなんと言う？", 4, 2, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12463, "", "デトックス", "デトックス", "これ、何と言う？", 12473, 0, 5, 12483, 5, 12487, 12488, 5, 5074, 5, "体内の老廃物や毒素を排出する健康法。食事やエクササイズ、マッサージなど、方法はさまざま", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12412, "", "てんぴぼし", "てんぴぼし", "これ、何と言う？", 12375, 0, 4, 12404, 4, 12390, 12435, 5, 5075, 4, "布団や洗濯物を直射日光の下で干すこと。紫外線の殺菌効果で雑菌の繁殖を抑えることができる", 4, 2, 4);
